"use client";

import { ArrowUp, AudioLines, Paperclip } from "lucide-react";
import { useState } from "react";
import { Button } from "@/registry/brook/ui/button/button";
import { Card, CardContent, CardFooter } from "@/registry/brook/ui/card/card";
import { Field, FieldControl } from "@/registry/brook/ui/field/field";
import { Form } from "@/registry/brook/ui/form/form";
import {
  Select,
  SelectIcon,
  SelectItem,
  SelectItemIndicator,
  SelectItemText,
  SelectList,
  SelectPopup,
  SelectPortal,
  SelectPositioner,
  SelectSpacer,
  SelectTrigger,
  SelectValue,
} from "@/registry/brook/ui/select/select";
import styles from "./ai-chat.module.css";

const aiModes = [
  { value: "creative", label: "Creative" },
  { value: "fast", label: "Fast" },
  { value: "reasoning", label: "Reason" },
  { value: "teach", label: "Teach" },
];

export function AiChat() {
  const [inputValue, setInputValue] = useState("");
  const [selectedItem, setSelectedItem] = useState<string>(aiModes[0].value);

  return (
    <Form className={styles.form}>
      <Card className={styles.aiChatCard}>
        <CardContent>
          <Field className={styles.field}>
            <FieldControl
              placeholder="How can I helpâ€¦"
              render={
                <textarea
                  className={styles.textarea}
                  onChange={(e) => setInputValue(e.target.value)}
                  value={inputValue}
                />
              }
            />
          </Field>
        </CardContent>
        <CardFooter className={styles.footer}>
          <Button
            aria-label="Attach file"
            className={styles.attachButton}
            size="icon"
            type="button"
            variant="ghost"
          >
            <Paperclip size={14} />
          </Button>

          <div className={styles.footerActions}>
            <Select
              defaultValue={aiModes[0].value}
              items={aiModes}
              onValueChange={(value) => setSelectedItem(value as string)}
              value={selectedItem}
            >
              <SelectTrigger
                className={styles.selectTrigger}
                render={<Button className={styles.selectButton} size="sm" variant="ghost" />}
              >
                <SelectValue>
                  {(value) => {
                    const selectedMode = aiModes.find((mode) => mode.value === value);
                    return <span className={styles.selectValue}>{selectedMode?.label}</span>;
                  }}
                </SelectValue>
                <SelectIcon className={styles.selectIcon} />
              </SelectTrigger>
              <SelectPortal>
                <SelectPositioner align="start" alignItemWithTrigger={false} side="top" sideOffset={8}>
                  <SelectPopup className={styles.popup} data-slot="select-popup">
                    <SelectSpacer />
                    <SelectList>
                      {aiModes.map(({ label, value }) => (
                        <SelectItem data-slot="select-item" key={value} value={value}>
                          <SelectItemText>{label}</SelectItemText>
                          <SelectItemIndicator className={styles.selectIndicator} />
                        </SelectItem>
                      ))}
                    </SelectList>
                    <SelectSpacer />
                  </SelectPopup>
                </SelectPositioner>
              </SelectPortal>
            </Select>

            <Button
              aria-label={inputValue.trim() ? "Send message" : "Start voice input"}
              className={styles.submitButton}
              size="icon"
              type="submit"
              variant="ghost"
            >
              {inputValue.trim() ? (
                <ArrowUp className={styles.submitIcon} size={16} />
              ) : (
                <AudioLines className={styles.submitIcon} size={16} />
              )}
            </Button>
          </div>
        </CardFooter>
      </Card>
    </Form>
  );
}

.aiChatCard[data-slot="card"] {
  padding: 0.75rem;
  gap: 0.75rem;
  width: 100%;
  height: auto;
  max-width: 600px;
  min-width: 0;
  margin: 0 auto;
  border-radius: var(--radius-lg);
  border-color: oklch(from var(--border) l c h / 0.25);
  background-color: var(--mix-card-50-bg);
  transition: border-color 0.2s ease;

  &:hover,
  &:focus-within {
    border-color: oklch(from var(--border) l c h / 0.5);
  }
}

.form {
  width: 100%;
}

.textarea {
  background-color: transparent;
  border: none;
  padding: 0.25rem;
  resize: none;
  min-height: 2.5rem;
  height: auto;
  line-height: 1.5;

  &:focus {
    outline: none;
  }
}

.attachButton[data-slot="button"] {
  width: 32px;
  height: 32px;
  padding: 8px;
  border-radius: 50%;
  flex-shrink: 0;

  & svg {
    color: var(--muted-foreground);
    flex-shrink: 0;
    transform: rotate(-45deg);
  }
}

.footerActions {
  display: flex;
  gap: 0.5rem;
  align-items: center;
}

.selectButton[data-slot="button"] {
  border-radius: var(--radius);
}

.selectValue {
  color: var(--muted-foreground);
}

.selectIcon {
  margin-left: 0.25rem;
}

.selectIndicator {
  color: var(--muted-foreground);
}

.submitButton[data-slot="button"] {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background-color: var(--primary);
  flex-shrink: 0;
}

.submitButton[data-slot="button"]:hover:not([data-disabled]) {
  background-color: oklch(from var(--primary) calc(l * 0.8) c h);
}

.submitIcon {
  color: var(--primary-foreground);
}

.footer[data-slot="card-footer"] {
  gap: 0.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}


.selectButton:hover,
.selectButton[data-popup-open] {
  background-color: var(--accent) !important;
}

.popup[data-slot="select-popup"] {
  box-sizing: border-box;
  min-width: 140px;
}

@media (max-width: 768px) {
  .popup[data-slot="select-popup"] {
    width: 120px !important;
    min-width: 120px !important;
    max-width: 120px !important;
  }

  .selectValue {
    font-size: 0.875rem;
  }

  .selectIcon {
    display: none;
  }

}

@media (max-width: 1280px) {
  .textarea {
    min-height: 4rem;
    padding: 0.5rem;
  }
}

@media (max-width: 640px) {
  .aiChatCard[data-slot="card"] {
    min-width: 230px;
  }

  .form {
    display: flex;
    justify-content: center;
    align-items: center;
  }
}
