"use client";

import { AlertDialog } from "@base-ui-components/react/alert-dialog";
import type React from "react";
import { cn } from "@/lib/utils";
import styles from "./alert-dialog.module.css";

function AlertDialogRoot({
  ...props
}: React.ComponentProps<typeof AlertDialog.Root>) {
  return <AlertDialog.Root {...props} />;
}

function AlertDialogTrigger({
  ...props
}: React.ComponentProps<typeof AlertDialog.Trigger>) {
  return <AlertDialog.Trigger {...props} />;
}

const AlertDialogPortal = AlertDialog.Portal;

function AlertDialogBackdrop({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Backdrop>) {
  return (
    <AlertDialog.Backdrop
      className={cn(styles.overlay, className)}
      {...props}
    />
  );
}

function AlertDialogPopup({
  className,
  children,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Popup>) {
  return (
    <AlertDialogPortal>
      <AlertDialogBackdrop />
      <AlertDialog.Popup className={cn(styles.content, className)} {...props}>
        {children}
      </AlertDialog.Popup>
    </AlertDialogPortal>
  );
}

const AlertDialogContent = AlertDialogPopup;

function AlertDialogTitle({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Title>) {
  return (
    <AlertDialog.Title className={cn(styles.title, className)} {...props} />
  );
}

function AlertDialogDescription({
  className,
  ...props
}: React.ComponentProps<typeof AlertDialog.Description>) {
  return (
    <AlertDialog.Description
      className={cn(styles.description, className)}
      {...props}
    />
  );
}

function AlertDialogClose({
  ...props
}: React.ComponentProps<typeof AlertDialog.Close>) {
  return <AlertDialog.Close {...props} />;
}

const AlertDialogOverlay = AlertDialogBackdrop;

function AlertDialogHeader({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return <div className={cn(styles.header, className)} {...props} />;
}

function AlertDialogFooter({
  className,
  ...props
}: React.ComponentProps<"div">) {
  return <div className={cn(styles.footer, className)} {...props} />;
}

export {
  AlertDialogRoot as AlertDialog,
  AlertDialogClose,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogOverlay,
  AlertDialogPortal,
  AlertDialogRoot,
  AlertDialogTitle,
  AlertDialogTrigger,
};

.overlay {
  position: fixed;
  inset: 0;
  z-index: var(--dialog-z);
  background-color: var(--dialog-overlay);
  transition: opacity 150ms;
}

.overlay[data-starting-style],
.overlay[data-ending-style] {
  opacity: 0;
}

.content {
  position: fixed;
  left: 50%;
  top: 50%;
  z-index: 101;
  display: grid;
  width: 100%;
  max-width: 32rem;
  max-height: 85vh;
  overflow-y: auto;
  gap: 1rem;
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  padding: 1.5rem;
  border-radius: var(--radius);
  background-color: var(--background);
  transform: translate(-50%, -50%);
  transition: all 150ms;
}

.content[data-starting-style],
.content[data-ending-style] {
  opacity: 0;
  transform: translate(-50%, -50%) scale(0.95);
}

.header {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  text-align: left;
}

.header h2 {
  margin: 0;
}

.header p {
  margin: 0;
}

.footer {
  display: flex;
  flex-direction: column-reverse;
  gap: 0.5rem;
}

@media (min-width: 640px) {
  .footer {
    flex-direction: row;
    justify-content: flex-end;
  }
}

.title {
  font-size: 1.125rem;
  font-weight: 600;
  line-height: 1;
  letter-spacing: -0.025em;
  color: var(--foreground);
  margin: 0;
}

.description {
  font-size: 0.875rem;
  color: var(--secondary-foreground);
  margin: 0;
  line-height: 1.5;
}

.close {
  appearance: none;
  border: none;
  background: transparent;
  cursor: pointer;
  padding: 0.5rem 1rem;
  border-radius: var(--radius);
  font-size: 0.875rem;
  font-weight: 500;
  transition: all 150ms ease-out;
}

.close:hover {
  background-color: var(--muted);
}

.close:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

@media (max-width: 640px) {
  .content {
    width: calc(100vw - 2rem);
    max-width: none;
    left: 1rem;
    right: 1rem;
    top: 50%;
    transform: translateY(-50%);
  }

  .content[data-starting-style],
  .content[data-ending-style] {
    opacity: 0;
    transform: translateY(-50%) scale(0.9);
  }
}
