"use client";

import { AlertDialog } from "@base-ui-components/react/alert-dialog";
import { cn } from "@/lib/utils";
import styles from "./alert-dialog.module.css";

const AlertDialogRoot = ({ ...props }: React.ComponentProps<typeof AlertDialog.Root>) => {
  return <AlertDialog.Root {...props} />;
};

const AlertDialogTrigger = ({ ...props }: React.ComponentProps<typeof AlertDialog.Trigger>) => {
  return <AlertDialog.Trigger {...props} />;
};

const AlertDialogPortal = AlertDialog.Portal;

const AlertDialogBackdrop = ({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Backdrop>) => (
  <AlertDialog.Backdrop className={cn(styles.overlay, className)} {...props} />
);

const AlertDialogPopup = ({
  className,
  children,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Popup>) => (
  <AlertDialogPortal>
    <AlertDialogBackdrop />
    <AlertDialog.Popup className={cn(styles.content, className)} {...props}>
      {children}
    </AlertDialog.Popup>
  </AlertDialogPortal>
);

const AlertDialogContent = AlertDialogPopup;

const AlertDialogTitle = ({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Title>) => (
  <AlertDialog.Title className={cn(styles.title, className)} {...props} />
);

const AlertDialogDescription = ({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof AlertDialog.Description>) => (
  <AlertDialog.Description className={cn(styles.description, className)} {...props} />
);

const AlertDialogClose = ({ ...props }: React.ComponentProps<typeof AlertDialog.Close>) => {
  return <AlertDialog.Close {...props} />;
};

const AlertDialogOverlay = AlertDialogBackdrop;

const AlertDialogHeader = ({ className, ...props }: React.ComponentProps<"div">) => (
  <div className={cn(styles.header, className)} {...props} />
);

const AlertDialogFooter = ({ className, ...props }: React.ComponentProps<"div">) => (
  <div className={cn(styles.footer, className)} {...props} />
);

export {
  AlertDialogRoot,
  AlertDialogTrigger,
  AlertDialogContent,
  AlertDialogHeader,
  AlertDialogFooter,
  AlertDialogTitle,
  AlertDialogDescription,
  AlertDialogClose,
  AlertDialogOverlay,
  AlertDialogPortal,
};

.overlay {
    position: fixed;
    inset: 0;
    z-index: var(--dialog-z);
    background-color: var(--dialog-overlay);
    opacity: 1;
    transition: opacity 200ms cubic-bezier(0.4, 0, 0.2, 1);
}

.overlay[data-starting-style],
.overlay[data-ending-style] {
    opacity: 0;
}

.content {
    position: fixed;
    left: 50%;
    top: 50%;
    z-index: 101;
    display: grid;
    width: 100%;
    max-width: 32rem;
    max-height: 85vh;
    overflow-y: auto;
    gap: 1rem;
    border: 1px solid var(--border);
    background-color: var(--card);
    padding: 1.5rem;
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-lg);
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
    transition:
        opacity 200ms cubic-bezier(0.4, 0, 0.2, 1),
        transform 200ms cubic-bezier(0.4, 0, 0.2, 1);
}

.content[data-starting-style],
.content[data-ending-style] {
    opacity: 0;
    transform: translate(-50%, -54%) scale(0.96);
}

.header {
    display: flex;
    flex-direction: column;
    gap: 0.375rem;
    text-align: left;
}

.header h2 {
    margin: 0;
}

.header p {
    margin: 0;
}

.footer {
    display: flex;
    flex-direction: column-reverse;
    gap: 0.5rem;
}

@media (min-width: 640px) {
    .footer {
        flex-direction: row;
        justify-content: flex-end;
    }
}

.title {
    font-size: 1.125rem;
    font-weight: 600;
    line-height: 1;
    letter-spacing: -0.025em;
    color: var(--title);
    margin: 0;
}

.description {
    font-size: 0.875rem;
    color: var(--foreground);
    margin: 0;
    line-height: 1.5;
}

.close {
    appearance: none;
    border: none;
    background: transparent;
    cursor: pointer;
    padding: 0.5rem 1rem;
    border-radius: var(--radius);
    font-size: 0.875rem;
    font-weight: 500;
    transition: all 150ms ease-out;
}

.close:hover {
    background-color: var(--muted);
}

.close:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
}

@media (max-width: 640px) {
    .content {
        width: calc(100vw - 2rem);
        max-width: none;
        margin: 1rem;
        left: 50%;
        top: 50%;
    }

    .content:not([hidden]) {
        transform: translate(-50%, -50%) scale(1);
    }
}
