import { cn } from "@/lib/utils";
import styles from "./alert.module.css";

export interface AlertProps extends React.HTMLAttributes<HTMLDivElement> {
  ref?: React.Ref<HTMLDivElement>;
  variant?: "default" | "destructive" | "warning" | "success" | "info";
  size?: "default" | "sm";
}

export const Alert = ({ className, ref, variant = "default", size = "default", ...props }: AlertProps) => (
  <div ref={ref} className={cn(styles.alert, styles[variant], size === "sm" && styles.sm, className)} {...props} />
);
Alert.displayName = "Alert";

export interface AlertTitleProps extends React.HTMLAttributes<HTMLHeadingElement> {
  ref?: React.Ref<HTMLHeadingElement>;
}

export const AlertTitle = ({ className, ref, ...props }: AlertTitleProps) => (
  <h5 ref={ref} className={cn(styles.title, className)} {...props} />
);
AlertTitle.displayName = "AlertTitle";

export interface AlertDescriptionProps extends React.HTMLAttributes<HTMLDivElement> {
  ref?: React.Ref<HTMLDivElement>;
}

export const AlertDescription = ({ className, ref, ...props }: AlertDescriptionProps) => (
  <div ref={ref} className={cn(styles.description, className)} {...props} />
);
AlertDescription.displayName = "AlertDescription";

export interface AlertIconProps extends React.HTMLAttributes<HTMLDivElement> {
  ref?: React.Ref<HTMLDivElement>;
}

export const AlertIcon = ({ className, ref, ...props }: AlertIconProps) => (
  <div ref={ref} className={cn(styles.icon, className)} {...props} />
);
AlertIcon.displayName = "AlertIcon";

.alert {
    position: relative;
    width: 100%;
    border-radius: var(--radius);
    border: 1px solid var(--border);
    padding: 0.75rem;
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    font-size: 0.75rem;
    justify-content: center;
    align-items: center;
}

.sm {
    padding: 0.5rem;
}

.default {
    background-color: var(--muted);
    color: var(--foreground);
}

.destructive {
    background-color: var(--muted);
    color: var(--destructive);
}

.destructive .title {
    color: var(--destructive);
}

.destructive .description {
    color: var(--destructive);
}

.warning {
    background-color: var(--muted);
    color: var(--warning);
}

.warning .title {
    color: var(--warning);
}

.warning .description {
    color: var(--warning);
}

.success {
    background-color: var(--muted);
    color: var(--success);
}

.success .title {
    color: var(--success);
}

.success .description {
    color: var(--success);
}

.info {
    background-color: var(--muted);
    color: var(--info);
}

.info .title {
    color: var(--info);
}

.info .description {
    color: var(--info);
}

.icon {
    flex-shrink: 0;
    width: 1rem;
    height: 1rem;
    margin-top: 0.125rem;
}

.title {
    margin: 0 0 0.25rem 0;
    font-weight: 500;
    font-size: 0.875rem;
    line-height: 1.25;
    margin: 0;
}

.description {
    line-height: 1.5;
    margin: 0;
    flex: 1;
}
