import { cn } from "@/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";
import styles from "./alert.module.css";

const alertVariants = cva(styles.alert, {
  variants: {
    variant: {
      default: styles.default,
      destructive: styles.destructive,
      warning: styles.warning,
      success: styles.success,
      info: styles.info,
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

function Alert({
  className,
  variant,
  ...props
}: React.ComponentProps<"div"> & VariantProps<typeof alertVariants>) {
  return (
    <div role="alert" data-slot="alert" className={cn(alertVariants({ variant }), className)} {...props} />
  );
}

function AlertTitle({ className, ...props }: React.ComponentProps<"div">) {
  return <div data-slot="alert-title" className={cn(styles.title, className)} {...props} />;
}

function AlertDescription({ className, ...props }: React.ComponentProps<"div">) {
  return <div data-slot="alert-description" className={cn(styles.description, className)} {...props} />;
}

function AlertIcon({ className, ...props }: React.ComponentProps<"div">) {
  return <div data-slot="alert-icon" className={cn(styles.icon, className)} {...props} />;
}

export { Alert, AlertDescription, AlertIcon, AlertTitle };

.alert {
  position: relative;
  width: 100%;
  border-radius: var(--radius);
  border: 1px solid var(--border);
  padding: 0.75rem;
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  font-size: 0.75rem;
  justify-content: center;
  align-items: center;
}

.default {
  background-color: var(--muted);
  color: var(--foreground);
}

.destructive {
  background-color: var(--muted);
  color: var(--destructive);
}

.destructive .title {
  color: var(--destructive);
}

.destructive .description {
  color: var(--destructive);
}

.warning {
  background-color: var(--muted);
  color: var(--warning);
}

.warning .title {
  color: var(--warning);
}

.warning .description {
  color: var(--warning);
}

.success {
  background-color: var(--muted);
  color: var(--success);
}

.success .title {
  color: var(--success);
}

.success .description {
  color: var(--success);
}

.info {
  background-color: var(--muted);
  color: var(--info);
}

.info .title {
  color: var(--info);
}

.info .description {
  color: var(--info);
}

.icon {
  flex-shrink: 0;
  width: 1rem;
  height: 1rem;
  margin-top: 0.125rem;
}

.title {
  margin: 0 0 0.25rem 0;
  font-weight: 500;
  font-size: 0.875rem;
  line-height: 1.25;
  margin: 0;
}

.description {
  line-height: 1.5;
  margin: 0;
  flex: 1;
}
