import { cn } from "@/lib/utils";
import { cva, type VariantProps } from "class-variance-authority";
import styles from "./alert.module.css";

const alertVariants = cva(styles.alert, {
  variants: {
    variant: {
      default: styles.default,
      destructive: styles.destructive,
      warning: styles.warning,
      success: styles.success,
      info: styles.info,
    },
  },
  defaultVariants: {
    variant: "default",
  },
});

function Alert({
  className,
  variant,
  ...props
}: React.ComponentProps<"div"> & VariantProps<typeof alertVariants>) {
  return (
    <div role="alert" data-slot="alert" className={cn(alertVariants({ variant }), className)} {...props} />
  );
}

function AlertTitle({ className, ...props }: React.ComponentProps<"div">) {
  return <div data-slot="alert-title" className={cn(styles.title, className)} {...props} />;
}

function AlertDescription({ className, ...props }: React.ComponentProps<"div">) {
  return <div data-slot="alert-description" className={cn(styles.description, className)} {...props} />;
}

export { Alert, AlertDescription, AlertTitle };

.alert {
  position: relative;
  width: 100%;
  border-radius: var(--radius);
  border: 1px solid var(--border);
  padding: 0.75rem 1rem;
  display: grid;
  grid-template-columns: 0 1fr auto;
  gap: 0.125rem 0.75rem;
  align-items: start;
  font-size: 0.75rem;
  line-height: 1.2;
  background-color: var(--mix-card-70-bg);
}

.alert:has(> svg) {
  grid-template-columns: 1rem 1fr auto;
}

.alert > svg {
  width: 1rem;
  height: 100%;
}

.default {
  color: var(--foreground);
}

.destructive {
  color: var(--destructive);
}

.destructive .title {
  color: var(--destructive);
}

.destructive .description {
  color: var(--destructive);
}

.warning {
  color: var(--warning);
}

.warning .title {
  color: var(--warning);
}

.warning .description {
  color: var(--warning);
}

.success {
  color: var(--success);
}

.success .title {
  color: var(--success);
}

.success .description {
  color: var(--success);
}

.info {
  color: var(--info);
}

.info .title {
  color: var(--info);
}

.info .description {
  color: var(--info);
}

.title {
  margin: 0;
  font-weight: 400;
  font-size: 0.875rem;
  grid-column-start: 2;
  letter-spacing: -0.02em;
}

.description {
  margin: 0;
  font-weight: 300;
  font-size: 0.875rem;
  grid-column-start: 2;

  color: var(--muted-foreground);
}
