"use client";

import { Autocomplete } from "@base-ui-components/react/autocomplete";
import { Search, X } from "lucide-react";
import type React from "react";
import { cn } from "@/lib/utils";
import { Input } from "@/registry/brook/ui/input/input";
import styles from "./autocomplete.module.css";

function AutocompleteRoot({
  ...props
}: React.ComponentProps<typeof Autocomplete.Root>) {
  return <Autocomplete.Root {...props} />;
}

function AutocompleteValue({
  ...props
}: React.ComponentProps<typeof Autocomplete.Value>) {
  return <Autocomplete.Value data-slot="autocomplete-value" {...props} />;
}

function AutocompleteInput({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Input>) {
  return <Autocomplete.Input render={<Input className={className} />} {...props} />;
}

function AutocompleteTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Autocomplete.Trigger>) {
  return (
    <Autocomplete.Trigger
      className={cn(styles.trigger, className)}
      data-slot="autocomplete-trigger"
      {...props}
    >
      {children || <Search className={styles.icon} size={16} />}
    </Autocomplete.Trigger>
  );
}

function AutocompleteIcon({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Autocomplete.Icon>) {
  return (
    <Autocomplete.Icon
      className={cn(styles.icon, className)}
      data-slot="autocomplete-icon"
      {...props}
    >
      {children || <Search size={16} />}
    </Autocomplete.Icon>
  );
}

function AutocompleteClear({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Autocomplete.Clear>) {
  return (
    <Autocomplete.Clear
      className={cn(styles.clear, className)}
      data-slot="autocomplete-clear"
      {...props}
    >
      {children || <X size={16} />}
    </Autocomplete.Clear>
  );
}

const AutocompletePortal = Autocomplete.Portal;

function AutocompleteBackdrop({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Backdrop>) {
  return (
    <Autocomplete.Backdrop
      className={cn(styles.backdrop, className)}
      data-slot="autocomplete-backdrop"
      {...props}
    />
  );
}

function AutocompletePositioner({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Positioner>) {
  return (
    <Autocomplete.Positioner
      className={cn(styles.positioner, className)}
      data-slot="autocomplete-positioner"
      sideOffset={4}
      {...props}
    />
  );
}

function AutocompletePopup({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Popup>) {
  return (
    <Autocomplete.Popup
      className={cn(styles.popup, className)}
      data-slot="autocomplete-popup"
      {...props}
    />
  );
}

function AutocompleteArrow({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Arrow>) {
  return (
    <Autocomplete.Arrow
      className={cn(styles.arrow, className)}
      data-slot="autocomplete-arrow"
      {...props}
    />
  );
}

function AutocompleteStatus({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Status>) {
  return (
    <Autocomplete.Status
      className={cn(styles.status, className)}
      data-slot="autocomplete-status"
      {...props}
    />
  );
}

function AutocompleteList({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.List>) {
  return (
    <Autocomplete.List
      className={cn(styles.list, className)}
      data-slot="autocomplete-list"
      {...props}
    />
  );
}

function AutocompleteEmpty({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Autocomplete.Empty>) {
  return (
    <Autocomplete.Empty
      className={cn(styles.empty, className)}
      data-slot="autocomplete-empty"
      {...props}
    >
      {children || "No items found"}
    </Autocomplete.Empty>
  );
}

function AutocompleteCollection({
  ...props
}: React.ComponentProps<typeof Autocomplete.Collection>) {
  return (
    <Autocomplete.Collection data-slot="autocomplete-collection" {...props} />
  );
}

function AutocompleteRow({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Row>) {
  return (
    <Autocomplete.Row
      className={cn(styles.row, className)}
      data-slot="autocomplete-row"
      {...props}
    />
  );
}

function AutocompleteItem({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Item>) {
  return (
    <Autocomplete.Item
      className={cn(styles.item, className)}
      data-slot="autocomplete-item"
      {...props}
    />
  );
}

function AutocompleteGroup({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Group>) {
  return (
    <Autocomplete.Group
      className={cn(styles.group, className)}
      data-slot="autocomplete-group"
      {...props}
    />
  );
}

function AutocompleteGroupLabel({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.GroupLabel>) {
  return (
    <Autocomplete.GroupLabel
      className={cn(styles.groupLabel, className)}
      data-slot="autocomplete-group-label"
      {...props}
    />
  );
}

function AutocompleteSeparator({
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Separator>) {
  return (
    <Autocomplete.Separator
      className={cn(styles.separator, className)}
      data-slot="autocomplete-separator"
      {...props}
    />
  );
}

export {
  AutocompleteRoot as Autocomplete,
  AutocompleteArrow,
  AutocompleteBackdrop,
  AutocompleteClear,
  AutocompleteCollection,
  AutocompleteEmpty,
  AutocompleteGroup,
  AutocompleteGroupLabel,
  AutocompleteIcon,
  AutocompleteInput,
  AutocompleteItem,
  AutocompleteList,
  AutocompletePopup,
  AutocompletePortal,
  AutocompletePositioner,
  AutocompleteRow,
  AutocompleteSeparator,
  AutocompleteStatus,
  AutocompleteTrigger,
  AutocompleteValue,
};

.input {
  display: flex;
  height: 2.5rem;
  width: 100%;
  border-radius: var(--radius);
  border: 1px solid var(--border);
  background-color: var(--mix-card-50-bg);
  padding: 0 0.75rem;
  font-size: 0.875rem;
  transition: all 150ms ease-out;
  color: var(--foreground);

  &::placeholder {
    color: var(--muted-foreground);
    font-size: 0.875rem;
  }

  &[data-focused] {
    outline: none;
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &[data-invalid] {
    border-color: var(--destructive);

    &:focus,
    &[data-focused] {
      border-color: var(--destructive);
      box-shadow: 0 0 0 2px var(--destructive) / 0.2;
    }
  }

  &[data-valid] {
    border-color: var(--success, var(--primary));
  }

  @media (max-width: 640px) {
    height: 2.75rem;
    font-size: 0.9375rem;
    padding: 0 1rem;

    &::placeholder {
      font-size: 0.9375rem;
    }
  }
}

.trigger {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.5rem;
  background: transparent;
  border: none;
  cursor: pointer;
  color: var(--muted-foreground);
  transition: color 150ms ease;

  &:hover {
    color: var(--foreground);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
  }
}

.icon {
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  opacity: 0.6;
}

.clear {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  color: var(--muted-foreground);
  transition: all 150ms ease;

  &:hover {
    color: var(--foreground);
    background: var(--accent);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
  }
}

.backdrop {
  position: fixed;
  inset: 0;
  z-index: 140;
  background-color: var(--dialog-overlay);
  opacity: 0;
  transition: opacity 150ms ease;

  &[data-open] {
    opacity: 1;
  }
}

.positioner {
  position: absolute;
  z-index: 150;
  width: var(--anchor-width);
  outline: none;
}

.popup {
  box-sizing: border-box;
  padding: 0.25rem;
  border-radius: var(--radius);
  margin-top: 1px;
  background-color: var(--mix-card-75-bg);
  color: var(--popover-foreground);
  width: var(--anchor-width);
  max-height: min(var(--available-height), 23rem);
  max-width: var(--available-width);
  overflow-y: auto;
  scroll-padding-block: 0.5rem;
  overscroll-behavior: contain;
  border: 1px solid var(--border);
  box-shadow:
    0 10px 15px -3px rgb(0 0 0 / 0.1),
    0 4px 6px -4px rgb(0 0 0 / 0.1);
  transform-origin: var(--transform-origin);
  animation: slideDownAndFade 150ms ease-out;

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }

  @media (max-width: 640px) {
    max-width: calc(100vw - 2rem);
    padding: 0.5rem;
  }
}

@keyframes slideDownAndFade {
  from {
    opacity: 0;
    transform: translateY(-0.5rem) scale(0.95);
  }

  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

.arrow {
  position: absolute;
  width: 0.5rem;
  height: 0.5rem;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  transform: rotate(45deg);

  &[data-side="top"] {
    bottom: -0.25rem;
    border-right: none;
    border-bottom: none;
  }

  &[data-side="right"] {
    left: -0.25rem;
    border-bottom: none;
    border-left: none;
  }

  &[data-side="bottom"] {
    top: -0.25rem;
    border-top: none;
    border-left: none;
  }

  &[data-side="left"] {
    right: -0.25rem;
    border-top: none;
    border-right: none;
  }
}

.status {
  padding: 0.5rem 1rem;
  font-size: 0.875rem;
  color: var(--muted-foreground);
}

.list {
  outline: none;
}

.empty {
  box-sizing: border-box;
  padding: 1rem;
  font-size: 0.925rem;
  color: var(--muted-foreground);
  text-align: center;

  &:empty {
    margin: 0;
    padding: 0;
  }

  @media (max-width: 640px) {
    padding: 1.25rem;
    font-size: 0.9375rem;
  }
}

.row {
  display: flex;
  align-items: center;
}

.item {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  cursor: pointer;
  user-select: none;
  outline: 0;
  z-index: 0;

  &[data-highlighted] {
    z-index: 1;
    position: relative;

    &::before {
      content: "";
      z-index: -1;
      position: absolute;
      inset: 0;
      border-radius: calc(var(--radius) - 2px);
      background: var(--mix-accent-33-trans);
    }
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
  }

  @media (max-width: 640px) {
    padding: 0.75rem 1rem;
    font-size: 0.9375rem;
  }
}

.group {
  padding: 0.25rem 0;
}

.groupLabel {
  padding: 0.5rem 0.75rem 0.25rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.025em;
}

.separator {
  margin: 0.375rem 0.75rem;
  height: 1px;
  background-color: var(--border);
}
