"use client";

import type { ComponentProps, ReactNode } from "react";
import { createContext, useContext } from "react";

import { cn } from "@/lib/utils";
import styles from "./badge.module.css";

type BadgeVariant = "default" | "secondary" | "destructive" | "outline" | "success";
type BadgeSize = "sm" | "md" | "lg";

interface BadgeProps extends ComponentProps<"span"> {
  variant?: BadgeVariant;
  size?: BadgeSize;
  children: ReactNode;
}

interface BadgeIconProps extends ComponentProps<"span"> {
  children: ReactNode;
}

const BadgeContext = createContext<{ variant: BadgeVariant }>({ 
  variant: "default"
});

const Badge = ({
  className,
  variant = "default",
  size = "md",
  children,
  ...props
}: BadgeProps) => {
  const badgeClassName = cn(styles.badge, styles[variant], styles[size], className);

  return (
    <BadgeContext.Provider value={{ variant }}>
      <span data-slot="badge" className={badgeClassName} {...props}>
        {children}
      </span>
    </BadgeContext.Provider>
  );
};

const BadgeIcon = ({ className, children, ...props }: BadgeIconProps) => {
  const { variant } = useContext(BadgeContext);
  const iconClassName = cn(
    styles.iconContainer, 
    styles[`${variant}Icon`],
    className
  );

  return (
    <span className={iconClassName} {...props}>
      {children}
    </span>
  );
};

export { Badge, BadgeIcon, type BadgeProps, type BadgeIconProps };

.badge {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border: 1px solid;
    font-weight: 500;
    width: fit-content;
    white-space: nowrap;
    flex-shrink: 0;
    gap: 0.375rem;
    transition:
        color 150ms ease,
        box-shadow 150ms ease;
    overflow: hidden;
}

.badge svg {
    pointer-events: none;
}

.badge:focus-visible {
    border-color: var(--ring);
    box-shadow:
        0 0 0 3px var(--ring),
        inset 0 0 0 1px var(--ring);
}

.badge[aria-invalid="true"] {
    box-shadow: 0 0 0 1px var(--destructive);
    border-color: var(--destructive);
}

.default {
    background-color: var(--foreground);
    color: var(--primary-foreground);
}

.secondary {
    border-color: transparent;
    background-color: color-mix(in oklch, var(--card) 66%, var(--background));
    color: var(--secondary-foreground);
}
.secondary:hover {
    background-color: var(--accent);
    cursor: pointer;
}

.destructive {
    border-color: var(--border);
    background-color: color-mix(in oklch, var(--card) 66%, var(--background));
    color: var(--destructive);
}

.destructive:focus-visible {
    box-shadow:
        0 0 0 3px var(--destructive),
        inset 0 0 0 1px var(--destructive);
}

.success {
    border-color: var(--border);
    background-color: color-mix(in oklch, var(--card) 66%, var(--background));
    color: var(--success);
}

.success:focus-visible {
    box-shadow:
        0 0 0 3px var(--success),
        inset 0 0 0 1px var(--success);
}

.outline {
    color: var(--foreground);
    border-color: var(--border);
    background-color: transparent;
}

/* Icon container */
.iconContainer {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    flex-shrink: 0;
    padding: 0.125rem;
    margin-right: 0.25rem;
}

.defaultIcon {
    background-color: color-mix(in srgb, var(--foreground) 20%, var(--muted) 80%);
}

.secondaryIcon {
    background-color: color-mix(in srgb, var(--secondary) 20%, var(--muted) 80%);
}

.destructiveIcon {
    background-color: color-mix(in srgb, var(--destructive) 20%, var(--muted) 80%);
}

.successIcon {
    background-color: color-mix(in srgb, var(--success) 20%, var(--muted) 80%);
}

.outlineIcon {
    background-color: color-mix(in srgb, var(--foreground) 20%, var(--muted) 80%);
}

.sm {
    padding: 0.0625rem 0.375rem;
    font-size: 0.625rem;
    border-radius: 0.25rem;
}

.md {
    padding: 0.125rem 0.5rem;
    font-size: 0.75rem;
    border-radius: 0.375rem;
}

.lg {
    padding: 0.25rem 0.75rem;
    font-size: 0.875rem;
    border-radius: 0.5rem;
}
