"use client";

interface PayloadEntry {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
}

export interface ChartTooltipProps {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
}

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!active || !payload || !payload.length) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && <div style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}>{formatLabel(label)}</div>}
      {payload.map((entry, index) => (
        <div
          key={index}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>{formatName(entry.name || entry.dataKey || "")}</span>
          </div>
          <span
            style={{
              fontFamily: "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import { BarChart as RechartsBarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";

import styles from "./bar-chart.module.css";
import ChartTooltip from "./chart-tooltip";

type NumberValue = number | { valueOf(): number };

export interface BarChartData {
  category: string;
  amount: number;
  type: string;
}

export interface BarChartProps {
  data: BarChartData[];
  showXAxis?: boolean;
  showYAxis?: boolean;
  showXGrid?: boolean;
  showYGrid?: boolean;
  xAxisFormatter?: (value: NumberValue | string) => string;
  useGradient?: boolean;
  animated?: boolean;
}

function BarChart({
  data,
  showXAxis = true,
  showYAxis = true,
  showXGrid = false,
  showYGrid = true,
  xAxisFormatter,
  animated = false,
}: BarChartProps) {
  const color = "var(--chart1)";

  const formatCategory = (value: NumberValue | string) => {
    if (xAxisFormatter) {
      return xAxisFormatter(value);
    }
    return String(value);
  };

  const tooltipLabelFormatter = (value: string | number) => formatCategory(value);

  const tooltipValueFormatter = (value: number | string, name?: string) => {
    const numValue = typeof value === 'number' ? value : parseFloat(String(value));
    return isNaN(numValue) ? String(value) : `$${numValue.toLocaleString()}k`;
  };

  interface TooltipPayload {
    value: number;
    payload: BarChartData;
  }

  const CustomTooltip = ({ active, payload, label }: { active?: boolean; payload?: TooltipPayload[]; label?: string | number }) => {
    if (!active || !payload || !payload.length) return null;

    const data = payload[0];
    return (
      <ChartTooltip 
        active={active}
        payload={[{
          ...data,
          name: "Sales",
          color: color
        }]}
        label={label}
        labelFormatter={tooltipLabelFormatter}
        valueFormatter={tooltipValueFormatter}
      />
    );
  };

  return (
    <div className={styles.barChart}>
      <ResponsiveContainer width="100%" height="100%">
        <RechartsBarChart data={data} margin={{ top: 10, right: 10, left: -5, bottom: 40 }}>
          {showXGrid && <CartesianGrid strokeDasharray="3 3" stroke="var(--border)" strokeOpacity={0.2} />}
          {showYGrid && <CartesianGrid strokeDasharray="3 3" stroke="var(--border)" strokeOpacity={0.2} />}
          {showXAxis && (
            <XAxis
              dataKey="category"
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 11, fill: "var(--muted-foreground)" }}
              tickFormatter={formatCategory}
            />
          )}
          {showYAxis && (
            <YAxis
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 11, fill: "var(--muted-foreground)" }}
              width={30}
            />
          )}
          <Tooltip
            content={<CustomTooltip />}
            cursor={{ fill: "var(--secondary)", opacity: 0.15 }}
          />
          <Bar
            dataKey="amount"
            fill={color}
            radius={[4, 4, 0, 0]}
            animationDuration={animated ? 500 : 0}
            animationBegin={animated ? 0 : undefined}
            style={{
              cursor: "pointer",
            }}
            onMouseEnter={() => {}}
            onMouseLeave={() => {}}
          />
        </RechartsBarChart>
      </ResponsiveContainer>
    </div>
  );
}

export default BarChart;

.barChart {
    position: relative;
    width: 100%;
    height: 100%;
}

.barChart .recharts-active-bar {
    filter: none !important;
    opacity: 1 !important;
}

.barChart .recharts-bar-rectangle:hover {
    fill-opacity: 1 !important;
    stroke: none !important;
    filter: none !important;
}

.barChart *:focus,
.barChart *:focus-visible {
    outline: none !important;
}
