import { useRender } from "@base-ui-components/react/use-render";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import styles from "./button.module.css";

const buttonVariants = cva(styles.base, {
  variants: {
    variant: {
      primary: styles.primary,
      secondary: styles.secondary,
      destructive: styles.destructive,
      ghost: styles.ghost,
      outline: styles.outline,
      link: styles.link,
    },
    size: {
      sm: styles.sm,
      md: styles.md,
      lg: styles.lg,
      icon: styles.icon,
    },
  },
  defaultVariants: {
    variant: "primary",
    size: "md",
  },
});

function Spinner() {
  return (
    // biome-ignore lint/a11y/noSvgWithoutTitle: Spinner is decorative loading indicator
    <svg
      className={styles.spinner}
      fill="none"
      height="16"
      viewBox="0 0 24 24"
      width="16"
    >
      <circle
        cx="12"
        cy="12"
        r="10"
        stroke="currentColor"
        strokeDasharray="31.416"
        strokeDashoffset="31.416"
        strokeLinecap="round"
        strokeWidth="2"
      />
    </svg>
  );
}

/**
 * ArrowPointer component for displaying directional arrows within buttons.
 *
 * @param pointLeft - When true, arrow points left instead of right
 * @param pointExternal - When true, applies external link arrow styling (diagonal orientation)
 *
 * @example
 * ```tsx
 * // Right-pointing arrow (default)
 * <ArrowPointer />
 *
 * // Left-pointing arrow
 * <ArrowPointer pointLeft />
 *
 * // External link arrow
 * <ArrowPointer pointExternal />
 * ```
 */
function ArrowPointer({
  pointLeft = false,
  pointExternal = false,
}: {
  pointLeft?: boolean;
  pointExternal?: boolean;
}) {
  return (
    // biome-ignore lint/a11y/noSvgWithoutTitle: Arrow is decorative button icon
    <svg
      className={cn(
        styles.arrow,
        pointLeft && styles.arrowLeft,
        pointExternal && styles.arrowExternal
      )}
      fill="none"
      viewBox="0 0 14 10"
      xmlns="http://www.w3.org/2000/svg"
    >
      <g fillRule="nonzero">
        <path
          className={styles.arrowPoint}
          d={pointLeft ? "M7.2 1l-4 4 4 4" : "M-0.8 1l4 4-4 4"}
          stroke="currentColor"
          strokeLinecap="square"
          strokeLinejoin="miter"
          strokeWidth="2"
        />
        <path
          className={styles.arrowShaft}
          d={pointLeft ? "M7.2 5H2.2" : "M0 5h4.8"}
          stroke="currentColor"
          strokeLinecap="square"
          strokeLinejoin="miter"
          strokeWidth="2"
        />
      </g>
    </svg>
  );
}

interface ButtonProps
  extends useRender.ComponentProps<"button">,
    VariantProps<typeof buttonVariants> {
  showArrow?: boolean;
  pointLeft?: boolean;
  pointExternal?: boolean;
  loading?: boolean;
}

function Button({
  render,
  className,
  variant,
  size,
  showArrow = false,
  pointLeft = false,
  pointExternal = false,
  loading = false,
  ...props
}: ButtonProps) {
  const decoratedChildren = (
    <>
      {loading && <Spinner />}
      {!loading && showArrow && pointLeft && (
        <ArrowPointer pointExternal={pointExternal} pointLeft />
      )}
      {props.children}
      {!loading && showArrow && !pointLeft && (
        <ArrowPointer pointExternal={pointExternal} />
      )}
    </>
  );

  return useRender({
    defaultTagName: "button",
    render,
    props: {
      ...props,
      "data-slot": "button",
      className: cn(
        buttonVariants({ variant, size }),
        loading && styles.loading,
        className
      ),
      disabled: props.disabled || loading,
      children: decoratedChildren,
    },
  });
}

Button.displayName = "Button";

export { Button, ArrowPointer };

.base {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: var(--radius);
  font-weight: 450;
  transition: transform 0.2s var(--ease-out-quad);
  cursor: pointer;
  border: 1px solid transparent;
  outline: none;
  position: relative;
  overflow: hidden;
  letter-spacing: -0.014em;
  will-change: transform;
}
.base:active:not(:disabled):not(.loading) {
  transform: scale(0.97);
}

.base:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.base:disabled {
  opacity: 0.7;
  cursor: not-allowed;
}

.base.loading {
  opacity: 0.7;
  cursor: not-allowed;
}

.primary {
  background-color: var(--primary);
  color: var(--primary-foreground);
  box-shadow:
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px;
}

.primary:hover:not(:disabled) {
  background-color: oklch(from var(--primary) l c h / 0.8);
}

.secondary {
  background-color: var(--secondary);
  color: var(--secondary-foreground);
}

.secondary:hover:not(:disabled) {
  background-color: oklch(from var(--secondary) l c h / 0.8);
}

.destructive {
  background-color: var(--destructive);
  color: var(--destructive-foreground);
}

.destructive:hover:not(:disabled) {
  background-color: oklch(from var(--destructive) l c h / 0.85);
}

.ghost {
  background-color: transparent;
  color: var(--foreground);
}

.ghost:hover:not(:disabled) {
  background-color: oklch(from var(--accent) l c h / 0.66);
}

.outline {
  background-color: var(--mix-card-50-bg);
  color: var(--foreground);
  border: 1px solid oklch(from var(--border) l c h / 0.7);
}

.outline:hover:not(:disabled) {
  background-color: var(--mix-card-66-bg);
}

.link {
  background-color: transparent;
  color: var(--muted-foreground);
  padding: 0;
  text-decoration: none;
  transition: text-decoration 0.2s ease-out;
}

.link:hover:not(:disabled) {
  text-decoration: underline;
  color: var(--foreground);
}

.sm {
  height: 2rem;
  padding: 0 0.75rem;
  font-size: 0.875rem;
}

.md {
  height: 2.5rem;
  padding: 0.5rem 1rem;
  font-size: 0.925rem;
}

.lg {
  height: 3rem;
  padding: 0.5rem 1.5rem;
  font-size: 1rem;
}

.icon {
  height: auto;
  width: auto;
  padding: 0.375rem;
  font-size: 0.875rem;
  aspect-ratio: 1;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

.icon::before {
  content: "";
  position: absolute;
  display: block;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  min-height: 44px;
  min-width: 44px;
  z-index: -1;
}

.arrow {
  margin-top: -1px;
  margin-left: 8px;
  margin-right: -8px;
  position: relative;
  top: 0px;
  overflow: visible;
  width: 14px;
  height: 12px;
  transition: 200ms var(--ease-in-out-cubic);
}

.arrowLeft {
  margin-left: -8px;
  margin-right: 8px;
}

.arrowExternal {
  transform-origin: 8%;
  transition: transform 200ms var(--ease-in-out-cubic);
}

.base:hover .arrowExternal {
  transform: rotate(-45deg);
}

.arrowPoint {
  transition: 200ms var(--ease-in-out-cubic);
}

.arrowShaft {
  transition: 200ms var(--ease-in-out-cubic);
  opacity: 0;
}

.base:hover .arrowPoint {
  transform: translateX(2px);
}

.base:hover .arrowShaft {
  opacity: 1;
  transform: translateX(-2px);
}

.base:hover .arrowLeft .arrowPoint {
  transform: translateX(-2px);
}

.base:hover .arrowLeft .arrowShaft {
  opacity: 1;
  transform: translateX(1px);
}

.spinner {
  margin-right: 0.5rem;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
    stroke-dashoffset: 31.416;
  }
  to {
    transform: rotate(360deg);
    stroke-dashoffset: 0;
  }
}
