"use client";

import { Button } from "@base-ui/react/button";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import styles from "./button.module.css";

const buttonVariants = cva(styles.base, {
  variants: {
    variant: {
      primary: styles.primary,
      secondary: styles.secondary,
      destructive: styles.destructive,
      ghost: styles.ghost,
      outline: styles.outline,
      link: styles.link,
    },
    size: {
      sm: styles.sm,
      md: styles.md,
      lg: styles.lg,
      icon: styles.icon,
    },
  },
  defaultVariants: {
    variant: "primary",
    size: "md",
  },
});

function Spinner() {
  return (
    <svg className={styles.spinner} fill="none" height="16" viewBox="0 0 24 24" width="16">
      <circle
        cx="12"
        cy="12"
        r="10"
        stroke="currentColor"
        strokeDasharray="31.416"
        strokeDashoffset="31.416"
        strokeLinecap="round"
        strokeWidth="2"
      />
    </svg>
  );
}

/**
 * ArrowPointer component for displaying directional arrows within buttons.
 *
 * @param pointLeft - When true, arrow points left instead of right
 * @param pointExternal - When true, applies external link arrow styling (diagonal orientation)
 *
 * @example
 * ```tsx
 * // Right-pointing arrow (default)
 * <ArrowPointer />
 *
 * // Left-pointing arrow
 * <ArrowPointer pointLeft />
 *
 * // External link arrow
 * <ArrowPointer pointExternal />
 * ```
 */
function ArrowPointer({ pointLeft = false, pointExternal = false }: { pointLeft?: boolean; pointExternal?: boolean }) {
  return (
    <svg
      className={cn(styles.arrow, pointLeft ? styles.arrowLeft : null, pointExternal ? styles.arrowExternal : null)}
      fill="none"
      viewBox="0 0 14 10"
      xmlns="http://www.w3.org/2000/svg"
    >
      <g>
        <path
          className={styles.arrowPoint}
          d={pointLeft ? "M14.8 1l-4 4 4 4" : "M-0.8 1l4 4-4 4"}
          fill="none"
          stroke="currentColor"
          strokeLinecap="square"
          strokeLinejoin="miter"
          strokeWidth="2"
        />
        <path
          className={styles.arrowShaft}
          d={pointLeft ? "M14.8 5H9.8" : "M0 5h4.8"}
          fill="none"
          stroke="currentColor"
          strokeLinecap="square"
          strokeLinejoin="miter"
          strokeWidth="2"
        />
      </g>
    </svg>
  );
}

interface ButtonRootProps
  extends Omit<React.ComponentProps<"button">, "className" | "style">,
    VariantProps<typeof buttonVariants> {
  className?: string | ((state: Button.State) => string | undefined);
  style?: React.CSSProperties | ((state: Button.State) => React.CSSProperties | undefined);
  render?: React.ReactElement | ((props: React.HTMLAttributes<HTMLElement>, state: Button.State) => React.ReactElement);
  focusableWhenDisabled?: boolean;
  nativeButton?: boolean;
  showArrow?: boolean;
  pointLeft?: boolean;
  pointExternal?: boolean;
  loading?: boolean;
}

function ButtonRoot({
  className,
  variant,
  size,
  showArrow = false,
  pointLeft = false,
  pointExternal = false,
  loading = false,
  children,
  render,
  nativeButton,
  ...props
}: ButtonRootProps) {
  const decoratedChildren = (
    <>
      {loading ? <Spinner /> : null}
      {!loading && showArrow && pointLeft && <ArrowPointer pointExternal={pointExternal} pointLeft />}
      {children}
      {!loading && showArrow && !pointLeft && <ArrowPointer pointExternal={pointExternal} />}
    </>
  );

  // Auto-detect nativeButton based on render prop if not explicitly set
  const isNativeButton = nativeButton ?? (render ? false : undefined);

  return (
    <Button
      className={cn(buttonVariants({ variant, size }), loading ? styles.loading : null, className)}
      data-slot="button"
      disabled={props.disabled || loading}
      focusableWhenDisabled={loading}
      nativeButton={isNativeButton}
      {...(render !== undefined && { render })}
      {...props}
    >
      {decoratedChildren}
    </Button>
  );
}

export { ButtonRoot as Button, ArrowPointer };

.base {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  border-radius: var(--radius);
  font-weight: 450;
  transition:
    transform 0.2s var(--ease-out-quad),
    background-color 0.2s var(--ease-out-quad),
    box-shadow 0.2s var(--ease-out-quad);
  cursor: pointer;
  border: 1px solid transparent;
  outline: none;
  position: relative;
  overflow: hidden;
  letter-spacing: -0.014em;
  will-change: transform;
}

/* Scale animation removed from base - only applied to primary variant */

.base:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.base[data-disabled] {
  opacity: 0.7;
  cursor: not-allowed;
}

.base.loading {
  opacity: 0.7;
  cursor: not-allowed;
}

.primary {
  background-color: var(--primary);
  color: var(--primary-foreground);
  box-shadow:
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px;
}

.primary:hover:not([data-disabled]) {
  background-color: oklch(from var(--primary) l c h / 0.8);
}

.primary:active:not([data-disabled]):not(.loading) {
  transform: scale(0.97);
}

.secondary {
  background-color: var(--secondary);
  color: var(--secondary-foreground);
}

.secondary:hover:not([data-disabled]) {
  background-color: oklch(from var(--secondary) l c h / 0.8);
}

.destructive {
  background-color: var(--destructive);
  color: var(--destructive-foreground);
}

.destructive:hover:not([data-disabled]) {
  background-color: oklch(from var(--destructive) l c h / 0.85);
}

.ghost {
  background-color: transparent;
  color: var(--foreground);
}

.ghost:hover:not([data-disabled]) {
  background-color: oklch(from var(--accent) l c h / 0.6);
}

.outline {
  background-color: var(--mix-card-50-bg);
  color: var(--foreground);
  box-shadow: 0 0 0 1px oklch(from var(--border) l c h / 0.7);
}

.outline:hover:not([data-disabled]) {
  background-color: var(--mix-card-66-bg);
}

.link {
  background-color: transparent;
  color: var(--muted-foreground);
  padding: 0;
  text-decoration: none;
  transition: text-decoration 0.2s ease-out;
}

.link:hover:not([data-disabled]) {
  text-decoration: underline;
  color: var(--foreground);
}

.sm {
  height: 2rem;
  padding: 0 0.5rem;
  font-size: 0.875rem;
}

.md {
  height: 2.5rem;
  padding: 0.5rem 1rem;
  font-size: 0.925rem;
}

.lg {
  height: 3rem;
  padding: 0.5rem 1.5rem;
  font-size: 1rem;
}

.icon {
  height: auto;
  width: auto;
  padding: 0.375rem;
  font-size: 0.875rem;
  aspect-ratio: 1;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

.icon::before {
  content: "";
  position: absolute;
  display: block;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  min-height: 44px;
  min-width: 44px;
  z-index: -1;
}

.arrow {
  margin-top: 0;
  margin-left: 0;
  margin-right: -4px;
  position: relative;
  top: 0px;
  overflow: visible;
  width: 12px;
  height: 10px;
  transition: 200ms var(--ease-in-out-cubic);
}

.arrowLeft {
  margin-left: -4px;
  margin-right: 0;
}

.arrowExternal {
  transform-origin: 8%;
  transition: transform 200ms var(--ease-in-out-cubic);
}

.base:hover .arrowExternal {
  transform: rotate(-45deg);
}

.arrowPoint {
  transition: transform 200ms var(--ease-in-out-cubic);
}

.arrowShaft {
  transition:
    transform 200ms var(--ease-in-out-cubic),
    opacity 200ms var(--ease-in-out-cubic);
  opacity: 0;
  transform: translateX(-0.5px);
}

.base:hover .arrowPoint {
  transform: translateX(2px);
}

.base:hover .arrowShaft {
  opacity: 1;
  transform: translateX(-2px);
}

.base:hover .arrowLeft .arrowPoint {
  transform: translateX(-2px);
}

.base:hover .arrowLeft .arrowShaft {
  opacity: 1;
  transform: translateX(1px);
}

.spinner {
  margin-right: 0.5rem;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
    stroke-dashoffset: 31.416;
  }
  to {
    transform: rotate(360deg);
    stroke-dashoffset: 0;
  }
}
