"use client";

import { ChevronLeft, ChevronRight } from "lucide-react";
import * as React from "react";
import { type DayButton, DayPicker, getDefaultClassNames } from "react-day-picker";
import { cn } from "@/lib/utils";
import type { Button } from "@/registry/brook/ui/button/button";
import styles from "./calendar.module.css";

interface CalendarDayButtonProps extends React.ComponentProps<typeof DayButton> {}

function CalendarDayButton({ className, day, modifiers, ...props }: CalendarDayButtonProps) {
  const defaultClassNames = getDefaultClassNames();
  const buttonRef = React.useRef<HTMLButtonElement>(null);

  React.useEffect(() => {
    if (modifiers.focused) {
      buttonRef.current?.focus();
    }
  }, [modifiers.focused]);

  return (
    <button
      className={cn(styles.dayButton, defaultClassNames.day, className)}
      data-day={day.date.toLocaleDateString()}
      data-range-end={modifiers.range_end}
      data-range-middle={modifiers.range_middle}
      data-range-start={modifiers.range_start}
      data-selected-single={
        modifiers.selected && !modifiers.range_start && !modifiers.range_end && !modifiers.range_middle
      }
      ref={buttonRef}
      type="button"
      {...props}
    />
  );
}

type CalendarProps = React.ComponentProps<typeof DayPicker> & {
  buttonVariant?: React.ComponentProps<typeof Button>["variant"];
};

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  captionLayout = "label",
  buttonVariant = "ghost",
  formatters,
  components,
  ...props
}: CalendarProps) {
  const defaultClassNames = getDefaultClassNames();

  return (
    <DayPicker
      captionLayout={captionLayout}
      className={cn(styles.root, className)}
      classNames={{
        root: cn(styles.root, defaultClassNames.root),
        months: cn(styles.months, defaultClassNames.months),
        month: cn(styles.month, defaultClassNames.month),
        nav: cn(styles.nav, defaultClassNames.nav),
        button_previous: cn(styles.navButton, defaultClassNames.button_previous),
        button_next: cn(styles.navButton, defaultClassNames.button_next),
        month_caption: cn(styles.monthCaption, defaultClassNames.month_caption),
        dropdowns: cn(styles.dropdowns, defaultClassNames.dropdowns),
        dropdown_root: cn(styles.dropdownRoot, defaultClassNames.dropdown_root),
        dropdown: cn(styles.dropdown, defaultClassNames.dropdown),
        caption_label: cn(
          styles.captionLabel,
          captionLayout !== "label" && styles.captionLabelDropdown,
          defaultClassNames.caption_label
        ),
        table: styles.table,
        weekdays: cn(styles.weekdays, defaultClassNames.weekdays),
        weekday: cn(styles.weekday, defaultClassNames.weekday),
        week: cn(styles.week, defaultClassNames.week),
        week_number_header: cn(styles.weekNumberHeader, defaultClassNames.week_number_header),
        week_number: cn(styles.weekNumber, defaultClassNames.week_number),
        day: cn(styles.day, props.showWeekNumber ? styles.dayWithWeekNumber : styles.dayDefault, defaultClassNames.day),
        range_start: cn(styles.rangeStart, defaultClassNames.range_start),
        range_middle: cn(styles.rangeMiddle, defaultClassNames.range_middle),
        range_end: cn(styles.rangeEnd, defaultClassNames.range_end),
        today: cn(styles.today, defaultClassNames.today),
        outside: cn(styles.outside, defaultClassNames.outside),
        disabled: cn(styles.disabled, defaultClassNames.disabled),
        hidden: cn(styles.hidden, defaultClassNames.hidden),
        ...classNames,
      }}
      components={{
        Root: ({ className: rootClassName, rootRef, ...rootProps }) => (
          <div className={cn(rootClassName)} data-slot="calendar" ref={rootRef} {...rootProps} />
        ),
        Chevron: ({ className: chevronClassName, orientation, ...chevronProps }) => {
          if (orientation === "left") {
            return <ChevronLeft className={cn(styles.chevron, chevronClassName)} {...chevronProps} />;
          }
          return <ChevronRight className={cn(styles.chevron, chevronClassName)} {...chevronProps} />;
        },
        DayButton: CalendarDayButton,
        WeekNumber: ({ children, ...weekNumberProps }) => (
          <td {...weekNumberProps}>
            <div className={styles.weekNumberCell}>{children}</div>
          </td>
        ),
        ...components,
      }}
      formatters={{
        formatMonthDropdown: (date) => date.toLocaleString("default", { month: "short" }),
        ...formatters,
      }}
      showOutsideDays={showOutsideDays}
      {...props}
    />
  );
}

export { Calendar, CalendarDayButton };

.root {
  --cell-size: 2rem;
  padding: 0.75rem;
  background-color: var(--popover);
  width: fit-content;
}

.months {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  position: relative;
}

@media (min-width: 768px) {
  .months {
    flex-direction: row;
  }
}

.month {
  display: flex;
  flex-direction: column;
  width: 100%;
  gap: 1rem;
}

.nav {
  display: flex;
  align-items: center;
  gap: 0.25rem;
  width: 100%;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  justify-content: space-between;
}

.navButton {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: var(--cell-size);
  height: var(--cell-size);
  padding: 0;
  user-select: none;
  border-radius: var(--radius);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  background-color: var(--popover);
  color: var(--foreground);
  cursor: pointer;
  transition:
    background-color 150ms,
    border-color 150ms;
}

.navButton:hover {
  background-color: var(--accent);
  border-color: var(--accent);
}

.navButton:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.navButton[aria-disabled="true"] {
  opacity: 0.5;
  pointer-events: none;
}

.monthCaption {
  display: flex;
  align-items: center;
  justify-content: center;
  height: var(--cell-size);
  width: 100%;
  padding-left: var(--cell-size);
  padding-right: var(--cell-size);
}

.dropdowns {
  width: 100%;
  display: flex;
  align-items: center;
  font-size: 0.875rem;
  font-weight: 500;
  justify-content: center;
  height: var(--cell-size);
  gap: 0.375rem;
}

.dropdownRoot {
  position: relative;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  box-shadow: var(--shadow-xs);
}

.dropdownRoot:has(:focus) {
  border-color: var(--ring);
  box-shadow: 0 0 0 3px oklch(from var(--ring) l c h / 0.5);
}

.dropdown {
  position: absolute;
  inset: 0;
  opacity: 0;
  background-color: var(--popover);
}

.captionLabel {
  user-select: none;
  font-weight: 500;
  font-size: 0.875rem;
}

.captionLabelDropdown {
  border-radius: var(--radius);
  padding-left: 0.5rem;
  padding-right: 0.25rem;
  display: flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.875rem;
  height: 2rem;
}

.captionLabelDropdown svg {
  color: var(--muted-foreground);
  width: 0.875rem;
  height: 0.875rem;
}

.table {
  width: 100%;
  border-collapse: collapse;
}

.weekdays {
  display: flex;
}

.weekday {
  color: var(--muted-foreground);
  border-radius: var(--radius);
  flex: 1;
  font-weight: 400;
  font-size: 0.8rem;
  user-select: none;
  text-align: center;
}

.week {
  display: flex;
  width: 100%;
  margin-top: 0.5rem;
}

.weekNumberHeader {
  user-select: none;
  width: var(--cell-size);
}

.weekNumber {
  font-size: 0.8rem;
  user-select: none;
  color: var(--muted-foreground);
}

.weekNumberCell {
  display: flex;
  width: var(--cell-size);
  height: var(--cell-size);
  align-items: center;
  justify-content: center;
  text-align: center;
}

.day {
  position: relative;
  flex: 1;
  padding: 0;
  text-align: center;
  user-select: none;
}

.dayDefault[data-selected="true"] button {
  border-top-left-radius: var(--radius);
  border-bottom-left-radius: var(--radius);
}

.day:last-child[data-selected="true"] button {
  border-top-right-radius: var(--radius);
  border-bottom-right-radius: var(--radius);
}

.dayWithWeekNumber:nth-child(2)[data-selected="true"] button {
  border-top-left-radius: var(--radius);
  border-bottom-left-radius: var(--radius);
}

.rangeStart {
  border-top-left-radius: var(--radius);
  border-bottom-left-radius: var(--radius);
  background-color: var(--accent);
}

.rangeMiddle {
  border-radius: 0;
}

.rangeEnd {
  border-top-right-radius: var(--radius);
  border-bottom-right-radius: var(--radius);
  background-color: var(--accent);
}

.today {
}

.today[data-selected="true"] {
}

.outside {
  color: var(--muted-foreground);
}

.outside[aria-selected="true"] {
  color: var(--muted-foreground);
}

.disabled {
  color: var(--muted-foreground);
  opacity: 0.5;
}

.hidden {
  visibility: hidden;
}

.chevron {
  width: 1rem;
  height: 1rem;
}

.dayButton {
  display: flex;
  aspect-ratio: 1;
  width: 100%;
  min-width: var(--cell-size);
  height: auto;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 0.25rem;
  line-height: 1;
  font-weight: 400;
  font-size: 0.875rem;
  border-radius: var(--radius);
  border: none;
  background-color: transparent;
  color: var(--foreground);
  cursor: pointer;
  transition: background-color 150ms;
}

.dayButton:hover:not([data-selected-single="true"]):not(
    [data-range-start="true"]
  ):not([data-range-end="true"]) {
  background-color: var(--accent);
}

.dayButton span {
  font-size: 0.75rem;
  opacity: 0.7;
}

.dayButton[data-selected-single="true"] {
  background-color: var(--primary);
  color: var(--primary-foreground);
}

.dayButton[data-range-start="true"] {
  background-color: var(--primary);
  color: var(--primary-foreground);
  border-radius: var(--radius);
  border-top-left-radius: var(--radius);
  border-bottom-left-radius: var(--radius);
}

.dayButton[data-range-end="true"] {
  background-color: var(--primary);
  color: var(--primary-foreground);
  border-radius: var(--radius);
  border-top-right-radius: var(--radius);
  border-bottom-right-radius: var(--radius);
}

.dayButton[data-range-middle="true"] {
  background-color: var(--accent);
  color: var(--accent-foreground);
  border-radius: 0;
}

.day[data-focused="true"] .dayButton {
  position: relative;
  z-index: 10;
  box-shadow: 0 0 0 3px oklch(from var(--ring) l c h / 0.5);
  border-color: var(--ring);
}
