"use client";
import { CardImageSection } from "./components/card-image-section";
import data from "./data.json";

export default function Page() {
  return <CardImageSection data={data} />;
}

import { Button } from "@/registry/brook/ui/button/button";
import { Card, CardContent, CardDescription, CardFooter, CardImage, CardTitle } from "@/registry/brook/ui/card/card";
import styles from "./card-image-section.module.css";

type CardData = {
  id: number;
  imageSrc: string;
  alt: string;
  title: string;
  description: string;
};

type CardImageSectionProps = {
  data: {
    caption: string;
    heading: string;
    description: string;
    cards: CardData[];
  };
};

export function CardImageSection({ data }: CardImageSectionProps) {
  return (
    <section className={styles.section}>
      <header className={styles.header}>
        <span className={styles.caption}>{data.caption}</span>
        <h2 className={styles.heading}>{data.heading}</h2>
        <p className={styles.description}>{data.description}</p>
      </header>

      <div className={styles.grid}>
        {data.cards.map((card) => (
          <Card className={styles.card} key={card.id} variant="lift">
            <CardImage alt={card.alt} className={styles.cardImage} src={card.imageSrc} />

            <CardContent>
              <CardTitle className={styles.cardTitle}>{card.title}</CardTitle>
              <CardDescription>{card.description}</CardDescription>
            </CardContent>

            <CardFooter>
              <Button className={styles.linkButton} showArrow size="sm" variant="link">
                Learn More
              </Button>
            </CardFooter>
          </Card>
        ))}
      </div>
    </section>
  );
}

.section {
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
  padding: 48px 24px;
}

.header {
  text-align: left;
  margin-bottom: 48px;
}

.caption {
  display: block;
  font-size: var(--font-size-sm);
  font-weight: 500;
  color: var(--accent);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  margin-bottom: 12px;
}

.heading {
  font-size: clamp(1.75rem, 4vw, 2.5rem);
  font-weight: 600;
  color: var(--foreground);
  margin: 0 0 16px 0;
  letter-spacing: -0.02em;
  line-height: 1.2;
}

.description {
  font-size: var(--font-size-base);
  color: var(--muted-foreground);
  margin: 0;
  max-width: 600px;
  line-height: 1.6;
}

.grid {
  columns: 2;
  column-gap: 24px;
}

.card {
  break-inside: avoid;
  margin-bottom: 24px;
  border-radius: 20px;
  max-width: 100%;
  transform: translateZ(0);
}

.card:first-child {
  margin-top: 120px;
}

.cardImage {
  border-radius: 12px;
  width: auto;
}

.cardTitle {
  margin: 0;
}

.card [data-slot="card-content"] {
  gap: 0.5rem;
}

.linkButton {
  padding-left: 0;
}

@media (max-width: 640px) {
  .section {
    padding: 32px 16px;
  }

  .header {
    margin-bottom: 32px;
  }

  .grid {
    columns: 1;
  }

  .card {
    margin-bottom: 16px;
    border-radius: 16px;
  }

  .card:first-child {
    margin-top: 0;
  }

  .cardImage {
    border-radius: 8px;
  }

  .cardTitle {
    font-size: 1.125rem;
    line-height: 1.3;
  }
}

