import { Button } from "@/registry/brook/ui/button/button";
import { Card, CardContent, CardDescription, CardFooter, CardImage, CardTitle } from "@/registry/brook/ui/card/card";
import styles from "./card-image.module.css";

export default function CardImageDemo() {
  return (
    <Card className={styles.card} variant="lift">
      <CardImage
        alt="Veiled woman gazing at architecture under the night sky"
        className={styles.cardImage}
        src="/scene_01.svg"
      />

      <CardContent>
        <CardTitle className={styles.title}>Threshold</CardTitle>

        <CardDescription>
          Between what we build and what eternally exists standing in quiet contemplation.
        </CardDescription>
      </CardContent>

      <CardFooter>
        <Button showArrow size="sm" variant="outline">
          Learn More
        </Button>
      </CardFooter>
    </Card>
  );
}

.card {
  max-width: 360px;
  border-radius: 24px;
}

.cardImage {
  border-radius: 12px;
  width: auto;
}

.title {
  margin: 0;
}

.card [data-slot="card-content"] {
  gap: 0.75rem;
}

@media (max-width: 640px) {
  .card {
    max-width: 100%;
    border-radius: 16px;
  }

  .cardImage {
    border-radius: 8px;
  }

  .title {
    font-size: 1.125rem;
    line-height: 1.3;
  }
}
