import { Button } from "@/registry/brook/ui/button/button";
import {
  Card,
  CardContent,
  CardFooter,
  CardImage,
  CardTitle,
} from "@/registry/brook/ui/card/card";
import styles from "./card-image.module.css";

export default function CardImageDemo() {
  return (
    <Card className={styles.card} variant="lift">
      <CardImage
        alt="Veiled woman gazing at architecture under the night sky"
        className={styles.cardImage}
        src="/scene_01.svg"
      />

      <CardContent>
        <CardTitle className={styles.title}>Threshold</CardTitle>
        <p className={styles.description}>
          Between what we build and what eternally exists standing in quiet
          contemplation.
        </p>
      </CardContent>

      <CardFooter>
        <Button showArrow size="sm" variant="outline">
          Learn More
        </Button>
      </CardFooter>
    </Card>
  );
}

.card {
  max-width: 360px;
  border-radius: 24px;
}

.cardImage {
  border-radius: 12px;
  width: auto;
}

.title {
  margin: 0;
}

.description {
  margin: 0;
  color: var(--muted-foreground);
}

@media (max-width: 640px) {
  .card {
    max-width: 100%;
    border-radius: 16px;
  }

  .cardImage {
    border-radius: 8px;
  }

  .title {
    font-size: 1.125rem;
    line-height: 1.3;
  }

  .description {
    font-size: 0.9375rem;
    line-height: 1.5;
  }
}
