"use client";

import { AlertCircle, Check } from "lucide-react";
import { Card, CardContent } from "@/registry/brook/ui/card/card";
import styles from "./progress-card.module.css";

export type StepStatus = "pending" | "in_progress" | "complete" | "error" | "warning";

export type Step = {
  id: string;
  title: string;
  description?: string;
  status: StepStatus;
};

type StepIndicatorProps = {
  status: StepStatus;
  isFinal?: boolean;
};

function AnimatedClock({ className }: { className?: string }) {
  return (
    <svg
      className={className}
      fill="none"
      height="12"
      stroke="currentColor"
      strokeLinecap="round"
      strokeLinejoin="round"
      strokeWidth="2.5"
      viewBox="0 0 24 24"
      width="12"
    >
      <circle cx="12" cy="12" r="10" />
      <line x1="12" x2="12" y1="12" y2="7" />
      <line className={styles.clockHand} x1="12" x2="15" y1="12" y2="13" />
    </svg>
  );
}

function getIndicatorConfig(status: StepStatus, isFinal: boolean) {
  switch (status) {
    case "complete":
      return {
        className: `${styles.spinnerToCheck} ${isFinal ? styles.finalCheck : ""}`,
        icon: "check",
      } as const;
    case "in_progress":
      return { className: styles.spinnerCircle, icon: null } as const;
    case "error":
      return { className: styles.errorCircle, icon: "error" } as const;
    case "warning":
      return { className: styles.clockCircle, icon: "clock" } as const;
    default:
      return { className: styles.pendingCircle, icon: null } as const;
  }
}

function StepIndicator({ status, isFinal = false }: StepIndicatorProps) {
  const { className, icon } = getIndicatorConfig(status, isFinal);
  const showSpinner = status === "in_progress" || status === "complete";
  const isComplete = status === "complete";

  return (
    <div className={styles.indicatorWrapper}>
      {showSpinner && (
        <div className={`${styles.spinnerCircleOverlay} ${isComplete ? styles.spinnerFading : ""}`} />
      )}
      <div className={className}>
        {icon === "clock" && <AnimatedClock className={styles.clockIcon} />}
        {icon === "check" && <Check className={styles.checkIcon} size={14} strokeWidth={3} />}
        {icon === "error" && <AlertCircle className={styles.errorIcon} size={14} strokeWidth={2.5} />}
      </div>
    </div>
  );
}

type ProgressCardProps = {
  steps: Step[];
};

export function ProgressCard({ steps = [] }: ProgressCardProps) {
  const allComplete = steps.length > 0 && steps.every((s) => s.status === "complete");

  return (
    <Card className={styles.card}>
      <CardContent className={styles.content}>
        <div className={styles.stepList}>
          {steps.map((step, index) => {
            const isHighlighted = step.status === "in_progress" || step.status === "error" || step.status === "warning";
            const showDescription = isHighlighted && step.description;
            const isFinal = index === steps.length - 1 && allComplete;

            return (
              <div className={`${styles.stepItem} ${styles[step.status]}`} key={step.id}>
                <div className={styles.stepIndicator}>
                  <StepIndicator status={step.status} isFinal={isFinal} />
                </div>
                <div className={styles.stepContent}>
                  <span
                    className={`${styles.stepTitle} ${step.status === "in_progress" ? styles.stepTitleShimmer : ""}`}
                  >
                    {step.title}
                  </span>
                  {step.description ? (
                    <div className={`${styles.descriptionWrapper} ${showDescription ? styles.descriptionVisible : ""}`}>
                      <span className={styles.stepDescription}>{step.description}</span>
                    </div>
                  ) : null}
                </div>
              </div>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

.card {
  width: 100%;
  max-width: 320px;
}

.card[data-slot="card"] {
  padding: 16px;
  background-color: oklch(from var(--card) l c h / 0.6);
  box-shadow:
    0 0 0 1px oklch(from var(--border) l c h / 0.5),
    0 1px 3px 0 #0000000d,
    0 1px 2px -1px #0000000d;
}

.content {
  display: flex;
  flex-direction: column;
}

.stepList {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.stepItem {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  padding: 12px;
  border-radius: var(--radius);
  transition: background-color 200ms ease-out;
  position: relative;
}

.pending {
}
.in_progress {
}
.complete {
}
.error {
}
.warning {
}

.stepIndicator {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 24px;
  height: 24px;
  flex-shrink: 0;
  position: relative;
}

.stepItem:not(:last-child)::after {
  content: "";
  position: absolute;
  left: 23px;
  top: 36px;
  width: 1px;
  height: calc(100% - 16px);
  background-color: var(--border);
  transition: background-color 200ms ease-out;
  z-index: 0;
}

.stepItem.complete:not(:last-child)::after {
  background-color: var(--foreground);
}

.indicatorWrapper {
  position: relative;
  width: 20px;
  height: 20px;
}

.spinnerCircleOverlay {
  position: absolute;
  inset: 0;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 2px solid var(--border);
  border-top-color: var(--foreground);
  background-color: var(--card);
  animation: spin 0.8s linear infinite;
  opacity: 1;
  transition: opacity 400ms ease-out;
  z-index: 2;
}

.spinnerFading {
  opacity: 0;
}

.pendingCircle {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 2px solid var(--border);
  background-color: var(--card);
  position: relative;
  z-index: 1;
}

.spinnerCircle {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  border: 2px solid var(--border);
  border-top-color: var(--foreground);
  background-color: var(--card);
  animation:
    spin 0.8s linear infinite,
    fadeIn 150ms ease-out;
  will-change: transform, opacity;
  position: relative;
  z-index: 1;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.spinnerToCheck {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
  background-color: var(--foreground);
  animation: circleSettle 200ms cubic-bezier(0.165, 0.84, 0.44, 1) forwards;
}

@keyframes circleSettle {
  0% {
    opacity: 0;
    transform: scale(0.97);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes pulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

.checkIcon {
  color: var(--background);
  position: relative;
  z-index: 1;
  opacity: 0;
  animation: iconAppear 150ms cubic-bezier(0.165, 0.84, 0.44, 1) 50ms forwards;
}

@keyframes iconAppear {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.clockCircle {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: var(--warning);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
}

.clockIcon {
  color: var(--warning-foreground);
}

.clockHand {
  transform-origin: 12px 12px;
  animation: clockRotate 2s linear infinite;
}

@keyframes clockRotate {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.errorCircle {
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: var(--destructive);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
}

.errorIcon {
  color: var(--destructive-foreground);
}

.stepItem.error .stepTitle {
  color: var(--destructive);
}

.finalCheck {
  background-color: var(--success, #22c55e);
  animation: circleSettle 200ms cubic-bezier(0.165, 0.84, 0.44, 1) forwards;
}

.stepContent {
  display: flex;
  flex-direction: column;
  gap: 2px;
  padding-top: 2px;
  min-height: 24px;
}

.stepTitle {
  font-size: 0.9375rem;
  font-weight: 500;
  color: var(--muted-foreground);
  transition: color 200ms ease;
}

.stepItem.complete .stepTitle {
  color: oklch(from var(--foreground) l c h / 0.5);
}

.stepItem.error .stepTitle {
  color: var(--destructive);
}

.stepItem.error .stepDescription {
  color: var(--destructive-foreground);
}

.stepItem.warning .stepTitle {
  color: var(--warning);
}

.stepTitleShimmer {
  background: linear-gradient(
    90deg,
    var(--muted-foreground) 0%,
    var(--muted-foreground) 40%,
    color-mix(in oklch, var(--foreground) 50%, var(--muted-foreground)) 50%,
    var(--muted-foreground) 60%,
    var(--muted-foreground) 100%
  );
  background-size: 200% 100%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: shimmer 1.5s ease-in-out infinite;
}

@keyframes shimmer {
  0% {
    background-position: 100% 0;
  }
  100% {
    background-position: -100% 0;
  }
}

.descriptionWrapper {
  display: grid;
  grid-template-rows: 0fr;
  opacity: 0;
  transition:
    grid-template-rows 250ms cubic-bezier(0.165, 0.84, 0.44, 1),
    opacity 200ms ease-out;
}

.descriptionWrapper.descriptionVisible {
  grid-template-rows: 1fr;
  opacity: 1;
}

.stepDescription {
  font-size: 0.8125rem;
  color: var(--muted-foreground);
  overflow: hidden;
}

@media (prefers-reduced-motion: reduce) {
  .spinnerCircle {
    animation: none;
  }

  .spinnerCircleOverlay {
    animation: none;
    transition: none;
  }

  .spinnerFading {
    opacity: 0;
  }

  .clockHand {
    animation: none;
  }

  .checkIcon {
    animation: none;
    opacity: 1;
  }

  .spinnerToCheck {
    animation: none;
  }

  .finalCheck {
    animation: none;
  }

  .descriptionWrapper {
    transition: none;
  }

  .stepItem {
    transition: none;
  }

  .stepTitle {
    transition: none;
  }

  .stepTitleShimmer {
    animation: none;
    background: none;
    -webkit-text-fill-color: var(--muted-foreground);
  }
}
