"use client";

import { Calendar, ListTodo, MessageCircleMore, Users } from "lucide-react";
import { useState } from "react";
import { Badge } from "@/registry/brook/tailwind/ui/badge";
import {
  Card,
  CardAction,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/registry/brook/tailwind/ui/card";
import { AvatarGroup } from "./avatar-group";
import { CollaboratorDialog } from "./collaborator-dialog";
import { DeleteTaskAlertDialog } from "./delete-task-alert-dialog";
import { TaskCardDropdownMenu } from "./task-card-dropdown-menu";

export type User = {
  value: string;
  label: string;
  email: string;
  avatar: string;
};

type TaskCardProps = {
  title: string;
  description: string;
  tags: Array<{
    label: string;
    variant?: "default" | "destructive";
  }>;
  collaborators: User[];
  onCollaboratorsChange?: (collaborators: User[]) => void;
  availableUsers?: User[];
  stats: {
    comments: number;
    subtasks: string;
  };
  dueDate: {
    label: string;
    variant?: "default" | "warning";
  };
  onDelete?: () => void;
};

export function CardTask({
  title,
  description,
  tags,
  collaborators,
  onCollaboratorsChange,
  availableUsers = [],
  stats,
  dueDate,
  onDelete,
}: TaskCardProps) {
  const [alertOpen, setAlertOpen] = useState(false);
  const [collaboratorDialogOpen, setCollaboratorDialogOpen] = useState(false);

  const handleCollaboratorsConfirm = (newCollaborators: User[]) => {
    onCollaboratorsChange?.(newCollaborators);
  };

  const handleDelete = () => {
    onDelete?.();
    setAlertOpen(false);
  };

  return (
    <>
      <Card className="w-full max-w-[300px] gap-4 p-4 xl:min-h-auto">
        <CardHeader>
          <CardTitle className="text-base">{title}</CardTitle>
          <CardDescription>{description}</CardDescription>
          <CardAction>
            <TaskCardDropdownMenu
              onAddCollaborator={() => setCollaboratorDialogOpen(true)}
              onDeleteTask={() => setAlertOpen(true)}
            />
          </CardAction>
        </CardHeader>
        <CardContent>
          <div className="flex w-full items-center justify-between">
            <div className="flex items-center gap-2">
              {tags.map((tag) => (
                <Badge key={tag.label} size="sm" variant={tag.variant}>
                  <span>{tag.label}</span>
                </Badge>
              ))}
            </div>
            <AvatarGroup users={collaborators} />
          </div>
        </CardContent>

        <CardFooter className="mb-0 mt-0 flex flex-col gap-[0.65rem]">
          <div className="-mx-4 w-[calc(100%+32px)] py-[5px]">
            <div className="h-px w-full border-b-[0.5px] border-[oklch(from_var(--border)_l_c_h_/_0.6)]" />
          </div>
          <div className="flex w-full justify-between text-xs text-[oklch(from_var(--muted-foreground)_l_c_h_/_0.6)]">
            <div className="flex items-center gap-3">
              <div className="flex h-[27px] items-center justify-center gap-1 rounded-xl border-[0.5px] border-[oklch(from_var(--border)_l_c_h_/_0.2)] bg-[var(--mix-card-33-bg)] px-2 py-1 transition-all duration-150 hover:border-[var(--border)] hover:bg-[var(--mix-card-50-bg)]">
                <Users size="14" />
                <span>{collaborators.length}</span>
              </div>
              <div className="flex h-[27px] items-center justify-center gap-1 rounded-xl border-[0.5px] border-[oklch(from_var(--border)_l_c_h_/_0.2)] bg-[var(--mix-card-33-bg)] px-2 py-1 transition-all duration-150 hover:border-[var(--border)] hover:bg-[var(--mix-card-50-bg)]">
                <MessageCircleMore size="14" />
                <span>{stats.comments}</span>
              </div>
              <div className="flex h-[27px] items-center justify-center gap-1 rounded-xl border-[0.5px] border-[oklch(from_var(--border)_l_c_h_/_0.2)] bg-[var(--mix-card-33-bg)] px-2 py-1 transition-all duration-150 hover:border-[var(--border)] hover:bg-[var(--mix-card-50-bg)]">
                <ListTodo size="14" />
                <span>{stats.subtasks}</span>
              </div>
            </div>
            <div className="flex h-[27px] items-center justify-center gap-1 rounded-xl border-[0.5px] border-[oklch(from_var(--border)_l_c_h_/_0.2)] bg-[var(--mix-card-33-bg)] px-2 py-1 transition-all duration-150 hover:border-[var(--border)] hover:bg-[var(--mix-card-50-bg)]">
              <Calendar size="14" />
              <span
                className={
                  dueDate.variant === "warning"
                    ? "text-[var(--warning-foreground)]"
                    : undefined
                }
              >
                {dueDate.label}
              </span>
            </div>
          </div>
        </CardFooter>
      </Card>

      <DeleteTaskAlertDialog
        onDelete={handleDelete}
        onOpenChange={setAlertOpen}
        open={alertOpen}
      />

      {availableUsers.length > 0 && (
        <CollaboratorDialog
          availableUsers={availableUsers}
          currentCollaborators={collaborators}
          onConfirm={handleCollaboratorsConfirm}
          onOpenChange={setCollaboratorDialogOpen}
          open={collaboratorDialogOpen}
        />
      )}
    </>
  );
}

// Demo data for examples/documentation
const demoUsers: User[] = [
  {
    value: "preetecool",
    label: "preetecool",
    email: "@preetecool",
    avatar: "/preetecool.png",
  },
  {
    value: "john-doe",
    label: "John Doe",
    email: "john@example.com",
    avatar:
      "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=32&h=32&fit=crop&crop=face",
  },
  {
    value: "jane-smith",
    label: "Jane Smith",
    email: "jane@example.com",
    avatar:
      "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=32&h=32&fit=crop&crop=face",
  },
  {
    value: "mike-johnson",
    label: "Mike Johnson",
    email: "mike@example.com",
    avatar:
      "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=32&h=32&fit=crop&crop=face",
  },
];

export default function CardTaskDemo() {
  const [collaborators, setCollaborators] = useState<User[]>([demoUsers[0]]);

  return (
    <CardTask
      availableUsers={demoUsers}
      collaborators={collaborators}
      description="Update the card component documentation to reflect the new style"
      dueDate={{ label: "1d", variant: "warning" }}
      onCollaboratorsChange={setCollaborators}
      onDelete={() => console.log("Task deleted")}
      stats={{ comments: 4, subtasks: "4/5" }}
      tags={[
        { label: "Urgent", variant: "destructive" },
        { label: "Docs" },
      ]}
      title="Update Documentation"
    />
  );
}

import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/brook/tailwind/ui/avatar";
import {
  Tooltip,
  TooltipArrow,
  TooltipPopup,
  TooltipPortal,
  TooltipPositioner,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/brook/tailwind/ui/tooltip";
import type { User } from "./card-task";

type AvatarGroupProps = {
  users: User[];
  maxDisplay?: number;
};

export function AvatarGroup({ users, maxDisplay = 3 }: AvatarGroupProps) {
  const displayUsers = users.slice(0, maxDisplay);
  const remainingCount = users.length - maxDisplay;

  return (
    <TooltipProvider>
      <div className="flex items-center">
        {displayUsers.map((user, index) => (
          <Tooltip key={user.value}>
            <TooltipTrigger
              render={
                <div className={index > 0 ? "-ml-[6px]" : ""}>
                  <Avatar
                    className="border-2 border-[var(--card)]"
                    style={{ width: "24px", height: "24px" }}
                  >
                    <AvatarImage
                      alt={`profile image for ${user.label}`}
                      src={user.avatar}
                    />
                    <AvatarFallback>
                      {user.label
                        ?.split(" ")
                        .map((n: string) => n[0])
                        .join("") || "??"}
                    </AvatarFallback>
                  </Avatar>
                </div>
              }
            />
            <TooltipPortal>
              <TooltipPositioner>
                <TooltipPopup>
                  <TooltipArrow />
                  {user.label}
                </TooltipPopup>
              </TooltipPositioner>
            </TooltipPortal>
          </Tooltip>
        ))}
        {remainingCount > 0 && (
          <div className="-ml-[6px] flex h-6 w-6 items-center justify-center rounded-full border-2 border-[var(--card)] bg-[var(--muted)] font-semibold text-[0.625rem] text-[var(--muted-foreground)]">
            +{remainingCount}
          </div>
        )}
      </div>
    </TooltipProvider>
  );
}

import { cn } from "@/lib/utils";
import {
  AlertDialog,
  AlertDialogClose,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/registry/brook/tailwind/ui/alert-dialog";
import { Button } from "@/registry/brook/tailwind/ui/button";

type DeleteTaskAlertDialogProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onDelete: () => void;
};

export function DeleteTaskAlertDialog({
  open,
  onOpenChange,
  onDelete,
}: DeleteTaskAlertDialogProps) {
  return (
    <AlertDialog onOpenChange={onOpenChange} open={open}>
      <AlertDialogContent
        className={cn(
          "flex max-h-[300px] w-[clamp(250px,90vw,400px)] flex-col gap-4 rounded-2xl p-6",
          "max-sm:!left-1/2 max-sm:!right-auto max-sm:!translate-x-[-50%] max-sm:!translate-y-[-50%]"
        )}
      >
        <AlertDialogHeader>
          <AlertDialogTitle>Delete Task</AlertDialogTitle>
          <AlertDialogDescription className="text-[var(--muted-foreground)]">
            Are you sure you want to delete this task?
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter className="mt-8 flex gap-3 [&>*]:flex-1">
          <AlertDialogClose
            render={<Button variant="outline">Cancel</Button>}
          />
          <Button onClick={onDelete} variant="destructive">
            Delete
          </Button>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

import { Calendar, MoreHorizontal, Trash, UserPlus } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/registry/brook/tailwind/ui/button";
import {
  DropdownMenu,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/brook/tailwind/ui/dropdown-menu";

type TaskCardDropdownMenuProps = {
  onAddCollaborator: () => void;
  onDeleteTask: () => void;
};

export function TaskCardDropdownMenu({
  onAddCollaborator,
  onDeleteTask,
}: TaskCardDropdownMenuProps) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger
        render={(props) => (
          <Button {...props} size="icon" variant="ghost">
            <MoreHorizontal
              size="16"
              style={{
                color: "oklch(from var(--muted-foreground) l c h / 0.8)",
              }}
            />
          </Button>
        )}
      />
      <DropdownMenuPortal>
        <DropdownMenuPositioner sideOffset={8}>
          <DropdownMenuPopup render={<ul />} style={{ minWidth: "160px" }}>
            <div style={{ height: "4px", width: "100%" }} />
            <DropdownMenuItem
              icon={<UserPlus size="14" />}
              onClick={onAddCollaborator}
              render={<li />}
            >
              Add collaborator
            </DropdownMenuItem>
            <DropdownMenuItem icon={<Calendar size="14" />} render={<li />}>
              Change due date
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem
              className={cn(
                "relative isolate text-[var(--destructive)]",
                "[&_svg]:text-[var(--destructive)]",
                "before:!absolute before:!left-1 before:!right-1 before:!top-0 before:!bottom-0 before:!-z-[1] before:!rounded-[0.3rem] before:!bg-transparent before:!content-['']",
                "hover:before:!bg-[var(--destructive)]",
                "data-[highlighted]:before:!bg-[var(--destructive)]",
                "hover:!text-[var(--destructive-foreground)]",
                "data-[highlighted]:!text-[var(--destructive-foreground)]",
                "hover:[&_svg]:!text-[var(--destructive-foreground)]",
                "data-[highlighted]:[&_svg]:!text-[var(--destructive-foreground)]"
              )}
              icon={<Trash size="14" />}
              onClick={onDeleteTask}
              render={<li />}
            >
              Delete task
            </DropdownMenuItem>
            <div style={{ height: "4px", width: "100%" }} />
          </DropdownMenuPopup>
        </DropdownMenuPositioner>
      </DropdownMenuPortal>
    </DropdownMenu>
  );
}

import { useRef, useState } from "react";
import { cn } from "@/lib/utils";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/brook/tailwind/ui/avatar";
import { Button } from "@/registry/brook/tailwind/ui/button";
import {
  Combobox,
  ComboboxEmpty,
  ComboboxInput,
  ComboboxItem,
  ComboboxList,
  ComboboxPopup,
  ComboboxPortal,
  ComboboxPositioner,
  ComboboxTrigger,
} from "@/registry/brook/tailwind/ui/combobox";
import {
  Dialog,
  DialogClose,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogOverlay,
  DialogPopup,
  DialogPortal,
  DialogTitle,
} from "@/registry/brook/tailwind/ui/dialog";
import {
  Tooltip,
  TooltipArrow,
  TooltipPopup,
  TooltipPortal,
  TooltipPositioner,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/brook/tailwind/ui/tooltip";
import type { User } from "./card-task";

type CollaboratorDialogProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  availableUsers: User[];
  currentCollaborators: User[];
  onConfirm: (collaborators: User[]) => void;
};

export function CollaboratorDialog({
  open,
  onOpenChange,
  availableUsers,
  currentCollaborators,
  onConfirm,
}: CollaboratorDialogProps) {
  const comboboxAnchorRef = useRef<HTMLDivElement>(null);
  const [selectedCollaborators, setSelectedCollaborators] =
    useState<User[]>(currentCollaborators);

  const handleOpenChange = (nextOpen: boolean) => {
    if (nextOpen) {
      setSelectedCollaborators([...currentCollaborators]);
    }
    onOpenChange(nextOpen);
  };

  const handleConfirm = () => {
    onConfirm(selectedCollaborators);
    onOpenChange(false);
  };

  const handleRemoveCollaborator = (userValue: string) => {
    setSelectedCollaborators(
      selectedCollaborators.filter((c) => c.value !== userValue)
    );
  };

  return (
    <Dialog onOpenChange={handleOpenChange} open={open}>
      <DialogPortal>
        <DialogOverlay />
        <DialogPopup className="max-w-[410px]">
          <DialogHeader>
            <DialogTitle>Add Collaborator</DialogTitle>
            <DialogDescription>
              Invite a team member to collaborate on this task.
            </DialogDescription>
          </DialogHeader>

          <div className="flex flex-col gap-4">
            {/* Current Collaborators */}
            <div>
              <div className="mb-2 block text-sm font-medium">
                Current Collaborators
              </div>
              <div className="flex flex-wrap items-center gap-2">
                <TooltipProvider>
                  {selectedCollaborators?.map((collaborator, index) => {
                    const isNewlyAdded = !currentCollaborators.some(
                      (c) => c.value === collaborator.value
                    );
                    const lastIndex = selectedCollaborators.length - 1;
                    const isLast = index === lastIndex;
                    const isFirst = index === 0;

                    return (
                      <Tooltip key={collaborator.value}>
                        <TooltipTrigger
                          render={
                            <div className="group relative inline-block">
                              <Avatar
                                className={cn(
                                  "h-8 w-8 rounded-full border-2 transition-[border]",
                                  isNewlyAdded
                                    ? "border-[var(--success)]"
                                    : "border-transparent"
                                )}
                              >
                                <AvatarImage
                                  alt={collaborator.label}
                                  src={collaborator.avatar}
                                />
                                <AvatarFallback>
                                  {collaborator.label
                                    ?.split(" ")
                                    .map((n: string) => n[0])
                                    .join("") || "??"}
                                </AvatarFallback>
                              </Avatar>
                              <button
                                className={cn(
                                  "-right-1 -top-1 absolute flex h-[18px] w-[18px] cursor-pointer items-center justify-center rounded-full border-2 border-[var(--background)] bg-[var(--destructive)] text-[10px] font-semibold leading-none text-[var(--destructive-foreground)] transition-all hover:scale-110 hover:bg-[oklch(from_var(--destructive)_calc(l*0.9)_c_h)]",
                                  "focus-visible:pointer-events-auto focus-visible:opacity-100 focus-visible:outline focus-visible:outline-2 focus-visible:outline-[var(--ring)] focus-visible:outline-offset-2",
                                  isLast && !isFirst
                                    ? "pointer-events-auto opacity-100"
                                    : "pointer-events-none opacity-0 group-hover:pointer-events-auto group-hover:opacity-100"
                                )}
                                onClick={() =>
                                  handleRemoveCollaborator(collaborator.value)
                                }
                                type="button"
                              >
                                Ã—
                              </button>
                            </div>
                          }
                        />
                        <TooltipPortal>
                          <TooltipPositioner>
                            <TooltipPopup>
                              <TooltipArrow />
                              {collaborator.label}
                            </TooltipPopup>
                          </TooltipPositioner>
                        </TooltipPortal>
                      </Tooltip>
                    );
                  })}
                </TooltipProvider>
              </div>
            </div>

            {/* Add New Collaborator */}
            <div className="flex flex-col gap-2">
              <div className="text-sm font-medium">Add New Collaborator</div>
              <Combobox<User, true>
                items={availableUsers}
                itemToStringLabel={(item: User | null) => item?.label || ""}
                itemToStringValue={(item: User | null) => item?.value || ""}
                multiple={true}
                onValueChange={(value) => {
                  if (value && Array.isArray(value)) {
                    setSelectedCollaborators(value);
                  }
                }}
                value={selectedCollaborators}
              >
                <div
                  className="relative flex w-full items-center rounded-[var(--radius)] border border-[var(--border)] bg-[var(--mix-card-50-bg)] transition-all duration-150 has-[:focus-visible]:outline has-[:focus-visible]:outline-2 has-[:focus-visible]:outline-[var(--ring)] has-[:focus-visible]:outline-offset-2"
                  ref={comboboxAnchorRef}
                >
                  <ComboboxInput placeholder="Search users..." />
                  <ComboboxTrigger />
                </div>

                <ComboboxPortal>
                  <ComboboxPositioner anchor={comboboxAnchorRef}>
                    <ComboboxPopup className="w-[var(--anchor-width)]">
                      <ComboboxEmpty>No user found.</ComboboxEmpty>
                      <ComboboxList>
                        {(user: User) => (
                          <ComboboxItem
                            indicatorPosition="right"
                            key={user.value}
                            value={user}
                          >
                            <div className="flex flex-1 items-center gap-4">
                              <Avatar className="h-6 w-6">
                                <AvatarImage
                                  alt={user.label}
                                  src={user.avatar}
                                />
                                <AvatarFallback>
                                  {user.label
                                    .split(" ")
                                    .map((n: string) => n[0])
                                    .join("")}
                                </AvatarFallback>
                              </Avatar>
                              <div className="text-left">
                                <div className="text-sm font-medium">
                                  {user.label}
                                </div>
                                <div className="text-xs text-[var(--muted-foreground)]">
                                  {user.email}
                                </div>
                              </div>
                            </div>
                          </ComboboxItem>
                        )}
                      </ComboboxList>
                    </ComboboxPopup>
                  </ComboboxPositioner>
                </ComboboxPortal>
              </Combobox>
            </div>
          </div>

          <DialogFooter className="mt-8 flex gap-3 [&>*]:flex-1">
            <DialogClose render={<Button variant="outline">Cancel</Button>} />
            <Button onClick={handleConfirm}>Confirm</Button>
          </DialogFooter>
        </DialogPopup>
      </DialogPortal>
    </Dialog>
  );
}
