"use client";

import { Calendar, ListTodo, MessageCircleMore, Users } from "lucide-react";
import { useState } from "react";
import { Badge } from "@/registry/brook/ui/badge/badge";
import {
  Card,
  CardAction,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/registry/brook/ui/card/card";
import { AvatarGroup } from "./avatar-group";
import styles from "./card-task.module.css";
import { CollaboratorDialog } from "./collaborator-dialog";
import { DeleteTaskAlertDialog } from "./delete-task-alert-dialog";
import { TaskCardDropdownMenu } from "./task-card-dropdown-menu";

type User = {
  value: string;
  label: string;
  email: string;
  avatar: string;
};

const users: User[] = [
  {
    value: "preetecool",
    label: "preetecool",
    email: "@preetecool",
    avatar: "/preetecool.png",
  },
  {
    value: "john-doe",
    label: "John Doe",
    email: "john@example.com",
    avatar:
      "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=32&h=32&fit=crop&crop=face",
  },
  {
    value: "jane-smith",
    label: "Jane Smith",
    email: "jane@example.com",
    avatar:
      "https://images.unsplash.com/photo-1494790108755-2616b612b786?w=32&h=32&fit=crop&crop=face",
  },
  {
    value: "mike-johnson",
    label: "Mike Johnson",
    email: "mike@example.com",
    avatar:
      "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=32&h=32&fit=crop&crop=face",
  },
];

export function CardTask() {
  const [alertOpen, setAlertOpen] = useState(false);
  const [collaboratorDialogOpen, setCollaboratorDialogOpen] = useState(false);
  const [currentCollaborators, setCurrentCollaborators] = useState<User[]>([
    users[0],
  ]);
  const [selectedCollaborators, setSelectedCollaborators] = useState<User[]>([
    users[0],
  ]);
  const [newlyAddedCollaborators, setNewlyAddedCollaborators] = useState<
    string[]
  >([]);

  const handleConfirm = () => {
    const previousCollaborators = currentCollaborators;
    setCurrentCollaborators(selectedCollaborators);

    // Track newly added: items that weren't in the previous list OR were removed and re-added
    const newlyAdded = selectedCollaborators
      .filter((s) => !previousCollaborators.find((c) => c.value === s.value))
      .map((s) => s.value);

    // Preserve previously newly-added items that are still selected
    const stillNewlyAdded = newlyAddedCollaborators.filter((value) =>
      selectedCollaborators.find((s) => s.value === value)
    );

    // Combine: keep old newly-added + add new ones
    setNewlyAddedCollaborators([
      ...new Set([...stillNewlyAdded, ...newlyAdded]),
    ]);
    setCollaboratorDialogOpen(false);
  };

  const handleDialogOpen = (open: boolean) => {
    if (open) {
      // Reset selected to current when opening
      setSelectedCollaborators([...currentCollaborators]);
      // Clear newly added collaborators when opening
      setNewlyAddedCollaborators([]);
    }
    setCollaboratorDialogOpen(open);
  };

  return (
    <>
      <Card className={styles.taskCard}>
        <CardHeader>
          <CardTitle style={{ fontSize: "1rem" }}>
            Update Documentation
          </CardTitle>
          <CardDescription>
            Update the card component documentation to reflect the new style
          </CardDescription>
          <CardAction>
            <TaskCardDropdownMenu
              onAddCollaborator={() => handleDialogOpen(true)}
              onDeleteTask={() => setAlertOpen(true)}
            />
          </CardAction>
        </CardHeader>
        <CardContent>
          <div className={styles.contentContainer}>
            <div className={styles.badgeContainer}>
              <Badge size="sm" variant="destructive">
                <span>Urgent</span>
              </Badge>
              <Badge size="sm">
                <span>Docs</span>
              </Badge>
            </div>
            <AvatarGroup users={currentCollaborators} />
          </div>
        </CardContent>

        <CardFooter className={styles.taskFooter}>
          <div
            style={{
              padding: "5px 0",
              width: "calc(100% + 32px)",
              marginLeft: "-16px",
              marginRight: "-16px",
            }}
          >
            <div
              style={{
                height: "1px",
                width: "100%",
                borderBottom:
                  "0.5px solid oklch(from var(--border) l c h / 0.6)",
              }}
            />
          </div>
          <div className={styles.footerContainer}>
            <div className={styles.footerLeftGroup}>
              <div className={styles.iconBubble}>
                <Users size="14" />
                <span>{currentCollaborators.length}</span>
              </div>
              <div className={styles.iconBubble}>
                <MessageCircleMore size="14" />
                <span>4</span>
              </div>
              <div className={styles.iconBubble}>
                <ListTodo size="14" />
                <span>4/5</span>
              </div>
            </div>
            <div className={styles.iconBubble}>
              <Calendar size="14" />
              <span className={styles.tomorrowText}>1d </span>
            </div>
          </div>
        </CardFooter>
      </Card>

      <DeleteTaskAlertDialog onOpenChange={setAlertOpen} open={alertOpen} />

      <CollaboratorDialog
        currentCollaborators={currentCollaborators}
        onConfirm={handleConfirm}
        onOpenChange={handleDialogOpen}
        onSelectedCollaboratorsChange={setSelectedCollaborators}
        open={collaboratorDialogOpen}
        selectedCollaborators={selectedCollaborators}
        users={users}
      />
    </>
  );
}

.taskCard {
  width: 100%;
  max-width: 300px;
  gap: 1rem;
  padding: 16px;
}

.contentContainer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}

.badgeContainer {
  display: flex;
  gap: 0.5rem;
  align-items: center;
}

.divider {
  width: calc(100% + 32px);
  height: 1px;
  background: oklch(from var(--border) l c h / 0.5);
  margin-left: -16px;
  margin-right: -16px;
  margin-top: auto;
}

.footerContainer {
  display: flex;
  justify-content: space-between;
  width: 100%;
  color: oklch(from var(--muted-foreground) l c h / 0.6);
  font-size: 12px;
}

.footerLeftGroup {
  display: flex;
  gap: 0.75rem;
  align-items: center;
}

.footerItem {
  display: flex;
  justify-content: flex-center;
  align-items: center;
  gap: 4px;
  height: 14px;
  line-height: 14px;
}

.iconBubble {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 4px;
  padding: 4px 8px;
  border-radius: 12px;
  background-color: var(--mix-card-33-bg);
  border: 0.5px solid oklch(from var(--border) l c h / 0.2);
  transition: all 150ms ease;
}

.iconBubble:hover {
  background-color: var(--mix-card-50-bg);
  border-color: var(--border);
}
.tomorrowText {
  color: var(--warning-foreground);
}

.taskFooter {
  margin-top: 0;
  margin-bottom: 0;
  display: flex;
  flex-direction: column;
  gap: 0.65rem;
}

.destructiveMenuItem {
  color: var(--destructive);
  isolation: isolate;
}

.destructiveMenuItem svg {
  color: var(--destructive);
}

.destructiveMenuItem::before {
  content: "";
  position: absolute;
  inset: 0 4px;
  border-radius: 0.3rem;
  background-color: transparent;
  z-index: -1;
}

.destructiveMenuItem:hover::before {
  background-color: var(--destructive);
}

.destructiveMenuItem:hover {
  color: var(--destructive-foreground);
}

.destructiveMenuItem:hover svg {
  color: var(--destructive-foreground);
}

@media (max-width: 1280px) {
  .taskCard {
    min-height: auto;
  }
}

.alertContent {
  width: clamp(250px, 90vw, 400px);
  max-height: 300px;
  border-radius: 1rem;
  display: flex;
  flex-direction: column;
  padding: 1.5rem;
  gap: 1rem;
}

@media (max-width: 640px) {
  .alertContent {
    left: 50% !important;
    right: auto !important;
    transform: translate(-50%, -50%) !important;
  }
}

.alertDescription {
  color: var(--muted-foreground);
}

.alertFooter {
  margin-top: 32px;
  display: flex;
  gap: 12px;
}

.alertFooter > * {
  flex: 1;
}

.dialogFooter {
  margin-top: 32px;
  display: flex;
  gap: 12px;
}

.dialogFooter > * {
  flex: 1;
}

.collaboratorAvatar {
  position: relative;
  display: inline-block;
}

.collaboratorAvatarImage {
  border-radius: 50%;
  transition: border 150ms ease;
}

.collaboratorAvatarImageNew {
  border: 2px solid var(--success);
}

.collaboratorAvatarImageOriginal {
  border: 2px solid transparent;
}

.removeButton {
  position: absolute;
  top: -4px;
  right: -4px;
  width: 18px;
  height: 18px;
  border-radius: 50%;
  background: var(--destructive);
  color: var(--destructive-foreground);
  border: 2px solid var(--background);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 10px;
  font-weight: 600;
  line-height: 1;
  transition: all 150ms ease;
  opacity: 0;
  pointer-events: none;
}

.removeButton.visible {
  opacity: 1;
  pointer-events: auto;
}

.removeButton.alwaysVisible {
  opacity: 1;
  pointer-events: auto;
}

.collaboratorAvatar:hover .removeButton {
  opacity: 1;
  pointer-events: auto;
}

.removeButton:hover {
  transform: scale(1.1);
  background: oklch(from var(--destructive) calc(l * 0.9) c h);
}

.removeButton:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
  opacity: 1;
  pointer-events: auto;
}

.comboboxWrapper {
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  background: var(--mix-card-50-bg);
  transition: all 150ms ease;
}

.comboboxWrapper:has(:focus-visible) {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.dialogPopup {
  max-width: 410px;
}

.dialogContent {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.sectionLabel {
  font-size: 0.875rem;
  font-weight: 500;
  margin-bottom: 0.5rem;
  display: block;
}

.collaboratorList {
  display: flex;
  gap: 0.5rem;
  align-items: center;
  flex-wrap: wrap;
}

.addCollaboratorSection {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.addCollaboratorLabel {
  font-size: 0.875rem;
  font-weight: 500;
}

.comboboxPopup {
  width: var(--anchor-width);
}

.userItemContainer {
  display: flex;
  align-items: center;
  gap: 1rem;
  flex: 1;
}

.userAvatar {
  width: 1.5rem;
  height: 1.5rem;
}

.userInfo {
  text-align: left;
}

.userName {
  font-size: 0.875rem;
  font-weight: 500;
}

.userEmail {
  font-size: 0.75rem;
  color: var(--muted-foreground);
}

.avatarSize {
  width: 32px;
  height: 32px;
}

import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/brook/ui/avatar/avatar";
import {
  Tooltip,
  TooltipArrow,
  TooltipPopup,
  TooltipPortal,
  TooltipPositioner,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/brook/ui/tooltip/tooltip";

type User = {
  value: string;
  label: string;
  email: string;
  avatar: string;
};

type AvatarGroupProps = {
  users: User[];
  maxDisplay?: number;
};

export function AvatarGroup({ users, maxDisplay = 3 }: AvatarGroupProps) {
  const displayUsers = users.slice(0, maxDisplay);
  const remainingCount = users.length - maxDisplay;

  return (
    <TooltipProvider>
      <div style={{ display: "flex", alignItems: "center" }}>
        {displayUsers.map((user, index) => (
          <Tooltip key={user.value}>
            <TooltipTrigger
              render={
                <div style={{ marginLeft: index > 0 ? "-6px" : "0" }}>
                  <Avatar
                    style={{
                      width: "24px",
                      height: "24px",
                      border: "2px solid var(--card)",
                    }}
                  >
                    <AvatarImage
                      alt={`profile image for ${user.label}`}
                      src={user.avatar}
                    />
                    <AvatarFallback>
                      {user.label
                        ?.split(" ")
                        .map((n: string) => n[0])
                        .join("") || "??"}
                    </AvatarFallback>
                  </Avatar>
                </div>
              }
            />
            <TooltipPortal>
              <TooltipPositioner>
                <TooltipPopup>
                  <TooltipArrow />
                  {user.label}
                </TooltipPopup>
              </TooltipPositioner>
            </TooltipPortal>
          </Tooltip>
        ))}
        {remainingCount > 0 && (
          <div
            style={{
              width: "24px",
              height: "24px",
              borderRadius: "50%",
              background: "var(--muted)",
              color: "var(--muted-foreground)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: "0.625rem",
              fontWeight: 600,
              marginLeft: "-6px",
              border: "2px solid var(--card)",
            }}
          >
            +{remainingCount}
          </div>
        )}
      </div>
    </TooltipProvider>
  );
}

import {
  AlertDialog,
  AlertDialogClose,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/registry/brook/ui/alert-dialog/alert-dialog";
import { Button } from "@/registry/brook/ui/button/button";
import styles from "./card-task.module.css";

type DeleteTaskAlertDialogProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
};

export function DeleteTaskAlertDialog({
  open,
  onOpenChange,
}: DeleteTaskAlertDialogProps) {
  return (
    <AlertDialog onOpenChange={onOpenChange} open={open}>
      <AlertDialogContent className={styles.alertContent}>
        <AlertDialogHeader>
          <AlertDialogTitle>Delete Task</AlertDialogTitle>
          <AlertDialogDescription className={styles.alertDescription}>
            Are you sure you want to delete this task?
          </AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter className={styles.alertFooter}>
          <AlertDialogClose
            render={<Button variant="outline">Cancel</Button>}
          />
          <Button onClick={() => onOpenChange(false)} variant="destructive">
            Delete
          </Button>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
}

import { Calendar, MoreHorizontal, Trash, UserPlus } from "lucide-react";
import { Button } from "@/registry/brook/ui/button/button";
import {
  DropdownMenu,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/registry/brook/ui/dropdown-menu/dropdown-menu";
import styles from "./card-task.module.css";

type TaskCardDropdownMenuProps = {
  onAddCollaborator: () => void;
  onDeleteTask: () => void;
};

export function TaskCardDropdownMenu({
  onAddCollaborator,
  onDeleteTask,
}: TaskCardDropdownMenuProps) {
  return (
    <DropdownMenu>
      <DropdownMenuTrigger
        render={(props) => (
          <Button {...props} size="icon" variant="ghost">
            <MoreHorizontal
              size="16"
              style={{
                color: "oklch(from var(--muted-foreground) l c h / 0.8)",
              }}
            />
          </Button>
        )}
      />
      <DropdownMenuPortal>
        <DropdownMenuPositioner sideOffset={8}>
          <DropdownMenuPopup render={<ul />} style={{ minWidth: "160px" }}>
            <div style={{ height: "4px", width: "100%" }} />
            <DropdownMenuItem
              icon={<UserPlus size="14" />}
              onClick={onAddCollaborator}
              render={<li />}
            >
              Add collaborator
            </DropdownMenuItem>
            <DropdownMenuItem icon={<Calendar size="14" />} render={<li />}>
              Change due date
            </DropdownMenuItem>
            <DropdownMenuSeparator />
            <DropdownMenuItem
              className={styles.destructiveMenuItem}
              icon={<Trash size="14" />}
              onClick={onDeleteTask}
              render={<li />}
            >
              Delete task
            </DropdownMenuItem>
            <div style={{ height: "4px", width: "100%" }} />
          </DropdownMenuPopup>
        </DropdownMenuPositioner>
      </DropdownMenuPortal>
    </DropdownMenu>
  );
}

import { useRef } from "react";
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/registry/brook/ui/avatar/avatar";
import { Button } from "@/registry/brook/ui/button/button";
import {
  Combobox,
  ComboboxEmpty,
  ComboboxInput,
  ComboboxItem,
  ComboboxList,
  ComboboxPopup,
  ComboboxPortal,
  ComboboxPositioner,
  ComboboxTrigger,
} from "@/registry/brook/ui/combobox/combobox";
import {
  Dialog,
  DialogClose,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogOverlay,
  DialogPopup,
  DialogPortal,
  DialogTitle,
} from "@/registry/brook/ui/dialog/dialog";
import {
  Tooltip,
  TooltipArrow,
  TooltipPopup,
  TooltipPortal,
  TooltipPositioner,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/brook/ui/tooltip/tooltip";
import styles from "./card-task.module.css";

type User = {
  value: string;
  label: string;
  email: string;
  avatar: string;
};

type CollaboratorDialogProps = {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  users: User[];
  currentCollaborators: User[];
  selectedCollaborators: User[];
  onSelectedCollaboratorsChange: (collaborators: User[]) => void;
  onConfirm: () => void;
};

export function CollaboratorDialog({
  open,
  onOpenChange,
  users,
  currentCollaborators,
  selectedCollaborators,
  onSelectedCollaboratorsChange,
  onConfirm,
}: CollaboratorDialogProps) {
  const comboboxAnchorRef = useRef<HTMLDivElement>(null);

  const handleRemoveCollaborator = (userValue: string) => {
    onSelectedCollaboratorsChange(
      selectedCollaborators.filter((c) => c.value !== userValue)
    );
  };

  return (
    <Dialog onOpenChange={onOpenChange} open={open}>
      <DialogPortal>
        <DialogOverlay />
        <DialogPopup className={styles.dialogPopup}>
          <DialogHeader>
            <DialogTitle>Add Collaborator</DialogTitle>
            <DialogDescription>
              Invite a team member to collaborate on this task.
            </DialogDescription>
          </DialogHeader>

          <div className={styles.dialogContent}>
            {/* Current Collaborators */}
            <div>
              <div className={styles.sectionLabel}>Current Collaborators</div>
              <div className={styles.collaboratorList}>
                <TooltipProvider>
                  {selectedCollaborators?.map((collaborator, index) => {
                    const isNewlyAdded = !currentCollaborators.some(
                      (c) => c.value === collaborator.value
                    );
                    const lastIndex = selectedCollaborators.length - 1;
                    const isLast = index === lastIndex;
                    const isFirst = index === 0;

                    return (
                      <Tooltip key={collaborator.value}>
                        <TooltipTrigger
                          render={
                            <div className={styles.collaboratorAvatar}>
                              <Avatar
                                className={`${styles.collaboratorAvatarImage} ${styles.avatarSize} ${
                                  isNewlyAdded
                                    ? styles.collaboratorAvatarImageNew
                                    : styles.collaboratorAvatarImageOriginal
                                }`}
                              >
                                <AvatarImage
                                  alt={collaborator.label}
                                  src={collaborator.avatar}
                                />
                                <AvatarFallback>
                                  {collaborator.label
                                    ?.split(" ")
                                    .map((n: string) => n[0])
                                    .join("") || "??"}
                                </AvatarFallback>
                              </Avatar>
                              <button
                                className={`${styles.removeButton} ${
                                  isLast && !isFirst ? styles.alwaysVisible : ""
                                }`}
                                onClick={() =>
                                  handleRemoveCollaborator(collaborator.value)
                                }
                                type="button"
                              >
                                Ã—
                              </button>
                            </div>
                          }
                        />
                        <TooltipPortal>
                          <TooltipPositioner>
                            <TooltipPopup>
                              <TooltipArrow />
                              {collaborator.label}
                            </TooltipPopup>
                          </TooltipPositioner>
                        </TooltipPortal>
                      </Tooltip>
                    );
                  })}
                </TooltipProvider>
              </div>
            </div>

            {/* Add New Collaborator */}
            <div className={styles.addCollaboratorSection}>
              <div className={styles.addCollaboratorLabel}>
                Add New Collaborator
              </div>
              <Combobox
                items={users}
                itemToStringLabel={(item) => item?.label || ""}
                itemToStringValue={(item) => item?.value || ""}
                // @ts-expect-error - Base UI Combobox multiple prop type inference issue
                multiple={true}
                onValueChange={(value) => {
                  if (value && Array.isArray(value)) {
                    onSelectedCollaboratorsChange(value as User[]);
                  }
                }}
                // @ts-expect-error - Base UI Combobox value type with multiple=true
                value={selectedCollaborators}
              >
                <div className={styles.comboboxWrapper} ref={comboboxAnchorRef}>
                  <ComboboxInput placeholder="Search users..." />
                  <ComboboxTrigger />
                </div>

                <ComboboxPortal>
                  <ComboboxPositioner anchor={comboboxAnchorRef}>
                    <ComboboxPopup className={styles.comboboxPopup}>
                      <ComboboxEmpty>No user found.</ComboboxEmpty>
                      <ComboboxList>
                        {(user: User) => (
                          <ComboboxItem
                            indicatorPosition="right"
                            key={user.value}
                            value={user}
                          >
                            <div className={styles.userItemContainer}>
                              <Avatar className={styles.userAvatar}>
                                <AvatarImage
                                  alt={user.label}
                                  src={user.avatar}
                                />
                                <AvatarFallback>
                                  {user.label
                                    .split(" ")
                                    .map((n: string) => n[0])
                                    .join("")}
                                </AvatarFallback>
                              </Avatar>
                              <div className={styles.userInfo}>
                                <div className={styles.userName}>
                                  {user.label}
                                </div>
                                <div className={styles.userEmail}>
                                  {user.email}
                                </div>
                              </div>
                            </div>
                          </ComboboxItem>
                        )}
                      </ComboboxList>
                    </ComboboxPopup>
                  </ComboboxPositioner>
                </ComboboxPortal>
              </Combobox>
            </div>
          </div>

          <DialogFooter className={styles.dialogFooter}>
            <DialogClose render={<Button variant="outline">Cancel</Button>} />
            <Button onClick={onConfirm}>Confirm</Button>
          </DialogFooter>
        </DialogPopup>
      </DialogPortal>
    </Dialog>
  );
}
