"use client";

import { useState, useRef, useMemo, useCallback, useEffect, isValidElement, cloneElement, ReactElement } from "react";
import { cn } from "@/lib/utils";
import styles from "./carousel.module.css";

export interface CarouselItem {
  id: string;
  content: React.ReactNode;
}

interface CarouselProps extends React.HTMLAttributes<HTMLDivElement> {
  items: CarouselItem[];
  showIndicators?: boolean;
  showNavigation?: boolean;
  itemsPerView?: number;
  gap?: number;
  ref?: React.Ref<HTMLDivElement>;
}

const Carousel = ({
  className,
  items,
  showIndicators = false,
  showNavigation = true,
  itemsPerView = 3.2,
  gap = 16,
  ref,
  ...props
}: CarouselProps) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const carouselRef = useRef<HTMLDivElement>(null);
  const viewportRef = useRef<HTMLDivElement>(null);
  const isScrollingRef = useRef(false);

  const totalItems = items.length;

  const itemWidth = useMemo(() => {
    return `calc((100% - ${gap * (itemsPerView - 1)}px) / ${itemsPerView})`;
  }, [itemsPerView, gap]);

  const [scrollLeft, setScrollLeft] = useState(0);
  const [maxScrollLeft, setMaxScrollLeft] = useState(0);

  const canGoNext = scrollLeft < maxScrollLeft;
  const canGoPrev = scrollLeft > 0;

  const goToIndex = useCallback(
    (index: number) => {
      const viewport = viewportRef.current;
      if (!viewport) return;

      const containerWidth = viewport.clientWidth;
      const itemWidth = (containerWidth - gap * (itemsPerView - 1)) / itemsPerView;
      const itemWithGap = itemWidth + gap;

      const previousItemOffset = index > 0 ? itemWidth * 0.2 : 0;
      const targetScroll = Math.max(0, index * itemWithGap - previousItemOffset);

      isScrollingRef.current = true;
      viewport.scrollTo({ left: targetScroll, behavior: "smooth" });

      setTimeout(() => {
        isScrollingRef.current = false;
      }, 300);
    },
    [itemsPerView, gap],
  );

  const nextSlide = useCallback(() => {
    setCurrentIndex((prev) => {
      const newIndex = Math.min(prev + 1, totalItems - Math.ceil(itemsPerView));
      goToIndex(newIndex);
      return newIndex;
    });
  }, [totalItems, itemsPerView, goToIndex]);

  const prevSlide = useCallback(() => {
    setCurrentIndex((prev) => {
      const newIndex = Math.max(prev - 1, 0);
      goToIndex(newIndex);
      return newIndex;
    });
  }, [goToIndex]);

  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      const isActiveInCarousel = carouselRef.current?.contains(document.activeElement);
      const isActiveOnCarouselContainer = document.activeElement === carouselRef.current?.parentElement;

      if (!isActiveInCarousel && !isActiveOnCarouselContainer) return;

      switch (event.key) {
        case "ArrowLeft":
          if (canGoPrev) {
            event.preventDefault();
            prevSlide();
          }
          break;
        case "ArrowRight":
          if (canGoNext) {
            event.preventDefault();
            nextSlide();
          }
          break;
        case "Home":
          event.preventDefault();
          goToIndex(0);
          break;
        case "End":
          event.preventDefault();
          goToIndex(Math.max(0, totalItems - Math.ceil(itemsPerView)));
          break;
      }
    };

    document.addEventListener("keydown", handleKeyDown);
    return () => document.removeEventListener("keydown", handleKeyDown);
  }, [prevSlide, nextSlide, goToIndex, canGoNext, canGoPrev, totalItems, itemsPerView]);

  useEffect(() => {
    const viewport = viewportRef.current;
    if (!viewport) return;

    const updateScrollInfo = () => {
      const newScrollLeft = viewport.scrollLeft;
      const newMaxScrollLeft = viewport.scrollWidth - viewport.clientWidth;

      setScrollLeft(newScrollLeft);
      setMaxScrollLeft(newMaxScrollLeft);

      if (!isScrollingRef.current) {
        const containerWidth = viewport.clientWidth;
        const itemWidth = (containerWidth - gap * (itemsPerView - 1)) / itemsPerView;
        const itemWithGap = itemWidth + gap;

        const adjustedScrollLeft = newScrollLeft + itemWidth * 0.2;
        const newIndex = Math.round(adjustedScrollLeft / itemWithGap);
        setCurrentIndex(Math.max(0, Math.min(newIndex, totalItems - 1)));
      }
    };

    updateScrollInfo();

    viewport.addEventListener("scroll", updateScrollInfo, { passive: true });

    window.addEventListener("resize", updateScrollInfo);

    return () => {
      viewport.removeEventListener("scroll", updateScrollInfo);
      window.removeEventListener("resize", updateScrollInfo);
    };
  }, [itemsPerView, totalItems, gap]);

  return (
    <div
      ref={ref}
      className={cn(styles.carousel, className)}
      role="region"
      aria-roledescription="carousel"
      aria-label="Interactive carousel"
      tabIndex={0}
      {...props}
    >
      <div ref={viewportRef} className={styles.viewport} aria-live="polite" aria-atomic="false">
        <div
          ref={carouselRef}
          id="carousel-slides"
          className={styles.container}
          style={{
            gap: `${gap}px`,
          }}
        >
          {items.map((item, index) => {
            const isVisible = index >= Math.max(0, currentIndex - 1) && index < currentIndex + Math.ceil(itemsPerView);

            return (
              <div
                key={item.id}
                className={styles.slide}
                role="group"
                aria-roledescription="slide"
                aria-label={`${index + 1} of ${items.length}`}
                aria-hidden={!isVisible}
                inert={!isVisible ? true : undefined}
                style={{
                  width: itemWidth,
                  minWidth: itemWidth,
                  visibility: isVisible ? "visible" : "hidden",
                }}
              >
                {item.content ? (
                  isValidElement(item.content) ? (
                    cloneElement(item.content as ReactElement<React.HTMLAttributes<HTMLElement>>, {
                      tabIndex: isVisible ? 0 : -1,
                      "aria-label": `Item ${index + 1} content`,
                      style: {
                        ...((item.content as ReactElement<React.HTMLAttributes<HTMLElement>>)?.props?.style || {}),
                        outline: "none",
                        width: "100%",
                        height: "100%",
                      },
                    })
                  ) : (
                    <div
                      tabIndex={isVisible ? 0 : -1}
                      aria-label={`Item ${index + 1} content`}
                      style={{
                        outline: "none",
                        width: "100%",
                        height: "100%",
                      }}
                    >
                      {item.content}
                    </div>
                  )
                ) : (
                  <div>Invalid carousel item</div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {showNavigation && items.length > 1 && (
        <div className={styles.navContainer}>
          <button
            className={cn(styles.navButton, styles.prevButton)}
            onClick={prevSlide}
            disabled={!canGoPrev}
            aria-label="Scroll to previous items"
            aria-controls="carousel-slides"
            type="button"
          >
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" aria-hidden="true">
              <path d="m15 18-6-6 6-6" />
            </svg>
          </button>
          <button
            className={cn(styles.navButton, styles.nextButton)}
            onClick={nextSlide}
            disabled={!canGoNext}
            aria-label="Scroll to next items"
            aria-controls="carousel-slides"
            type="button"
          >
            <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" aria-hidden="true">
              <path d="m9 18 6-6-6-6" />
            </svg>
          </button>
        </div>
      )}

      {showIndicators && items.length > 1 && (
        <div className={styles.indicators} role="tablist" aria-label="Choose slide to display">
          {items.map((_, index) => (
            <button
              key={index}
              className={cn(styles.indicator, currentIndex === index && styles.indicatorActive)}
              onClick={() => goToIndex(index)}
              role="tab"
              aria-selected={false}
              aria-label={`Scroll to item ${index + 1}`}
              aria-controls="carousel-slides"
              type="button"
            />
          ))}
        </div>
      )}

      <div className={styles.srOnly}>
        <div aria-live="polite" aria-atomic="true">
          Showing items {currentIndex + 1} to {Math.min(items.length, currentIndex + Math.ceil(itemsPerView))} of{" "}
          {items.length}
        </div>
        <p>Use arrow keys or tab/shift+tab to navigate slides.</p>
      </div>
    </div>
  );
};

Carousel.displayName = "Carousel";

export { Carousel };
export type { CarouselProps };

.carousel {
    position: relative;
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    border-radius: 0.5rem;
    overflow: hidden;
}

.carousel:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
}

.viewport {
    position: relative;
    width: 100%;
    overflow-x: auto;
    overflow-y: hidden;

    scrollbar-width: none;
    -ms-overflow-style: none;
    scroll-behavior: smooth;
    scroll-snap-type: none;
}

.viewport::-webkit-scrollbar {
    display: none;
}

.container {
    display: flex;
    align-items: stretch;
    padding-left: 0;
}

.slide {
    flex-shrink: 0;
    position: relative;
}

.slide[aria-hidden="true"] {
    pointer-events: none;
}

.slide:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: -2px;
}

.slide > *:focus-visible {
    outline: 2px solid var(--ring) !important;
    outline-offset: 2px !important;
}

.slide > *:focus {
    outline: 2px solid var(--ring) !important;
    outline-offset: 2px !important;
}

.navContainer {
    display: flex;
    justify-content: flex-end;
    gap: 0.5rem;
    margin-top: 1.5rem;
    padding-right: 0;
}

.navButton {
    position: relative;
    width: 2.5rem;
    height: 2.5rem;
    border-radius: 50%;
    border: none;
    background: var(--card);
    color: var(--foreground);
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: var(--shadow-md);
    transition: all 0.2s ease;
    opacity: 0.9;
    border: 1px solid var(--border);
}

.navButton:hover {
    opacity: 1;
    background: var(--muted);
    transform: scale(1.05);
}

.navButton:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
}

.navButton:active {
    transform: scale(0.95);
}

.navButton:disabled {
    opacity: 0.3;
    cursor: default;
    background: var(--muted);
    color: var(--muted-foreground);
    pointer-events: none;
}

.navButton:disabled:hover {
    opacity: 0.3;
    transform: none;
    background: var(--muted);
}

.navButton svg {
    width: 1rem;
    height: 1rem;
}

.indicators {
    position: absolute;
    bottom: 1rem;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 0.5rem;
    z-index: 10;
}

.indicator {
    width: 0.75rem;
    height: 0.75rem;
    border-radius: 50%;
    border: none;
    background: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    transition: all 0.2s ease;
    position: relative;
}

.indicator:hover {
    background: rgba(255, 255, 255, 0.7);
    transform: scale(1.1);
}

.indicator:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
}

.indicatorActive {
    background: var(--primary);
    transform: scale(1.2);
}

.indicatorActive:hover {
    background: var(--primary);
}

.srOnly {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
}

@media (max-width: 640px) {
    .carousel {
        max-width: 100%;
        border-radius: 0;
    }

    .viewport {
        height: 250px;
    }

    .navContainer {
        margin-top: 1rem;
    }

    .navButton {
        width: 2rem;
        height: 2rem;
    }

    .navButton svg {
        width: 0.875rem;
        height: 0.875rem;
    }

    .indicators {
        bottom: 0.5rem;
    }

    .indicator {
        width: 0.625rem;
        height: 0.625rem;
    }
}

[data-theme="dark"] .indicator {
    background: rgba(255, 255, 255, 0.3);
}

[data-theme="dark"] .indicator:hover {
    background: rgba(255, 255, 255, 0.5);
}

@media (prefers-reduced-motion: reduce) {
    .container {
        transition: none;
    }

    .navButton,
    .indicator {
        transition: none;
    }
}
