"use client";

import { Checkbox } from "@base-ui/react/checkbox";
import { cn } from "@/lib/utils";
import styles from "./checkbox.module.css";

function CheckboxRoot({ className, ...props }: Checkbox.Root.Props) {
  return (
    <Checkbox.Root className={cn("hit-area-extend", styles.root, className)} data-slot="checkbox-root" {...props} />
  );
}

function CheckboxIndicator({ className, ...props }: Checkbox.Indicator.Props) {
  return <Checkbox.Indicator className={cn(styles.indicator, className)} data-slot="checkbox-indicator" {...props} />;
}

export { CheckboxRoot as Checkbox, CheckboxIndicator };

.root {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 1.125rem;
  height: 1.125rem;
  border-radius: 0.3125rem;
  border: none;
  background-color: transparent;
  cursor: pointer;
  flex-shrink: 0;
  position: relative;

  &[data-unchecked] {
    border: 1px solid oklch(from var(--border) l c h / 0.8);
    background-color: transparent;
  }

  &[data-checked],
  &[data-indeterminate] {
    border: 1px solid var(--border);
    background-color: var(--primary);
  }

  &:focus-visible {
    outline: 1px solid var(--ring);
    outline-offset: 1px;
  }
}

.indicator {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  color: var(--primary-foreground);

  & > svg {
    width: 16px;
    height: 16px;
  }

  &[data-unchecked]:not([data-indeterminate]) {
    display: none;
  }
}
