"use client";

import { Checkbox } from "@base-ui-components/react/checkbox";
import { cn } from "@/lib/utils";
import styles from "./checkbox.module.css";

const CheckboxRoot = ({ className, ...props }: React.ComponentProps<typeof Checkbox.Root>) => (
  <Checkbox.Root className={cn(styles.root, className)} {...props} />
);

const CheckboxIndicator = ({ className, ...props }: React.ComponentProps<typeof Checkbox.Indicator>) => (
  <Checkbox.Indicator className={cn(styles.indicator, className)} {...props} />
);

export { CheckboxRoot as Checkbox, CheckboxIndicator };

.root {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 14px;
    height: 14px;
    border-radius: 0.25rem;
    border: none;
    background-color: transparent;
    cursor: pointer;
    transition: all 150ms ease-out;
    flex-shrink: 0;
    box-shadow: 0 0 0 1px var(--border);
}

.root:hover {
    border-color: var(--ring);
}

.root[data-focused] {
    outline: none;
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
}

.root[data-checked] {
    background-color: var(--primary);
    border-color: var(--primary);
}

.root[data-checked]:hover {
    background-color: var(--primary);
    border-color: var(--primary);
}

.root[data-disabled] {
    pointer-events: none;
    opacity: 0.5;
}

.indicator {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    height: 100%;
    color: var(--primary-foreground);
    transform: scale(0);
    transition: transform 150ms ease-out;
}

.root[data-checked] .indicator {
    transform: scale(1);
}
