"use client";

import { Collapsible } from "@base-ui-components/react/collapsible";
import { cn } from "@/lib/utils";
import styles from "./collapsible.module.css";

function CollapsibleRoot({
  className,
  ...props
}: React.ComponentProps<typeof Collapsible.Root>) {
  return <Collapsible.Root className={cn(styles.root, className)} {...props} />;
}

function CollapsibleTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof Collapsible.Trigger>) {
  return (
    <Collapsible.Trigger className={cn(styles.trigger, className)} {...props}>
      {children}
      <svg
        className={styles.chevron}
        fill="none"
        height="16"
        viewBox="0 0 24 24"
        width="16"
      >
        <path
          d="m7 15 5 5 5-5"
          stroke="currentColor"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
        />
        <path
          d="m7 9 5-5 5 5"
          stroke="currentColor"
          strokeLinecap="round"
          strokeLinejoin="round"
          strokeWidth="2"
        />
      </svg>
    </Collapsible.Trigger>
  );
}

function CollapsiblePanel({
  className,
  ...props
}: React.ComponentProps<typeof Collapsible.Panel>) {
  return (
    <Collapsible.Panel className={cn(styles.panel, className)} {...props} />
  );
}

export { CollapsibleRoot as Collapsible, CollapsibleTrigger, CollapsiblePanel };

.root {
  display: flex;
  width: 100%;
  max-width: 14rem;
  min-height: 9rem;
  flex-direction: column;
  justify-content: center;
  color: var(--foreground);
}

.trigger {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin: 0;
  border: 0;
  outline: 0;
  padding: 0.25rem 0.5rem;
  border-radius: 0.25rem;
  background-color: var(--mix-card-15-bg);
  color: var(--foreground);
  font-family: inherit;
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 150ms ease-out;
  border: 1px solid var(--border);
}

.trigger:hover {
  background-color: var(--mix-card-50-bg);
}

.trigger:active {
  background-color: var(--mix-card-15-bg);
}

.trigger:focus-visible {
  outline: 2px solid var(--ring);
}

.trigger[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.trigger[data-disabled]:hover {
  background-color: var(--muted);
}

.chevron {
  width: 0.75rem;
  height: 0.75rem;
  flex-shrink: 0;
  transition: transform 150ms ease-out;
  color: var(--muted-foreground);
}

.trigger[data-panel-open] .chevron {
  transform: rotate(90deg);
}

.panel {
  display: flex;
  height: var(--collapsible-panel-height);
  flex-direction: column;
  justify-content: end;
  overflow: hidden;
  font-size: 0.875rem;
  line-height: 1.25rem;
  transition: all 150ms ease-out;
  width: 100%;
  box-sizing: border-box;
}

.panel[data-starting-style],
.panel[data-ending-style] {
  height: 0;
}

.panel[data-closed] {
  height: 0;
  border: none;
}

.panel[data-panel-open] {
  animation: slideDown 150ms ease-out;
}

.panel[data-closed] {
  animation: slideUp 150ms ease-out;
}

@keyframes slideDown {
  from {
    height: 0;
  }
  to {
    height: var(--collapsible-panel-height);
  }
}

@keyframes slideUp {
  from {
    height: var(--collapsible-panel-height);
  }
  to {
    height: 0;
  }
}
