"use client";

import { Combobox } from "@base-ui-components/react/combobox";
import { Check, ChevronsUpDown, X } from "lucide-react";
import type React from "react";
import { cn } from "@/lib/utils";
import styles from "./combobox.module.css";

function ComboboxRoot<ItemValue>({
  ...props
}: React.ComponentProps<typeof Combobox.Root<ItemValue>>) {
  return <Combobox.Root<ItemValue> {...props} />;
}

function ComboboxTrigger({
  className,
  children,
  ...props
}: Combobox.Trigger.Props) {
  return (
    <Combobox.Trigger className={cn(styles.trigger, className)} {...props}>
      {children}
      <ChevronsUpDown className={styles.icon} size={16} />
    </Combobox.Trigger>
  );
}

function ComboboxInput({
  className,
  ...props
}: Combobox.Input.Props) {
  return <Combobox.Input className={cn(styles.input, className)} {...props} />;
}

function ComboboxClear({
  className,
  children,
  ...props
}: Combobox.Clear.Props) {
  return (
    <Combobox.Clear className={cn(styles.clear, className)} {...props}>
      {children || <X size={16} />}
    </Combobox.Clear>
  );
}

const ComboboxPortal = Combobox.Portal;

function ComboboxPositioner({
  className,
  ...props
}: Combobox.Positioner.Props) {
  return (
    <Combobox.Positioner
      data-slot="combobox-positioner"
      className={cn(styles.positioner, className)}
      sideOffset={4}
      {...props}
    />
  );
}

function ComboboxPopup({
  className,
  ...props
}: Combobox.Popup.Props) {
  return <Combobox.Popup className={cn(styles.popup, className)} {...props} />;
}

function ComboboxList({
  className,
  ...props
}: Combobox.List.Props) {
  return <Combobox.List className={cn(styles.list, className)} {...props} />;
}

function ComboboxEmpty({
  className,
  children,
  ...props
}: Combobox.Empty.Props) {
  return (
    <Combobox.Empty className={cn(styles.empty, className)} {...props}>
      {children || "No items found"}
    </Combobox.Empty>
  );
}

function ComboboxItem({
  className,
  children,
  indicatorPosition = "left",
  ...props
}: Combobox.Item.Props & {
  indicatorPosition?: "left" | "right";
}) {
  return (
    <Combobox.Item className={cn(styles.item, className)} {...props}>
      {indicatorPosition === "left" && (
        <Combobox.ItemIndicator className={styles.itemIndicator}>
          <Check size={16} />
        </Combobox.ItemIndicator>
      )}
      {children}
      {indicatorPosition === "right" && (
        <Combobox.ItemIndicator className={styles.itemIndicator}>
          <Check size={16} />
        </Combobox.ItemIndicator>
      )}
    </Combobox.Item>
  );
}

function ComboboxItemIndicator({
  className,
  children,
  ...props
}: Combobox.ItemIndicator.Props) {
  return (
    <Combobox.ItemIndicator
      data-slot="combobox-itemindicator"
      className={cn(styles.itemIndicator, className)}
      {...props}
    >
      {children || <Check size={16} />}
    </Combobox.ItemIndicator>
  );
}

function ComboboxGroup({
  className,
  ...props
}: Combobox.Group.Props) {
  return <Combobox.Group className={cn(styles.group, className)} {...props} />;
}

function ComboboxGroupLabel({
  className,
  ...props
}: Combobox.GroupLabel.Props) {
  return (
    <Combobox.GroupLabel
      data-slot="combobox-grouplabel"
      className={cn(styles.groupLabel, className)}
      {...props}
    />
  );
}

function ComboboxArrow({
  className,
  ...props
}: Combobox.Arrow.Props) {
  return <Combobox.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function ComboboxNoItems({
  className,
  children,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div className={cn(styles.noItems, className)} {...props}>
      {children || "No items found"}
    </div>
  );
}

export {
  ComboboxRoot as Combobox,
  ComboboxArrow,
  ComboboxClear,
  ComboboxEmpty,
  ComboboxGroup,
  ComboboxGroupLabel,
  ComboboxInput,
  ComboboxItem,
  ComboboxItemIndicator,
  ComboboxList,
  ComboboxNoItems,
  ComboboxPopup,
  ComboboxPortal,
  ComboboxPositioner,
  ComboboxTrigger,
};

.wrapper {
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  background: var(--mix-card-75-bg);
  transition: all 150ms ease;
}

.wrapper:focus-within {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.trigger {
  box-sizing: border-box;
  border: none;
  background: transparent;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 0.5rem;
  cursor: pointer;
}

.trigger:focus-visible {
  outline: none;
}

.trigger[data-disabled] {
  opacity: 0.5;
  cursor: not-allowed;
}

.input {
  flex: 1;
  background: transparent;
  border: none;
  outline: none;
  font-size: 0.875rem;
  color: var(--foreground);
  width: 100%;
  padding: 0.5rem 0.75rem;

  height: 2.5rem;
}

.input::placeholder {
  color: var(--muted-foreground);
}

.input:focus {
  outline: none;
}

.clear {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  color: var(--muted-foreground);
  transition: all 150ms ease;
}

.clear:hover {
  color: var(--foreground);
  background: var(--accent);
}

.icon {
  margin-left: 0.5rem;
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  opacity: 0.5;
}

.positioner {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 150;
  width: var(--anchor-width);
  margin-top: 0.25rem;
}

.popup {
  max-height: 20rem;
  overflow: auto;
  background: var(--mix-card-75-bg);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  box-shadow:
    0 10px 15px -3px rgba(0, 0, 0, 0.1),
    0 4px 6px -2px rgba(0, 0, 0, 0.05);
  outline: none;
  animation: slideDown 150ms ease-out;
  width: 100%;
  padding: 0.25rem;
}

.popup[data-starting-style],
.popup[data-ending-style] {
  opacity: 0;
  transform: translateY(-0.5rem);
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-0.5rem);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.item {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  cursor: pointer;
  user-select: none;
  outline: 0;
}

.list:not(:has(.item[data-highlighted])) .item[data-selected] {
  z-index: 0;
  position: relative;
}

.list:not(:has(.item[data-highlighted])) .item[data-selected]::before {
  content: "";
  z-index: -1;
  position: absolute;
  inset: 0;
  border-radius: calc(var(--radius) - 2px);
  background: var(--mix-accent-33-trans);
}

.item[data-highlighted] {
  z-index: 1;
  position: relative;
}

.item[data-highlighted]::before {
  content: "";
  z-index: -1;
  position: absolute;
  inset: 0;
  border-radius: calc(var(--radius) - 2px);
  background: var(--mix-accent-33-trans);
}

.item[data-disabled] {
  opacity: 0.5;
  cursor: not-allowed;
}

.itemText {
  flex: 1;
}

.itemIndicator {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1rem;
  width: 1rem;
  opacity: 0;
  transition: opacity 150ms ease;
}

.item[data-selected] .itemIndicator {
  opacity: 1;
}

.group {
  padding: 0.25rem 0;
}

.groupLabel {
  padding: 0.5rem 0.75rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
}

.arrow {
  position: absolute;
  top: -0.25rem;
  left: 1rem;
  width: 0.5rem;
  height: 0.5rem;
  background: var(--card);
  border-top: 1px solid var(--border);
  border-left: 1px solid var(--border);
  transform: rotate(45deg);
}

.noItems {
  padding: 2rem 1rem;
  text-align: center;
  font-size: 0.875rem;
  color: var(--muted-foreground);
}

@media (max-width: 640px) {
  .input {
    height: 2.75rem;
    font-size: 0.9375rem;
    padding: 0.5rem 1rem;
  }

  .input::placeholder {
    font-size: 0.9375rem;
  }

  .popup {
    max-width: calc(100vw - 2rem);
    padding: 0.25rem;
  }

  .item {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
  }

  .noItems {
    padding: 1.5rem 1rem;
    font-size: 0.9375rem;
  }
}
