"use client";

import { Combobox } from "@base-ui-components/react/combobox";
import { Check, ChevronsUpDown, X } from "lucide-react";
import type React from "react";
import { cn } from "@/lib/utils";
import { Input } from "@/registry/brook/ui/input/input";
import styles from "./combobox.module.css";

function ComboboxRoot<
  ItemValue,
  Multiple extends boolean | undefined = undefined,
>(props: React.ComponentProps<typeof Combobox.Root<ItemValue, Multiple>>) {
  return <Combobox.Root<ItemValue, Multiple> {...props} />;
}

function ComboboxTrigger({
  className,
  children,
  ...props
}: Combobox.Trigger.Props) {
  return (
    <Combobox.Trigger className={cn(styles.trigger, className)} {...props}>
      {children}
      <ChevronsUpDown className={styles.icon} size={16} />
    </Combobox.Trigger>
  );
}

function ComboboxInput({ className, ...props }: Combobox.Input.Props) {
  return <Combobox.Input className={className} render={<Input />} {...props} />;
}

function ComboboxClear({
  className,
  children,
  ...props
}: Combobox.Clear.Props) {
  return (
    <Combobox.Clear className={cn(styles.clear, className)} {...props}>
      {children || <X size={16} />}
    </Combobox.Clear>
  );
}

const ComboboxPortal = Combobox.Portal;

function ComboboxPositioner({
  className,
  ...props
}: Combobox.Positioner.Props) {
  return (
    <Combobox.Positioner
      className={cn(styles.positioner, className)}
      data-slot="combobox-positioner"
      sideOffset={4}
      {...props}
    />
  );
}

function ComboboxPopup({
  className,
  children,
  ...props
}: Combobox.Popup.Props) {
  return (
    <Combobox.Popup className={cn(styles.popup, className)} {...props}>
      <div style={{ height: "4px", width: "100%", flexShrink: 0 }} />
      {children}
      <div style={{ height: "4px", width: "100%", flexShrink: 0 }} />
    </Combobox.Popup>
  );
}

function ComboboxList({ className, ...props }: Combobox.List.Props) {
  return (
    <Combobox.List
      className={cn(styles.list, className)}
      data-slot="combobox-list"
      {...props}
    />
  );
}

function ComboboxEmpty({
  className,
  children,
  ...props
}: Combobox.Empty.Props) {
  return (
    <Combobox.Empty className={cn(styles.empty, className)} {...props}>
      {children || "No items found"}
    </Combobox.Empty>
  );
}

function ComboboxItem({
  className,
  children,
  indicatorPosition = "left",
  ...props
}: Combobox.Item.Props & {
  indicatorPosition?: "left" | "right";
}) {
  return (
    <Combobox.Item className={cn(styles.item, className)} {...props}>
      {indicatorPosition === "left" && (
        <Combobox.ItemIndicator className={styles.itemIndicator}>
          <Check size={16} />
        </Combobox.ItemIndicator>
      )}
      {children}
      {indicatorPosition === "right" && (
        <Combobox.ItemIndicator className={styles.itemIndicator}>
          <Check size={16} />
        </Combobox.ItemIndicator>
      )}
    </Combobox.Item>
  );
}

function ComboboxItemIndicator({
  className,
  children,
  ...props
}: Combobox.ItemIndicator.Props) {
  return (
    <Combobox.ItemIndicator
      className={cn(styles.itemIndicator, className)}
      data-slot="combobox-itemindicator"
      {...props}
    >
      {children || <Check size={16} />}
    </Combobox.ItemIndicator>
  );
}

function ComboboxGroup({ className, ...props }: Combobox.Group.Props) {
  return <Combobox.Group className={cn(styles.group, className)} {...props} />;
}

function ComboboxGroupLabel({
  className,
  ...props
}: Combobox.GroupLabel.Props) {
  return (
    <Combobox.GroupLabel
      className={cn(styles.groupLabel, className)}
      data-slot="combobox-grouplabel"
      {...props}
    />
  );
}

function ComboboxArrow({ className, ...props }: Combobox.Arrow.Props) {
  return <Combobox.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function ComboboxNoItems({
  className,
  children,
  ...props
}: React.ComponentProps<"div">) {
  return (
    <div className={cn(styles.noItems, className)} {...props}>
      {children || "No items found"}
    </div>
  );
}

function ComboboxChips({
  className,
  ref,
  ...props
}: Combobox.Chips.Props & { ref?: React.Ref<HTMLDivElement> }) {
  return <Combobox.Chips className={cn(styles.chips, className)} ref={ref} {...props} />;
}

function ComboboxChip({
  className,
  ...props
}: Combobox.Chip.Props) {
  return <Combobox.Chip className={cn(styles.chip, className)} {...props} />;
}

function ComboboxChipRemove({
  className,
  children,
  ...props
}: Combobox.ChipRemove.Props) {
  return (
    <Combobox.ChipRemove className={cn(styles.chipRemove, className)} {...props}>
      {children || <X size={14} />}
    </Combobox.ChipRemove>
  );
}

function ComboboxChipsInput({ className, ...props }: Combobox.Input.Props) {
  return <Combobox.Input className={cn(styles.chipsInput, className)} {...props} />;
}

const ComboboxValue = Combobox.Value;

export {
  ComboboxRoot as Combobox,
  ComboboxArrow,
  ComboboxChip,
  ComboboxChipRemove,
  ComboboxChips,
  ComboboxChipsInput,
  ComboboxClear,
  ComboboxEmpty,
  ComboboxGroup,
  ComboboxGroupLabel,
  ComboboxInput,
  ComboboxItem,
  ComboboxItemIndicator,
  ComboboxList,
  ComboboxNoItems,
  ComboboxPopup,
  ComboboxPortal,
  ComboboxPositioner,
  ComboboxTrigger,
  ComboboxValue,
};

.trigger {
  position: absolute;
  top: 50%;
  right: 0.5rem;
  transform: translateY(-50%);
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0.25rem;
  border: none;
  background: transparent;
  cursor: pointer;
  opacity: 0.5;

  &:hover {
    opacity: 1;
  }

  &:focus-visible {
    outline: none;
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
  }
}

.clear {
  position: absolute;
  top: 50%;
  right: 0.5rem;
  transform: translateY(-50%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  color: var(--muted-foreground);
  transition: all 150ms ease;

  &:hover {
    color: var(--foreground);
    background: var(--accent);
  }
}

.icon {
  margin-left: 0.5rem;
  height: 1rem;
  width: 1rem;
  flex-shrink: 0;
  opacity: 0.5;
}

.positioner {
  position: absolute;
  top: 100%;
  left: 0;
  z-index: 150;
  width: var(--anchor-width);
  margin-top: 0.25rem;
}

.popup {
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  max-height: min(var(--available-height), 20rem);
  width: var(--anchor-width);
  max-width: var(--available-width);
  overflow-y: auto;
  scroll-padding-block: 0.5rem;
  overscroll-behavior: contain;
  background: var(--popover);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  border-radius: var(--radius);
  box-shadow: var(--shadow-border-stack);
  outline: none;
  transform-origin: var(--transform-origin);
  transition:
    transform 250ms var(--ease-out-expo),
    opacity 250ms var(--ease-out-expo);

  &[data-starting-style] {
    opacity: 0;
    transform: scale(0.95);
  }

  &[data-ending-style] {
    transition: none;
  }

  @media (max-width: 640px) {
    max-width: calc(100vw - 2rem);
  }
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-0.5rem);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.item {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  margin-inline: 4px;
  min-height: 32px;
  font-size: 12px;
  font-weight: 400;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  user-select: none;
  background-color: transparent;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0;
    border-radius: calc(var(--radius) - 4px);
    background-color: transparent;
    z-index: -1;
  }

  &[data-highlighted]::before,
  &:hover::before,
  &:focus::before {
    background-color: var(--accent);
  }

  &:focus,
  &:focus-visible {
    outline: none;
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;

    &:hover::before {
      background: transparent;
    }
  }

  &[data-selected] {
    .itemIndicator {
      opacity: 1;
      margin: 4px;
    }
  }

  @media (max-width: 640px) {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
    min-height: 2.75rem;
  }
}

.list {
  display: flex;
  flex-direction: column;
  gap: 1px;
  outline: none;
}

.list:not(:has(.item[data-highlighted])) .item[data-selected]::before {
  background: var(--mix-accent-33-trans);
}

.itemText {
  flex: 1;
}

.itemIndicator {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1rem;
  width: 1rem;
  opacity: 0;
  transition: opacity 150ms ease;
}

.group {
  padding: 0.25rem 0;
}

.groupLabel {
  padding: 0.5rem 0.75rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
}

.arrow {
  position: absolute;
  top: -0.25rem;
  left: 1rem;
  width: 0.5rem;
  height: 0.5rem;
  background: var(--card);
  border-top: 1px solid var(--border);
  border-left: 1px solid var(--border);
  transform: rotate(45deg);
}

.noItems,
.empty:not(:empty) {
  padding: 2rem 1rem;
  text-align: center;
  font-size: 0.875rem;
  color: var(--muted-foreground);

  @media (max-width: 640px) {
    padding: 1.5rem 1rem;
    font-size: 0.9375rem;
  }
}

.chips {
  box-sizing: border-box;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  gap: 0.25rem;
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 0.25rem 0.375rem;
  width: 100%;
  background-color: var(--mix-card-50-bg);

  &:focus-within {
    border-color: var(--ring);
    box-shadow: 0 0 0 2px oklch(from var(--ring) l c h / 0.2);
  }
}

.chip {
  display: flex;
  align-items: center;
  background-color: var(--muted);
  color: var(--foreground);
  border-radius: calc(var(--radius) - 2px);
  font-size: 0.875rem;
  padding: 0.125rem 0.125rem 0.125rem 0.5rem;
  overflow: hidden;
  gap: 0.25rem;
  outline: 0;
  cursor: default;

  &:focus-within,
  &[data-highlighted] {
    background-color: var(--primary);
    color: var(--primary-foreground);
  }

  @media (max-width: 640px) {
    font-size: 0.9375rem;
  }
}

.chipRemove {
  border: none;
  background: none;
  padding: 0.25rem;
  display: flex;
  align-items: center;
  justify-content: center;
  color: inherit;
  border-radius: calc(var(--radius) - 4px);
  cursor: pointer;

  &:hover {
    background-color: oklch(from var(--foreground) l c h / 0.1);
  }
}

.chipsInput {
  flex: 1;
  box-sizing: border-box;
  padding-left: 0.5rem;
  margin: 0;
  border: none;
  height: 2rem;
  border-radius: var(--radius);
  font-family: inherit;
  font-size: 0.875rem;
  background-color: transparent;
  color: var(--foreground);
  min-width: 3rem;

  &::placeholder {
    color: var(--muted-foreground);
  }

  &:focus {
    outline: none;
  }

  @media (max-width: 640px) {
    font-size: 0.9375rem;
  }
}
