"use client";

import { Autocomplete } from "@base-ui/react/autocomplete";
import { Dialog } from "@base-ui/react/dialog";
import type React from "react";
import { cn } from "@/lib/utils";
import styles from "./command.module.css";

function CommandDialog({ ...props }: Dialog.Root.Props) {
  return <Dialog.Root {...props} />;
}

function CommandDialogTrigger({ ...props }: Dialog.Trigger.Props) {
  return <Dialog.Trigger data-slot="command-dialog-trigger" {...props} />;
}

const CommandDialogPortal = Dialog.Portal;

function CommandDialogBackdrop({ className, ...props }: Dialog.Backdrop.Props) {
  return <Dialog.Backdrop className={cn(styles.backdrop, className)} data-slot="command-dialog-backdrop" {...props} />;
}

function CommandDialogPopup({
  className,
  children,
  ...props
}: Dialog.Popup.Props & { children?: React.ReactNode }) {
  return (
    <CommandDialogPortal>
      <CommandDialogBackdrop />
      <Dialog.Popup className={cn(styles.dialogPopup, className)} data-slot="command-dialog-popup" {...props}>
        {children}
      </Dialog.Popup>
    </CommandDialogPortal>
  );
}

function Command({
  autoHighlight = "always",
  keepHighlight = true,
  className,
  ...props
}: React.ComponentProps<typeof Autocomplete.Root> & { className?: string }) {
  return (
    <div className={cn(styles.root, className)} data-slot="command">
      <Autocomplete.Root
        autoHighlight={autoHighlight}
        filter={null}
        inline
        keepHighlight={keepHighlight}
        open
        {...props}
      />
    </div>
  );
}

function CommandInput({
  className,
  placeholder = "Search...",
  ...props
}: React.ComponentProps<typeof Autocomplete.Input>) {
  return (
    <div className={styles.inputWrapper} data-slot="command-input-wrapper">
      <Autocomplete.Input
        autoFocus
        className={cn(styles.input, className)}
        data-slot="command-input"
        placeholder={placeholder}
        {...props}
      />
    </div>
  );
}

function CommandList({ className, ...props }: React.ComponentProps<typeof Autocomplete.List>) {
  return <Autocomplete.List className={cn(styles.list, className)} data-slot="command-list" {...props} />;
}

function CommandEmpty({ className, children, ...props }: React.ComponentProps<typeof Autocomplete.Empty>) {
  return (
    <Autocomplete.Empty className={cn(styles.empty, className)} data-slot="command-empty" {...props}>
      {children || "No results found."}
    </Autocomplete.Empty>
  );
}

function CommandGroup({ className, ...props }: React.ComponentProps<typeof Autocomplete.Group>) {
  return <Autocomplete.Group className={cn(styles.group, className)} data-slot="command-group" {...props} />;
}

function CommandGroupLabel({ className, ...props }: React.ComponentProps<typeof Autocomplete.GroupLabel>) {
  return (
    <Autocomplete.GroupLabel className={cn(styles.groupLabel, className)} data-slot="command-group-label" {...props} />
  );
}

function CommandCollection({ ...props }: React.ComponentProps<typeof Autocomplete.Collection>) {
  return <Autocomplete.Collection data-slot="command-collection" {...props} />;
}

function CommandItem({ className, ...props }: React.ComponentProps<typeof Autocomplete.Item>) {
  return <Autocomplete.Item className={cn(styles.item, className)} data-slot="command-item" {...props} />;
}

function CommandSeparator({ className, ...props }: React.ComponentProps<typeof Autocomplete.Separator>) {
  return (
    <Autocomplete.Separator className={cn(styles.separator, className)} data-slot="command-separator" {...props} />
  );
}

function CommandFooter({ className, ...props }: React.ComponentProps<"div">) {
  return <div className={cn(styles.footer, className)} data-slot="command-footer" {...props} />;
}

function CommandShortcut({ className, ...props }: React.ComponentProps<"kbd">) {
  return <kbd className={cn(styles.shortcut, className)} data-slot="command-shortcut" {...props} />;
}

export {
  Command,
  CommandCollection,
  CommandDialog,
  CommandDialogBackdrop,
  CommandDialogPopup,
  CommandDialogPortal,
  CommandDialogTrigger,
  CommandEmpty,
  CommandFooter,
  CommandGroup,
  CommandGroupLabel,
  CommandInput,
  CommandItem,
  CommandList,
  CommandSeparator,
  CommandShortcut,
};

.root {
  display: flex;
  flex-direction: column;
  width: 100%;
  overflow: hidden;
  color: var(--foreground);
}

.backdrop {
  position: fixed;
  inset: 0;
  z-index: var(--dialog-z);
  background-color: var(--dialog-overlay);
  transition: opacity 150ms;

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
  }
}

.dialogPopup {
  position: fixed;
  left: 50%;
  top: 50%;
  z-index: 101;
  transform: translate(-50%, -50%);
  max-width: 560px;
  width: calc(100% - 2rem);
  border-radius: var(--radius);
  border: none;
  box-shadow:
    0 0 0 0.5px oklch(from var(--border) l c h / 0.5),
    var(--shadow-lg);
  background-color: var(--background);
  transition: all 150ms;

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.95);
  }

  @media (max-width: 640px) {
    width: calc(100% - 2rem);
    max-width: none;
  }
}

.inputWrapper {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem;
  box-shadow: inset 0 -0.5px 0 0 oklch(from var(--border) l c h / 0.5);
}

.input {
  display: flex;
  height: 1.5rem;
  width: 100%;
  border: none;
  background-color: transparent;
  font-size: 0.875rem;
  outline: none;
  color: var(--foreground);
  caret-color: var(--primary);

  &::placeholder {
    color: var(--muted-foreground);

    @media (max-width: 640px) {
      font-size: 0.9375rem;
    }
  }

  &:focus {
    outline: none;
  }

  @media (max-width: 640px) {
    font-size: 0.9375rem;
  }
}

.list {
  max-height: 300px;
  overflow: auto;
  padding: 0.25rem;
  scrollbar-width: thin;
  scrollbar-color: oklch(from var(--border) l c h / 0.5) transparent;
  outline: none;

  &::-webkit-scrollbar {
    width: 6px;
  }

  &::-webkit-scrollbar-track {
    background: transparent;
  }

  &::-webkit-scrollbar-thumb {
    background: oklch(from var(--border) l c h / 0.5);
    border-radius: 3px;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: oklch(from var(--border) l c h / 0.7);
  }

  @media (max-width: 640px) {
    max-height: 250px;
    padding: 0.375rem;
  }
}

.item {
  display: flex;
  cursor: pointer;
  user-select: none;
  align-items: center;
  min-height: 2.5rem;
  padding: 0.625rem 0.375rem;
  font-size: 0.875rem;
  font-weight: 400;
  line-height: normal;
  gap: 0.5rem;
  outline: none;
  border-radius: 0;
  border: 1px solid transparent;

  &:hover,
  &[data-highlighted] {
    background-color: var(--mix-card-50-bg);
  }

  &:focus-visible {
    outline: 2px solid var(--primary);
    outline-offset: 2px;
    z-index: 1;
  }

  &[data-disabled] {
    pointer-events: none;
    opacity: 0.5;
  }

  svg {
    height: 1rem;
    width: 1rem;
    flex-shrink: 0;
    color: var(--muted-foreground);

    @media (max-width: 640px) {
      height: 1.125rem;
      width: 1.125rem;
    }
  }

  @media (max-width: 640px) {
    min-height: 2.75rem;
    padding: 0.75rem 0.625rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
  }
}

.group {
  overflow: hidden;
  padding: 0.25rem 0;
  color: var(--foreground);
}

.groupLabel {
  padding: 0.5rem 0.5rem 0.25rem;
  font-size: 0.71875rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.025em;

  @media (max-width: 640px) {
    font-size: 0.8125rem;
    padding: 0.5rem 0.625rem 0.25rem;
  }
}

.empty {
  padding: 1.5rem;
  text-align: center;
  color: var(--muted-foreground);
  font-size: 0.875rem;

  @media (max-width: 640px) {
    padding: 1.75rem 1rem;
    font-size: 0.9375rem;
  }
}

.separator {
  height: 0.5px;
  width: 100%;
  background-color: oklch(from var(--border) l c h / 0.4);
  margin: 0.25rem 0;
}

.footer {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem 1rem;
  border-top: 0.5px solid oklch(from var(--border) l c h / 0.5);
  background-color: oklch(from var(--muted) l c h / 0.2);
  flex-shrink: 0;
}

.shortcut {
  margin-left: auto;
  padding: 0.125rem 0.25rem;
  background-color: var(--background);
  border: 0.5px solid oklch(from var(--border) l c h / 0.5);
  font-size: 0.75rem;
  font-family: monospace;
  color: var(--muted-foreground);
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 0.25rem;

  svg {
    flex-shrink: 0;
  }

  @media (max-width: 640px) {
    font-size: 0.8125rem;
    padding: 0.1875rem 0.375rem;
  }
}
