"use client";

import { Dialog } from "@base-ui-components/react/dialog";
import { Command as CommandPrimitive } from "cmdk";
import { Command } from "lucide-react";
import { Children, cloneElement, isValidElement } from "react";
import { cn } from "@/lib/utils";
import styles from "./command.module.css";

function CommandRoot({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive>) {
  return <CommandPrimitive className={cn(styles.root, className)} {...props} />;
}

function CommandDialog({
  children,
  open,
  onOpenChange,
  ...props
}: Dialog.Root.Props & {
  children?: React.ReactNode;
}) {
  return (
    <Dialog.Root onOpenChange={onOpenChange} open={open} {...props}>
      <Dialog.Portal>
        <Dialog.Backdrop className={cn(styles.dialogOverlay)} />
        <Dialog.Popup className={cn(styles.dialogContent)}>
          {children}
        </Dialog.Popup>
      </Dialog.Portal>
    </Dialog.Root>
  );
}

function CommandInput({
  className,
  ...props
}: React.ComponentPropsWithRef<typeof CommandPrimitive.Input>) {
  return (
    <CommandPrimitive.Input
      className={cn(styles.input, className)}
      data-slot="commandprimitive-input"
      {...props}
    />
  );
}

function CommandList({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.List>) {
  return (
    <CommandPrimitive.List
      className={cn(styles.list, className)}
      data-slot="commandprimitive-list"
      {...props}
    />
  );
}

function CommandItem({
  className,
  icon,
  keyboardShortcut,
  children,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.Item> & {
  icon?: boolean;
  keyboardShortcut?: string;
}) {
  const processedChildren =
    icon && Children.count(children) > 0
      ? Children.map(children, (child, index) => {
          if (index === 0 && isValidElement(child)) {
            const typedChild = child as React.ReactElement<
              React.HTMLAttributes<HTMLElement>
            >;
            return cloneElement(typedChild, {
              className: cn(styles.iconBackground, typedChild.props.className),
            });
          }
          return child;
        })
      : children;

  return (
    <CommandPrimitive.Item className={cn(styles.item, className)} {...props}>
      {processedChildren}
      {keyboardShortcut && (
        <span className={styles.keyboardShortcut}>
          <Command size={11} />
          {keyboardShortcut}
        </span>
      )}
    </CommandPrimitive.Item>
  );
}

function CommandGroup({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.Group>) {
  return (
    <CommandPrimitive.Group
      className={cn(styles.group, className)}
      data-slot="commandprimitive-group"
      {...props}
    />
  );
}

function CommandEmpty({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.Empty>) {
  return (
    <CommandPrimitive.Empty
      className={cn(styles.empty, className)}
      data-slot="commandprimitive-empty"
      {...props}
    />
  );
}

function CommandSeparator({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.Separator>) {
  return (
    <CommandPrimitive.Separator
      className={cn(styles.separator, className)}
      data-slot="commandprimitive-separator"
      {...props}
    />
  );
}

function CommandLoading({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof CommandPrimitive.Loading>) {
  return (
    <CommandPrimitive.Loading
      className={cn(styles.loading, className)}
      data-slot="commandprimitive-loading"
      {...props}
    />
  );
}

export {
  CommandRoot as Command,
  CommandDialog,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
  CommandLoading,
  CommandSeparator,
};

.root[cmdk-root] {
  display: flex;
  flex-direction: column;
  width: 100%;
  overflow: hidden;
  color: var(--foreground);
  border: 0.5px solid oklch(from var(--border) l c h / 0.7);

  &:focus-within .input[cmdk-input] {
    outline: none;
  }

  @media (max-width: 640px) {
    height: auto;
  }
}

.dialogOverlay {
  position: fixed;
  inset: 0;
  z-index: 50;
  background-color: oklch(0 0 0 / 0.5);
  animation: fade-in 200ms ease-out;

  [data-theme="dark"] & {
    background-color: oklch(0 0 0 / 0.7);
  }
}

.dialogContent {
  position: fixed;
  left: 50%;
  top: 50%;
  z-index: 50;
  transform: translate(-50%, -50%);
  max-width: 32rem;
  width: calc(100% - 2rem);
  animation: dialog-in 200ms ease-out;

  .root {
    box-shadow: var(--shadow-lg);
    border-radius: var(--radius);
    border: 0.5px solid oklch(from var(--border) l c h / 0.5);
    background-color: var(--card);

    @media (max-width: 640px) {
      max-width: 100%;
    }
  }

  @media (max-width: 640px) {
    width: calc(100% - 2rem);
  }
}

.input[cmdk-input] {
  display: flex;
  height: 2.75rem;
  width: 100%;
  border: none;
  background-color: transparent;
  padding: 0.75rem 1rem;
  font-size: 0.875rem;
  outline: none;
  color: var(--foreground);
  caret-color: var(--primary);

  &::placeholder {
    color: var(--muted-foreground);

    @media (max-width: 640px) {
      font-size: 0.9375rem;
    }
  }

  &:focus {
    outline: none;
  }

  @media (max-width: 640px) {
    height: 3rem;
    font-size: 0.9375rem;
    padding: 0.75rem 1rem;
  }
}

.list[cmdk-list] {
  max-height: 300px;
  overflow: auto;
  padding: 0.25rem;
  scrollbar-width: thin;

  @media (max-width: 640px) {
    max-height: 250px;
    padding: 0.375rem;
  }
}

.item[cmdk-item] {
  position: relative;
  display: flex;
  cursor: pointer;
  user-select: none;
  align-items: center;
  padding: 0.5rem;
  font-size: 0.875rem;
  gap: 0.5rem;
  outline: none;
  margin-top: 0.5rem;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0;
    background-color: transparent;
    z-index: -1;
  }

  &:hover::before,
  &[data-selected="true"]::before {
    background-color: var(--mix-card-80-muted);
  }

  &[data-disabled="true"] {
    pointer-events: none;
    opacity: 0.5;
  }

  svg:not(.keyboardShortcut svg) {
    height: 1rem;
    width: 1rem;
    flex-shrink: 0;

    @media (max-width: 640px) {
      height: 1.125rem;
      width: 1.125rem;
    }
  }

  &:has(svg) {
    padding: 0.25rem;

    @media (max-width: 640px) {
      padding: 0.5rem 0.625rem;
    }
  }

  @media (max-width: 640px) {
    min-height: 2.75rem;
    padding: 0.75rem 0.625rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
  }
}

.keyboardShortcut {
  margin-left: auto;
  padding: 0.125rem 0.25rem;
  background-color: var(--background);
  border: 0.5px solid oklch(from var(--border) l c h / 0.5);
  font-size: 0.75rem;
  font-family: monospace;
  color: var(--muted-foreground);
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 0.25rem;

  svg {
    flex-shrink: 0;
  }

  @media (max-width: 640px) {
    font-size: 0.8125rem;
    padding: 0.1875rem 0.375rem;
  }
}

.iconBackground {
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: var(--muted);
  padding: 0.4rem;
  border: 0.5px solid oklch(from var(--border) l c h / 0.5);
}

.group[cmdk-group] {
  overflow: hidden;
  padding: 0.25rem 0;
  color: var(--foreground);

  [cmdk-group-heading] {
    padding: 0 0.5rem;
    font-size: 0.71875rem;
    font-weight: 500;
    color: var(--muted-foreground);
    text-transform: uppercase;
    letter-spacing: 0.025em;

    @media (max-width: 640px) {
      font-size: 0.8125rem;
      padding: 0 0.625rem;
    }
  }
}

.empty[cmdk-empty] {
  padding: 1.5rem;
  text-align: center;
  color: var(--muted-foreground);
  font-size: 0.875rem;

  @media (max-width: 640px) {
    padding: 1.75rem 1rem;
    font-size: 0.9375rem;
  }
}

.separator[cmdk-separator] {
  height: 0.5px;
  width: 100%;
  background-color: oklch(from var(--border) l c h / 0.4);
  margin: 0.25rem 0;
}

.loading[cmdk-loading] {
  padding: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  font-size: 0.875rem;
}

@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes dialog-in {
  from {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.95);
  }
  to {
    opacity: 1;
    transform: translate(-50%, -50%) scale(1);
  }
}
