"use client";

import { ContextMenu } from "@base-ui/react/context-menu";
import { Check, ChevronRight, Circle } from "lucide-react";
import type React from "react";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import styles from "./context-menu.module.css";

function ContextMenuRoot({ ...props }: ContextMenu.Root.Props) {
  return <ContextMenu.Root {...props} />;
}

function ContextMenuTrigger({ className, ...props }: ContextMenu.Trigger.Props) {
  return <ContextMenu.Trigger className={cn(styles.trigger, className)} data-slot="contextmenu-trigger" {...props} />;
}

const ContextMenuPortal = ContextMenu.Portal;

function ContextMenuBackdrop({ className, ...props }: ContextMenu.Backdrop.Props) {
  return (
    <ContextMenu.Backdrop className={cn(styles.backdrop, className)} data-slot="contextmenu-backdrop" {...props} />
  );
}

function ContextMenuPositioner({ className, ...props }: ContextMenu.Positioner.Props) {
  return (
    <ContextMenu.Positioner
      className={cn(styles.positioner, className)}
      data-slot="contextmenu-positioner"
      {...props}
    />
  );
}

function ContextMenuPopup({ className, ...props }: ContextMenu.Popup.Props) {
  return <ContextMenu.Popup className={cn(styles.popup, className)} data-slot="contextmenu-popup" {...props} />;
}

function ContextMenuArrow({ className, ...props }: ContextMenu.Arrow.Props) {
  return <ContextMenu.Arrow className={cn(styles.arrow, className)} data-slot="contextmenu-arrow" {...props} />;
}

interface ContextMenuItemProps extends ContextMenu.Item.Props {
  icon?: ReactNode;
  inset?: boolean;
  className?: string;
  children?: ReactNode;
  variant?: "default" | "destructive";
}

function ContextMenuItem({
  className,
  icon,
  children,
  inset = false,
  variant = "default",
  ...props
}: ContextMenuItemProps) {
  return (
    <ContextMenu.Item
      className={cn(styles.item, inset && styles.inset, className)}
      data-slot="contextmenu-item"
      data-variant={variant === "destructive" ? "destructive" : undefined}
      {...props}
    >
      {icon && <span className={styles.icon}>{icon}</span>}
      {children}
    </ContextMenu.Item>
  );
}

function ContextMenuCheckboxItem({ className, children, ...props }: ContextMenu.CheckboxItem.Props) {
  return (
    <ContextMenu.CheckboxItem
      className={cn(styles.checkboxItem, className)}
      data-slot="contextmenu-checkboxitem"
      {...props}
    >
      <span className={styles.itemIndicator}>
        <Check size={16} />
      </span>
      {children}
    </ContextMenu.CheckboxItem>
  );
}

function ContextMenuRadioItem({ className, children, ...props }: ContextMenu.RadioItem.Props) {
  return (
    <ContextMenu.RadioItem className={cn(styles.radioItem, className)} data-slot="contextmenu-radioitem" {...props}>
      <span className={styles.itemIndicator}>
        <Circle fill="currentColor" size={8} />
      </span>
      {children}
    </ContextMenu.RadioItem>
  );
}

function ContextMenuRadioGroup({ className, ...props }: ContextMenu.RadioGroup.Props) {
  return <ContextMenu.RadioGroup className={cn(className)} {...props} />;
}

function ContextMenuLabel({
  className,
  inset = false,
  ...props
}: React.HTMLAttributes<HTMLDivElement> & { inset?: boolean }) {
  return <div className={cn(styles.label, inset && styles.inset, className)} {...props} />;
}

function ContextMenuSeparator({ className, ...props }: ContextMenu.Separator.Props) {
  return (
    <div className={styles.seperatorWrapper}>
      <ContextMenu.Separator className={cn(styles.separator, className)} {...props} />
    </div>
  );
}

function ContextMenuSubmenuRoot({ ...props }: ContextMenu.SubmenuRoot.Props) {
  return <ContextMenu.SubmenuRoot {...props} />;
}

function ContextMenuSubmenuTrigger({
  className,
  inset = false,
  children,
  ...props
}: ContextMenu.SubmenuTrigger.Props & {
  inset?: boolean;
}) {
  return (
    <ContextMenu.SubmenuTrigger
      className={cn(styles.submenuTrigger, inset && styles.inset, className)}
      data-slot="contextmenu-submenutrigger"
      {...props}
    >
      {children}
      <ChevronRight className={styles.submenuIcon} size={16} />
    </ContextMenu.SubmenuTrigger>
  );
}

function ContextMenuGroup({ className, ...props }: ContextMenu.Group.Props) {
  return <ContextMenu.Group className={cn(styles.group, className)} data-slot="contextmenu-group" {...props} />;
}

function ContextMenuShortcut({ className, ...props }: React.HTMLAttributes<HTMLSpanElement>) {
  return <span className={cn(styles.shortcut, className)} {...props} />;
}

function ContextMenuSpacer() {
  return <div style={{ height: "4px", width: "100%" }} />;
}

export {
  ContextMenuRoot as ContextMenu,
  ContextMenuArrow,
  ContextMenuBackdrop,
  ContextMenuCheckboxItem,
  ContextMenuGroup,
  ContextMenuItem,
  ContextMenuLabel,
  ContextMenuPopup,
  ContextMenuPortal,
  ContextMenuPositioner,
  ContextMenuRadioGroup,
  ContextMenuRadioItem,
  ContextMenuSeparator,
  ContextMenuShortcut,
  ContextMenuSpacer,
  ContextMenuSubmenuRoot,
  ContextMenuSubmenuTrigger,
  ContextMenuTrigger,
};

.trigger {
  outline: none;
  user-select: none;
}

.backdrop {
  position: fixed;
  inset: 0;
  z-index: 140;
  background-color: rgb(0 0 0 / 0.8);
  opacity: 0;
  transition: opacity 150ms ease;

  &[data-open] {
    opacity: 1;
  }
}

.positioner {
  position: absolute;
  z-index: 150;
  outline: none;
}

.popup {
  box-sizing: border-box;
  min-width: 140px;
  background-color: var(--popover);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  border-radius: var(--radius);
  display: flex;
  flex-direction: column;
  box-shadow: var(--shadow-border-stack);
  transform-origin: "top center";
  transition:
    transform 250ms var(--ease-out-expo),
    opacity 250ms var(--ease-out-expo);

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }

  @media (max-width: 640px) {
    padding: 0.375rem;
    max-width: calc(100vw - 2rem);
  }
}

.seperatorWrapper {
  padding: 5px 0;
}

.separator {
  height: 1px;
  border-bottom: 0.5px solid oklch(from var(--border) l c h / 0.8);
}

.arrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}

.item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: calc(var(--radius) - 4px);
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before,
  &[data-highlighted]::before,
  &:hover::before,
  &:focus::before {
    background-color: var(--accent);
  }

  &:focus,
  &:focus-visible {
    outline: none;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;

    &:hover {
      background: transparent;
    }
  }

  &:hover {
    & > .shortcut {
      color: var(--secondary-foreground);
    }

    & > .icon,
    & .icon {
      color: var(--secondary-foreground);
      opacity: 1;
    }
  }

  &[data-variant="destructive"]:hover {
    color: var(--destructive);

    & .icon {
      color: var(--destructive);
    }
  }

  @media (max-width: 640px) {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
    min-height: 2.75rem;
  }
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  margin-left: 4px;
}

.inset {
  padding-left: 2rem;
}

.checkboxItem,
.radioItem {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  padding-left: 2rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }

  @media (max-width: 640px) {
    padding: 0.625rem 0.75rem;
    padding-left: 2.25rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }
}

.itemIndicator {
  position: absolute;
  left: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1rem;
  width: 1rem;

  @media (max-width: 640px) {
    left: 0.75rem;
    height: 1.125rem;
    width: 1.125rem;
  }
}

.label {
  padding: 0.375rem 0.5rem;
  padding-top: 0.75rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.025em;

  @media (max-width: 640px) {
    padding: 0.625rem 0.75rem;
    padding-top: 0.875rem;
    font-size: 0.8125rem;
  }
}

.submenuTrigger {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: calc(var(--radius) - 4px);
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before,
  &[data-highlighted]::before,
  &:hover::before,
  &:focus::before {
    background-color: var(--accent);
  }

  &:focus,
  &:focus-visible {
    outline: none;
  }

  &:hover {
    & .submenuIcon,
    & > svg {
      color: var(--secondary-foreground);
    }
  }

  @media (max-width: 640px) {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }
}

.submenuIcon {
  margin-left: auto;
  height: 1rem;
  width: 1rem;
  color: var(--muted-foreground);
  opacity: 0.6;

  @media (max-width: 640px) {
    height: 1.125rem;
    width: 1.125rem;
  }
}

.submenuTrigger > svg {
  color: var(--muted-foreground);
}

.group {
  overflow: hidden;
}

.shortcut {
  margin-left: auto;
  margin-right: 4px;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  letter-spacing: 0.025em;
  opacity: 0.6;

  @media (max-width: 640px) {
    display: none;
  }
}
