"use client";

import { ContextMenu } from "@base-ui-components/react/context-menu";
import { Check, ChevronRight, Circle } from "lucide-react";
import type React from "react";
import { cn } from "@/lib/utils";
import styles from "./context-menu.module.css";

function ContextMenuRoot({
  ...props
}: React.ComponentProps<typeof ContextMenu.Root>) {
  return <ContextMenu.Root {...props} />;
}

function ContextMenuTrigger({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Trigger>) {
  return (
    <ContextMenu.Trigger className={cn(styles.trigger, className)} {...props} />
  );
}

const ContextMenuPortal = ContextMenu.Portal;

function ContextMenuBackdrop({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Backdrop>) {
  return (
    <ContextMenu.Backdrop
      className={cn(styles.backdrop, className)}
      {...props}
    />
  );
}

function ContextMenuPositioner({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Positioner>) {
  return (
    <ContextMenu.Positioner
      className={cn(styles.positioner, className)}
      {...props}
    />
  );
}

function ContextMenuPopup({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Popup>) {
  return (
    <ContextMenu.Popup className={cn(styles.popup, className)} {...props} />
  );
}

function ContextMenuArrow({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Arrow>) {
  return (
    <ContextMenu.Arrow className={cn(styles.arrow, className)} {...props} />
  );
}

function ContextMenuItem({
  className,
  inset = false,
  ...props
}: React.ComponentProps<typeof ContextMenu.Item> & { inset?: boolean }) {
  return (
    <ContextMenu.Item
      className={cn(styles.item, inset && styles.inset, className)}
      {...props}
    />
  );
}

function ContextMenuCheckboxItem({
  className,
  children,
  ...props
}: React.ComponentProps<typeof ContextMenu.CheckboxItem>) {
  return (
    <ContextMenu.CheckboxItem
      className={cn(styles.checkboxItem, className)}
      {...props}
    >
      <span className={styles.itemIndicator}>
        <Check size={16} />
      </span>
      {children}
    </ContextMenu.CheckboxItem>
  );
}

function ContextMenuRadioItem({
  className,
  children,
  ...props
}: React.ComponentProps<typeof ContextMenu.RadioItem>) {
  return (
    <ContextMenu.RadioItem
      className={cn(styles.radioItem, className)}
      {...props}
    >
      <span className={styles.itemIndicator}>
        <Circle fill="currentColor" size={8} />
      </span>
      {children}
    </ContextMenu.RadioItem>
  );
}

function ContextMenuRadioGroup({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.RadioGroup>) {
  return <ContextMenu.RadioGroup className={cn(className)} {...props} />;
}

function ContextMenuLabel({
  className,
  inset = false,
  ...props
}: React.HTMLAttributes<HTMLDivElement> & { inset?: boolean }) {
  return (
    <div
      className={cn(styles.label, inset && styles.inset, className)}
      {...props}
    />
  );
}

function ContextMenuSeparator({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Separator>) {
  return (
    <ContextMenu.Separator
      className={cn(styles.separator, className)}
      {...props}
    />
  );
}

function ContextMenuSubmenuRoot({
  ...props
}: React.ComponentProps<typeof ContextMenu.SubmenuRoot>) {
  return <ContextMenu.SubmenuRoot {...props} />;
}

function ContextMenuSubmenuTrigger({
  className,
  inset = false,
  children,
  ...props
}: React.ComponentProps<typeof ContextMenu.SubmenuTrigger> & {
  inset?: boolean;
}) {
  return (
    <ContextMenu.SubmenuTrigger
      className={cn(styles.submenuTrigger, inset && styles.inset, className)}
      {...props}
    >
      {children}
      <ChevronRight className={styles.submenuIcon} size={16} />
    </ContextMenu.SubmenuTrigger>
  );
}

function ContextMenuGroup({
  className,
  ...props
}: React.ComponentProps<typeof ContextMenu.Group>) {
  return (
    <ContextMenu.Group className={cn(styles.group, className)} {...props} />
  );
}

function ContextMenuShortcut({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) {
  return <span className={cn(styles.shortcut, className)} {...props} />;
}

export {
  ContextMenuRoot as ContextMenu,
  ContextMenuArrow,
  ContextMenuBackdrop,
  ContextMenuCheckboxItem,
  ContextMenuGroup,
  ContextMenuItem,
  ContextMenuLabel,
  ContextMenuPopup,
  ContextMenuPortal,
  ContextMenuPositioner,
  ContextMenuRadioGroup,
  ContextMenuRadioItem,
  ContextMenuSeparator,
  ContextMenuShortcut,
  ContextMenuSubmenuRoot,
  ContextMenuSubmenuTrigger,
  ContextMenuTrigger,
};

.trigger {
  outline: none;
  user-select: none;
}

.backdrop {
  position: fixed;
  inset: 0;
  z-index: 140;
  background-color: rgb(0 0 0 / 0.8);
  opacity: 0;
  transition: opacity 150ms ease;

  &[data-open] {
    opacity: 1;
  }
}

.positioner {
  position: absolute;
  z-index: 150;
  outline: none;
}

.popup {
  box-sizing: border-box;
  min-width: 8rem;
  padding: 0.25rem;
  border-radius: var(--radius);
  background-color: var(--mix-card-85-bg);
  color: var(--secondary-foreground);
  border: 1px solid var(--border);
  box-shadow:
    0 10px 15px -3px rgb(0 0 0 / 0.1),
    0 4px 6px -4px rgb(0 0 0 / 0.1);
  transform-origin: 50%;
  animation: slideDownAndFade 250ms var(--ease-out-expo);

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }
}

@keyframes slideDownAndFade {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.arrow {
  position: absolute;
  width: 0.5rem;
  height: 0.5rem;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  transform: rotate(45deg);
}

.arrow[data-side="top"] {
  bottom: -0.25rem;
  border-right: none;
  border-bottom: none;
}

.arrow[data-side="right"] {
  left: -0.25rem;
  border-bottom: none;
  border-left: none;
}

.arrow[data-side="bottom"] {
  top: -0.25rem;
  border-top: none;
  border-left: none;
}

.arrow[data-side="left"] {
  right: -0.25rem;
  border-top: none;
  border-right: none;
}

.item {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  transition: all 150ms ease;
  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.inset {
  padding-left: 2rem;
}

.checkboxItem {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  padding-left: 2rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  transition: all 150ms ease;
  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.radioItem {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  padding-left: 2rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  transition: all 150ms ease;
  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.itemIndicator {
  position: absolute;
  left: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1rem;
  width: 1rem;
}

.label {
  padding: 0.375rem 0.5rem;
  padding-top: 0.75rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.025em;
}

.separator {
  margin: 0.25rem -0.25rem;
  height: 1px;
  background-color: var(--border);
}

.submenuTrigger {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;
  transition: all 150ms ease;
  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.submenuIcon {
  margin-left: auto;
  height: 1rem;
  width: 1rem;
  color: var(--muted-foreground);
  opacity: 0.6;
}

.group {
  overflow: hidden;
}

.shortcut {
  margin-left: auto;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  letter-spacing: 0.025em;
  opacity: 0.6;
}

@media (max-width: 640px) {
  .popup {
    padding: 0.375rem;
    max-width: calc(100vw - 2rem);
  }

  .item {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
    min-height: 2.75rem;
  }

  .checkboxItem,
  .radioItem {
    padding: 0.625rem 0.75rem;
    padding-left: 2.25rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }

  .submenuTrigger {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }

  .label {
    padding: 0.625rem 0.75rem;
    padding-top: 0.875rem;
    font-size: 0.8125rem;
  }

  .shortcut {
    font-size: 0.8125rem;
  }

  .itemIndicator {
    left: 0.75rem;
    height: 1.125rem;
    width: 1.125rem;
  }

  .submenuIcon {
    height: 1.125rem;
    width: 1.125rem;
  }
}
