"use client";

import { ContextMenu } from "@base-ui-components/react/context-menu";
import { Check, ChevronRight, Circle } from "lucide-react";
import type React from "react";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import styles from "./context-menu.module.css";

function ContextMenuRoot({
  ...props
}: ContextMenu.Root.Props) {
  return <ContextMenu.Root {...props} />;
}

function ContextMenuTrigger({
  className,
  ...props
}: ContextMenu.Trigger.Props) {
  return (
    <ContextMenu.Trigger data-slot="contextmenu-trigger" className={cn(styles.trigger, className)} {...props} />
  );
}

const ContextMenuPortal = ContextMenu.Portal;

function ContextMenuBackdrop({
  className,
  ...props
}: ContextMenu.Backdrop.Props) {
  return (
    <ContextMenu.Backdrop
      data-slot="contextmenu-backdrop"
      className={cn(styles.backdrop, className)}
      {...props}
    />
  );
}

function ContextMenuPositioner({
  className,
  ...props
}: ContextMenu.Positioner.Props) {
  return (
    <ContextMenu.Positioner
      data-slot="contextmenu-positioner"
      className={cn(styles.positioner, className)}
      {...props}
    />
  );
}

function ContextMenuPopup({
  className,
  ...props
}: ContextMenu.Popup.Props) {
  return (
    <ContextMenu.Popup data-slot="contextmenu-popup" className={cn(styles.popup, className)} {...props} />
  );
}

function ContextMenuArrow({
  className,
  ...props
}: ContextMenu.Arrow.Props) {
  return (
    <ContextMenu.Arrow data-slot="contextmenu-arrow" className={cn(styles.arrow, className)} {...props} />
  );
}

interface ContextMenuItemProps extends ContextMenu.Item.Props {
  icon?: ReactNode;
  inset?: boolean;
  className?: string;
  children?: ReactNode;
  variant?: "default" | "destructive";
}

function ContextMenuItem({
  className,
  icon,
  children,
  inset = false,
  variant = "default",
  ...props
}: ContextMenuItemProps) {
  return (
    <ContextMenu.Item
      data-slot="contextmenu-item"
      className={cn(
        styles.item,
        inset && styles.inset,
        variant === "destructive" && styles.itemDestructive,
        className
      )}
      {...props}
    >
      {icon && <span className={styles.icon}>{icon}</span>}
      {children}
    </ContextMenu.Item>
  );
}

function ContextMenuCheckboxItem({
  className,
  children,
  ...props
}: ContextMenu.CheckboxItem.Props) {
  return (
    <ContextMenu.CheckboxItem
      data-slot="contextmenu-checkboxitem"
      className={cn(styles.checkboxItem, className)}
      {...props}
    >
      <span className={styles.itemIndicator}>
        <Check size={16} />
      </span>
      {children}
    </ContextMenu.CheckboxItem>
  );
}

function ContextMenuRadioItem({
  className,
  children,
  ...props
}: ContextMenu.RadioItem.Props) {
  return (
    <ContextMenu.RadioItem
      data-slot="contextmenu-radioitem"
      className={cn(styles.radioItem, className)}
      {...props}
    >
      <span className={styles.itemIndicator}>
        <Circle fill="currentColor" size={8} />
      </span>
      {children}
    </ContextMenu.RadioItem>
  );
}

function ContextMenuRadioGroup({
  className,
  ...props
}: ContextMenu.RadioGroup.Props) {
  return <ContextMenu.RadioGroup className={cn(className)} {...props} />;
}

function ContextMenuLabel({
  className,
  inset = false,
  ...props
}: React.HTMLAttributes<HTMLDivElement> & { inset?: boolean }) {
  return (
    <div
      className={cn(styles.label, inset && styles.inset, className)}
      {...props}
    />
  );
}

function ContextMenuSeparator({
  className,
  ...props
}: ContextMenu.Separator.Props) {
  return (
    <div className={styles.seperatorWrapper}>
      <ContextMenu.Separator
        className={cn(styles.separator, className)}
        {...props}
      />
    </div>
  );
}

function ContextMenuSubmenuRoot({
  ...props
}: ContextMenu.SubmenuRoot.Props) {
  return <ContextMenu.SubmenuRoot {...props} />;
}

function ContextMenuSubmenuTrigger({
  className,
  inset = false,
  children,
  ...props
}: ContextMenu.SubmenuTrigger.Props & {
  inset?: boolean;
}) {
  return (
    <ContextMenu.SubmenuTrigger
      data-slot="contextmenu-submenutrigger"
      className={cn(styles.submenuTrigger, inset && styles.inset, className)}
      {...props}
    >
      {children}
      <ChevronRight className={styles.submenuIcon} size={16} />
    </ContextMenu.SubmenuTrigger>
  );
}

function ContextMenuGroup({
  className,
  ...props
}: ContextMenu.Group.Props) {
  return (
    <ContextMenu.Group data-slot="contextmenu-group" className={cn(styles.group, className)} {...props} />
  );
}

function ContextMenuShortcut({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) {
  return <span className={cn(styles.shortcut, className)} {...props} />;
}

function ContextMenuSpacer() {
  return <div style={{ height: "4px", width: "100%" }} />;
}

export {
  ContextMenuRoot as ContextMenu,
  ContextMenuArrow,
  ContextMenuBackdrop,
  ContextMenuCheckboxItem,
  ContextMenuGroup,
  ContextMenuItem,
  ContextMenuLabel,
  ContextMenuPopup,
  ContextMenuPortal,
  ContextMenuPositioner,
  ContextMenuRadioGroup,
  ContextMenuRadioItem,
  ContextMenuSeparator,
  ContextMenuShortcut,
  ContextMenuSpacer,
  ContextMenuSubmenuRoot,
  ContextMenuSubmenuTrigger,
  ContextMenuTrigger,
};

.trigger {
  outline: none;
  user-select: none;
}

.backdrop {
  position: fixed;
  inset: 0;
  z-index: 140;
  background-color: rgb(0 0 0 / 0.8);
  opacity: 0;
  transition: opacity 150ms ease;

  &[data-open] {
    opacity: 1;
  }
}

.positioner {
  position: absolute;
  z-index: 150;
  outline: none;
}

.popup {
  box-sizing: border-box;
  min-width: 140px;
  background-color: var(--mix-card-50-bg);
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  border-radius: var(--radius);
  display: flex;
  flex-direction: column;
  box-shadow:
    oklch(from var(--border) l c h / 0.1) 0px 1px 2px,
    oklch(from var(--border) l c h / 0.1) 0px 1px 2px,
    oklch(from var(--border) l c h / 0.1) 0px 1px 1px;
  transform-origin: "top center";
  transition:
    transform 150ms var(--ease-out-expo),
    opacity 150ms var(--ease-out-expo);

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }
}

.seperatorWrapper {
  padding: 5px 0;
}

.separator {
  height: 1px;
  border-bottom: 0.5px solid oklch(from var(--border) l c h / 0.6);
}

.arrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}

.item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3rem;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: 0.3rem;
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &[data-highlighted]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &:hover::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &:focus::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &:focus {
    outline: none;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &[data-disabled]:hover {
    background: transparent;
  }
}

.item:hover > .shortcut {
  color: var(--secondary-foreground);
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  margin-left: 4px;
}

.item:hover > .icon {
  color: var(--secondary-foreground);
}

.item:hover .icon {
  opacity: 1;
}

.itemDestructive {
  color: var(--destructive);
}

.itemDestructive .icon {
  color: var(--destructive);
}

.itemDestructive:hover::before {
  background-color: var(--destructive);
}

.itemDestructive:hover {
  color: var(--destructive-foreground);
}

.itemDestructive:hover .icon {
  color: var(--destructive-foreground);
}

.inset {
  padding-left: 2rem;
}

.checkboxItem {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  padding-left: 2rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;

  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.radioItem {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.375rem 0.5rem;
  padding-left: 2rem;
  font-size: 0.875rem;
  border-radius: calc(var(--radius) - 2px);
  cursor: pointer;

  user-select: none;
  outline: none;

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &:hover,
  &[data-highlighted] {
    background: var(--muted);
    color: var(--accent-foreground);
  }

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
    pointer-events: none;
  }
}

.itemIndicator {
  position: absolute;
  left: 0.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 1rem;
  width: 1rem;
}

.label {
  padding: 0.375rem 0.5rem;
  padding-top: 0.75rem;
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--muted-foreground);
  text-transform: uppercase;
  letter-spacing: 0.025em;
}

.submenuTrigger {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3rem;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: 0.3rem;
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &[data-highlighted]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &:hover::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }
}

.submenuIcon {
  margin-left: auto;
  height: 1rem;
  width: 1rem;
  color: var(--muted-foreground);
  opacity: 0.6;
}

.submenuTrigger:hover .submenuIcon {
  color: var(--secondary-foreground);
}

.submenuTrigger > svg {
  color: var(--muted-foreground);
}

.submenuTrigger:hover > svg {
  color: var(--secondary-foreground);
}

.group {
  overflow: hidden;
}

.shortcut {
  margin-left: auto;
  margin-right: 4px;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  letter-spacing: 0.025em;
  opacity: 0.6;
}

@media (max-width: 640px) {
  .popup {
    padding: 0.375rem;
    max-width: calc(100vw - 2rem);
  }

  .item {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    gap: 0.75rem;
    min-height: 2.75rem;
  }

  .checkboxItem,
  .radioItem {
    padding: 0.625rem 0.75rem;
    padding-left: 2.25rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }

  .submenuTrigger {
    padding: 0.625rem 0.75rem;
    font-size: 0.9375rem;
    min-height: 2.75rem;
  }

  .label {
    padding: 0.625rem 0.75rem;
    padding-top: 0.875rem;
    font-size: 0.8125rem;
  }

  .shortcut {
    display: none;
  }

  .itemIndicator {
    left: 0.75rem;
    height: 1.125rem;
    width: 1.125rem;
  }

  .submenuIcon {
    height: 1.125rem;
    width: 1.125rem;
  }
}
