"use client";

import React from "react";
import { Dialog } from "@base-ui-components/react/dialog";
import { X } from "lucide-react";
import { cn } from "@/lib/utils";
import styles from "./dialog.module.css";

function DialogRoot({ ...props }: React.ComponentProps<typeof Dialog.Root>) {
  return <Dialog.Root {...props} />;
}

function DialogTrigger({ ...props }: React.ComponentProps<typeof Dialog.Trigger>) {
  return <Dialog.Trigger {...props} />;
}

const DialogPortal = Dialog.Portal;

function DialogOverlay({ className, ...props }: React.ComponentPropsWithoutRef<typeof Dialog.Backdrop>) {
  return <Dialog.Backdrop className={cn(styles.overlay, className)} {...props} />;
}

function DialogPopup({ className, ...props }: React.ComponentPropsWithoutRef<typeof Dialog.Popup>) {
  return <Dialog.Popup className={cn(styles.content, className)} {...props} />;
}

function DialogTitle({ className, ...props }: React.ComponentPropsWithoutRef<typeof Dialog.Title>) {
  return <Dialog.Title className={cn(styles.title, className)} {...props} />;
}

function DialogDescription({ className, ...props }: React.ComponentProps<typeof Dialog.Description>) {
  return <Dialog.Description className={cn(styles.description, className)} {...props} />;
}

function DialogClose({ ...props }: React.ComponentProps<typeof Dialog.Close>) {
  return <Dialog.Close {...props} />;
}

function DialogHeader({ className, ...props }: React.ComponentProps<"div">) {
  return <div className={cn(styles.header, className)} {...props} />;
}

function DialogFooter({ className, ...props }: React.ComponentProps<"div">) {
  return <div className={cn(styles.footer, className)} {...props} />;
}

export {
  DialogRoot,
  DialogTrigger,
  DialogPortal,
  DialogPopup,
  DialogHeader,
  DialogFooter,
  DialogTitle,
  DialogDescription,
  DialogClose,
  DialogOverlay,
  X as CloseIcon,
};

.overlay {
  position: fixed;
  inset: 0;
  z-index: var(--dialog-z);
  background-color: var(--dialog-overlay);
  opacity: 1;
  transition: opacity 200ms var(--ease-out-expo);
}

.overlay[data-starting-style],
.overlay[data-ending-style] {
  opacity: 0;
}

.content {
  position: fixed;
  left: 50%;
  top: 50%;
  z-index: 101;
  display: grid;
  width: 100%;
  overflow-y: auto;
  gap: 1rem;
  border: 1px solid var(--border);
  background-color: var(--background);
  padding: 1.5rem;
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-lg);
  opacity: 1;
  transform: translate(-50%, -50%);
  transition:
    opacity 200ms var(--ease-out-expo),
    transform 200ms var(--ease-out-expo);
}

.content[data-starting-style],
.content[data-ending-style] {
  opacity: 0;
  transform: translate(-50%, -54%) scale(0.96);
}

.close {
  position: absolute;
  right: 1rem;
  top: 1rem;
  border-radius: 0.25rem;
  opacity: 0.7;
  background: transparent;
  border: none;
  padding: 0.25rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 1.5rem;
  height: 1.5rem;
}

.close:hover {
  opacity: 1;
}

.close:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.close svg {
  width: 1rem;
  height: 1rem;
}

.header {
  display: flex;
  flex-direction: column;
  gap: 0.375rem;
}

.header h2 {
  margin: 0;
}

.header p {
  margin: 0;
}

.footer {
  display: flex;
  flex-direction: column-reverse;
  gap: 0.5rem;
}

.footer > :last-child {
  margin-top: 0.5rem;
}

@media (min-width: 640px) {
  .footer {
    flex-direction: row;
    justify-content: flex-end;
  }

  .footer > :last-child {
    margin-top: 0;
  }
}

.title {
  font-size: 1.125rem;
  font-weight: 600;
  line-height: 1;
  letter-spacing: -0.025em;
  color: var(--title);
  margin: 0;
}

.description {
  font-size: 0.875rem;
  color: var(--muted-foreground);
  margin: 0;
  line-height: 1.5;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

@media (max-width: 640px) {
  .content {
    width: calc(100vw - 2rem);
    max-width: none;
  }
}
