"use client";

import { Dialog } from "@base-ui/react/dialog";
import type React from "react";
import { cn } from "@/lib/utils";
import styles from "./dialog.module.css";

function DialogRoot({ ...props }: Dialog.Root.Props) {
  return <Dialog.Root {...props} />;
}

function DialogTrigger({ ...props }: Dialog.Trigger.Props) {
  return <Dialog.Trigger {...props} />;
}

const DialogPortal = Dialog.Portal;

function DialogOverlay({ className, ...props }: Dialog.Backdrop.Props) {
  return <Dialog.Backdrop className={cn(styles.overlay, className)} data-slot="dialog-backdrop" {...props} />;
}

function DialogPopup({ className, ...props }: Dialog.Popup.Props) {
  return <Dialog.Popup className={cn(styles.content, className)} data-slot="dialog-popup" {...props} />;
}

function DialogTitle({ className, ...props }: Dialog.Title.Props) {
  return <Dialog.Title className={cn(styles.title, className)} {...props} />;
}

function DialogDescription({ className, ...props }: Dialog.Description.Props) {
  return <Dialog.Description className={cn(styles.description, className)} data-slot="dialog-description" {...props} />;
}

function DialogClose({ ...props }: Dialog.Close.Props) {
  return <Dialog.Close {...props} />;
}

function DialogHeader({ className, ...props }: React.ComponentProps<"div">) {
  return <div className={cn(styles.header, className)} {...props} />;
}

function DialogFooter({ className, ...props }: React.ComponentProps<"div">) {
  return <div className={cn(styles.footer, className)} {...props} />;
}

export {
  DialogRoot as Dialog,
  DialogClose,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogOverlay,
  DialogPopup,
  DialogPortal,
  DialogRoot,
  DialogTitle,
  DialogTrigger,
};

.overlay {
  position: fixed;
  inset: 0;
  z-index: var(--dialog-z);
  background-color: var(--dialog-overlay);
  transition: opacity 150ms;

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
  }
}

.content {
  position: fixed;
  left: 50%;
  top: 50%;
  z-index: 101;
  display: grid;
  width: 100%;
  overflow-y: auto;
  gap: 1rem;
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  background-color: var(--mix-card-5-bg);
  padding: 1.5rem;
  border-radius: var(--radius);
  box-shadow:
    0 8px 11px -2px oklch(from var(--foreground) l c h / 0.01),
    0 4px 6px -2px oklch(from var(--foreground) l c h / 0.01),
    inset 0 0 0 1px oklch(from var(--background) l c h / 0.01);
  transform: translate(-50%, -50%);
  transition: all 150ms;

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: translate(-50%, -50%) scale(0.95);
  }

  @media (max-width: 640px) {
    width: calc(100vw - 2rem);
    max-width: none;
  }
}

.close {
  position: absolute;
  right: 1rem;
  top: 1rem;
  border-radius: 0.25rem;
  opacity: 0.7;
  background: transparent;
  border: none;
  padding: 0.25rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 1.5rem;
  height: 1.5rem;

  &:hover {
    opacity: 1;
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  & svg {
    width: 1rem;
    height: 1rem;
  }
}

.header {
  display: flex;
  flex-direction: column;
  gap: 0.375rem;

  & h2 {
    margin: 0;
  }

  & p {
    margin: 0;
  }
}

.footer {
  display: flex;
  flex-direction: column-reverse;
  gap: 0.5rem;

  & > :last-child {
    margin-top: 0.5rem;
  }

  @media (min-width: 640px) {
    flex-direction: row;
    justify-content: flex-end;

    & > :last-child {
      margin-top: 0;
    }
  }
}

.title {
  font-size: 1.125rem;
  font-weight: 600;
  line-height: 1;
  letter-spacing: -0.008em;
  color: var(--foreground);
  margin: 0;
}

.description {
  font-size: 0.875rem;
  color: var(--muted-foreground);
  margin: 0;
  line-height: 1.5;
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}
