"use client";

import { cn } from "@/lib/utils";
import { Menu } from "@base-ui-components/react/menu";
import type { ReactNode } from "react";
import styles from "./dropdown-menu.module.css";

function DropdownMenuRoot({
  ...props
}: React.ComponentProps<typeof Menu.Root>) {
  return <Menu.Root {...props} />;
}

function DropdownMenuTrigger({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof Menu.Trigger>) {
  return <Menu.Trigger {...props} className={cn(styles.trigger, className)} />;
}

const DropdownMenuPortal = Menu.Portal;

function DropdownMenuPositioner({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Positioner>) {
  return (
    <Menu.Positioner
      align="start"
      className={cn(styles.positioner, className)}
      side="bottom"
      {...props}
    />
  );
}

function DropdownMenuPopup({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Popup>) {
  return <Menu.Popup className={cn(styles.popup, className)} {...props} />;
}

interface DropdownMenuItemProps extends React.ComponentProps<typeof Menu.Item> {
  icon?: ReactNode;
  className?: string;
  children?: ReactNode;
}

function DropdownMenuItem({
  className,
  icon,
  children,
  ...props
}: DropdownMenuItemProps) {
  return (
    <Menu.Item className={cn(styles.item, className)} {...props}>
      {icon && <span className={styles.icon}>{icon}</span>}
      {children}
    </Menu.Item>
  );
}

function DropdownMenuSeparator({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Separator>) {
  return (
    <Menu.Separator className={cn(styles.separator, className)} {...props} />
  );
}

function DropdownMenuArrow({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Arrow>) {
  return <Menu.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function DropdownMenuSubmenuRoot({
  ...props
}: React.ComponentProps<typeof Menu.SubmenuRoot>) {
  return <Menu.SubmenuRoot {...props} />;
}

function DropdownMenuSubmenuTrigger({
  className,
  ...props
}: React.ComponentProps<typeof Menu.SubmenuTrigger>) {
  return (
    <Menu.SubmenuTrigger
      className={cn(styles.submenuTrigger, className)}
      {...props}
    />
  );
}

export {
  DropdownMenuRoot as DropdownMenu,
  DropdownMenuArrow,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuSubmenuRoot,
  DropdownMenuSubmenuTrigger,
  DropdownMenuTrigger,
};

.root {
  display: inline-block;
}

.trigger {
  @media (hover: hover) {
  }

  &[data-popup-open] {
    background-color: var(--muted);
    color: var(--title-hover);
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }
}

.trigger:focus {
  outline: none;
}

.trigger[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.positioner {
  z-index: 150;
  position: absolute;
  left: 0;
}

.popup {
  min-width: 240px;
  padding: 0.5rem;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  border-radius: 0.5rem;
  box-shadow:
    0 20px 25px -5px rgba(0, 0, 0, 0.1),
    0 10px 10px -5px rgba(0, 0, 0, 0.04);
  animation: popup-in 0.15s ease-out;
  display: flex;
  flex-direction: column;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.popup[data-starting-style],
.popup[data-ending-style] {
  opacity: 0;
  transform: scale(0.95) translateY(-0.25rem);
}

@keyframes popup-in {
  from {
    opacity: 0;
    transform: scale(0.95) translateY(-0.25rem);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

.submenuTrigger,
.item {
  display: flex;
  align-items: center;
  padding: 0.5rem 0.4rem;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.375rem;
  transition: all 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  justify-content: flex-start;
  margin: 0rem;

  &[data-popup-open] {
    background-color: var(--muted);
    color: var(--title);
  }

  &[data-highlighted] {
    background-color: var(--muted);
    color: var(--title);
  }
}

.icon {
  margin-right: 0.75rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  opacity: 0.8;
  transition: opacity 0.2s ease;
}

.submenuTrigger:hover,
.item:hover {
  background-color: var(--muted);
  color: var(--title);
}

.item:hover .icon {
  opacity: 1;
}

.item:hover :global(.shortcut) {
  color: var(--title) !important;
}

.item:focus {
  outline: none;
  background: var(--muted);
  color: var(--title);
}

.item[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.item[data-disabled]:hover {
  background: transparent;
  color: var(--foreground);
}

.separator {
  height: 1px;
  margin: 0.25rem 0.4rem;
  background: var(--border);
  opacity: 0.6;
}

.arrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}
