"use client";

import { Menu } from "@base-ui-components/react/menu";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import styles from "./dropdown-menu.module.css";

function DropdownMenuRoot({
  ...props
}: React.ComponentProps<typeof Menu.Root>) {
  return <Menu.Root {...props} />;
}

function DropdownMenuTrigger({
  className,
  ...props
}: React.ComponentPropsWithoutRef<typeof Menu.Trigger>) {
  return <Menu.Trigger {...props} className={cn(styles.trigger, className)} />;
}

const DropdownMenuPortal = Menu.Portal;

function DropdownMenuPositioner({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Positioner>) {
  return (
    <Menu.Positioner
      className={cn(styles.positioner, className)}
      side="top"
      {...props}
    />
  );
}

function DropdownMenuPopup({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Popup>) {
  return <Menu.Popup className={cn(styles.popup, className)} {...props} />;
}

interface DropdownMenuItemProps extends React.ComponentProps<typeof Menu.Item> {
  icon?: ReactNode;
  className?: string;
  children?: ReactNode;
}

function DropdownMenuItem({
  className,
  icon,
  children,
  ...props
}: DropdownMenuItemProps) {
  return (
    <Menu.Item className={cn(styles.item, className)} {...props}>
      {icon && <span className={styles.icon}>{icon}</span>}
      {children}
    </Menu.Item>
  );
}

function DropdownMenuSeparator({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Separator>) {
  return (
    <div className={styles.seperatorWrapper}>
      <Menu.Separator className={cn(styles.separator, className)} {...props} />
    </div>
  );
}

function DropdownMenuArrow({
  className,
  ...props
}: React.ComponentProps<typeof Menu.Arrow>) {
  return <Menu.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function DropdownMenuSubmenuRoot({
  ...props
}: React.ComponentProps<typeof Menu.SubmenuRoot>) {
  return <Menu.SubmenuRoot {...props} />;
}

function DropdownMenuSubmenuTrigger({
  className,
  ...props
}: React.ComponentProps<typeof Menu.SubmenuTrigger>) {
  return (
    <Menu.SubmenuTrigger
      className={cn(styles.submenuTrigger, className)}
      {...props}
    />
  );
}

export {
  DropdownMenuRoot as DropdownMenu,
  DropdownMenuArrow,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuSubmenuRoot,
  DropdownMenuSubmenuTrigger,
  DropdownMenuTrigger,
};

.root {
  display: inline-block;
}

.trigger:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.trigger[data-popup-open] {
  background-color: var(--muted);
  color: var(--foreground);
}

.trigger[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.positioner {
  z-index: 150;
  position: absolute;
  left: 0;
}

.popup {
  min-width: 170px;
  background-color: var(--mix-card-50-bg);
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  border-radius: var(--radius);
  display: flex;
  flex-direction: column;
  box-shadow:
    oklch(from var(--border) l c h / 0.1) 0px 1px 2px,
    oklch(from var(--border) l c h / 0.1) 0px 1px 2px,
    oklch(from var(--border) l c h / 0.1) 0px 1px 1px;
  transform-origin: "top center";
  transition:
    transform 250ms var(--ease-out-expo),
    opacity 250ms var(--ease-out-expo);
}
.popup[data-starting-style],
.popup[data-ending-style] {
  opacity: 0;
  transform: scale(0.95);
}

.seperatorWrapper {
  padding: 5px 0;
}

.separator {
  height: 1px;
  border-bottom: 0.5px solid oklch(from var(--border) l c h / 0.6);
}

.submenuTrigger,
.item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3rem;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: 0.3rem;
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &[data-highlighted]::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  margin-left: 4px;
}

.item:hover > .icon {
  color: var(--secondary-foreground);
}

.submenuTrigger:hover::before,
.item:hover::before {
  background-color: oklch(from var(--accent) l c h / 0.7);
}

.item:hover .icon {
  opacity: 1;
}

.item:focus::before {
  background-color: oklch(from var(--accent) l c h / 0.7);
}

.item:focus {
  outline: none;
}

.item[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.item[data-disabled]:hover {
  background: transparent;
}

.arrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}
