"use client";

import { Menu } from "@base-ui-components/react/menu";
import { ChevronRight } from "lucide-react";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import styles from "./dropdown-menu.module.css";

function DropdownMenuRoot({ ...props }: Menu.Root.Props) {
  return <Menu.Root {...props} />;
}

function DropdownMenuTrigger({ className, ...props }: Menu.Trigger.Props) {
  return <Menu.Trigger {...props} className={cn(styles.trigger, className)} />;
}

const DropdownMenuPortal = Menu.Portal;

function DropdownMenuPositioner({
  className,
  ...props
}: Menu.Positioner.Props) {
  return (
    <Menu.Positioner
      className={cn(styles.positioner, className)}
      data-slot="menu-positioner"
      side="top"
      {...props}
    />
  );
}

function DropdownMenuPopup({ className, ...props }: Menu.Popup.Props) {
  return <Menu.Popup className={cn(styles.popup, className)} {...props} />;
}

interface DropdownMenuItemProps extends Menu.Item.Props {
  icon?: ReactNode;
  className?: string;
  children?: ReactNode;
  variant?: "default" | "destructive";
}

function DropdownMenuItem({
  className,
  icon,
  children,
  variant = "default",
  ...props
}: DropdownMenuItemProps) {
  return (
    <Menu.Item
      className={cn(styles.item, className)}
      data-variant={variant === "destructive" ? "destructive" : undefined}
      {...props}
    >
      {icon && <span className={styles.icon}>{icon}</span>}
      {children}
    </Menu.Item>
  );
}

function DropdownMenuSeparator({ className, ...props }: Menu.Separator.Props) {
  return (
    <div className={styles.seperatorWrapper}>
      <Menu.Separator className={cn(styles.separator, className)} {...props} />
    </div>
  );
}

function DropdownMenuArrow({ className, ...props }: Menu.Arrow.Props) {
  return <Menu.Arrow className={cn(styles.arrow, className)} {...props} />;
}

function DropdownMenuSubmenuRoot({ ...props }: Menu.SubmenuRoot.Props) {
  return <Menu.SubmenuRoot {...props} />;
}

function DropdownMenuSubmenuTrigger({
  className,
  children,
  ...props
}: Menu.SubmenuTrigger.Props) {
  return (
    <Menu.SubmenuTrigger
      className={cn(styles.submenuTrigger, className)}
      data-slot="menu-submenutrigger"
      {...props}
    >
      {children}
      <ChevronRight className={styles.submenuIcon} size={16} />
    </Menu.SubmenuTrigger>
  );
}

function DropdownMenuRadioGroup({
  className,
  ...props
}: Menu.RadioGroup.Props) {
  return <Menu.RadioGroup className={className} {...props} />;
}

interface DropdownMenuRadioItemProps extends Menu.RadioItem.Props {
  className?: string;
  children?: ReactNode;
}

function DropdownMenuRadioItem({
  className,
  children,
  ...props
}: DropdownMenuRadioItemProps) {
  return (
    <Menu.RadioItem className={cn(styles.item, className)} {...props}>
      {children}
    </Menu.RadioItem>
  );
}

function DropdownMenuRadioItemIndicator({
  className,
  ...props
}: Menu.RadioItemIndicator.Props) {
  return (
    <Menu.RadioItemIndicator
      className={cn(styles.radioIndicator, className)}
      {...props}
    />
  );
}

function DropdownMenuSpacer() {
  return <div style={{ height: "4px", width: "100%" }} />;
}

export {
  DropdownMenuRoot as DropdownMenu,
  DropdownMenuArrow,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuRadioItemIndicator,
  DropdownMenuSeparator,
  DropdownMenuSpacer,
  DropdownMenuSubmenuRoot,
  DropdownMenuSubmenuTrigger,
  DropdownMenuTrigger,
};

.root {
  display: inline-block;
}

.trigger {
  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &[data-popup-open] {
    background-color: var(--muted);
    color: var(--foreground);
  }

  @media (hover: hover) {
    &[data-popup-open]:hover {
      background-color: var(--muted);
      color: var(--foreground);
    }
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }
}

.positioner {
  z-index: 150;
  position: absolute;
  left: 0;
}

.popup {
  min-width: 170px;
  background-color: var(--popover);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  border-radius: var(--radius);
  display: flex;
  flex-direction: column;
  box-shadow: var(--shadow-border-stack);
  transform-origin: "top center";
  transition:
    transform 250ms var(--ease-out-expo),
    opacity 250ms var(--ease-out-expo);

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }
}

.seperatorWrapper {
  padding: 5px 0;
}

.separator {
  height: 1px;
  border-bottom: 0.5px solid oklch(from var(--border) l c h / 0.8);
}

.item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3125rem;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &:not(:has(.icon)) {
    padding-left: 12px;
  }

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: 0.3125rem;
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before,
  &[data-highlighted]::before,
  &:hover::before,
  &:focus::before {
    background-color: var(--accent);
  }

  &:focus,
  &:focus-visible {
    outline: none;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;

    &:hover {
      background: transparent;
    }
  }

  &:hover {
    & > .icon,
    & .icon {
      color: var(--secondary-foreground);
      opacity: 1;
    }
  }

  &[data-variant="destructive"] {
    color: var(--destructive);

    & .icon {
      color: var(--destructive);
    }

    &:hover,
    &[data-highlighted] {
      color: var(--destructive-foreground);

      &::before {
        background-color: var(--destructive);
      }

      & .icon {
        color: var(--destructive-foreground);
      }
    }
  }
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  margin-left: 4px;
}

.submenuTrigger {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding-inline: 8px 6px;
  font-weight: 400;
  height: 32px;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: 0.3rem;
  justify-content: flex-start;
  margin: 0rem;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0 4px;
    border-radius: 0.3rem;
    background-color: transparent;
    z-index: -1;
  }

  &[data-popup-open]::before,
  &[data-highlighted]::before,
  &:hover::before {
    background-color: oklch(from var(--accent) l c h / 0.7);
  }

  &:focus,
  &:focus-visible {
    outline: none;
  }

  &:hover {
    & .submenuIcon {
      color: var(--secondary-foreground);
    }
  }
}

.arrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}

.submenuIcon {
  margin-left: auto;
  height: 1rem;
  width: 1rem;
  color: var(--muted-foreground);
  opacity: 0.6;
}

.radioIndicator {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  margin-left: auto;
  flex-shrink: 0;
}
