"use client";
import { ExpandableCard, type ExpandableCardItem } from "@/registry/brook/ui/expandable-card/expandable-card";
import { Carousel } from "@/registry/brook/ui/carousel/carousel";
import data from "@/registry/brook/blocks-shared-files/expandable-card-carousel/data.json";
import styles from "./expandable-card-block.module.css";

const items: ExpandableCardItem[] = data.cards.map((card) => ({
  id: card.id,
  imageSrc: card.imageSrc,
  cardHeading: card.cardHeading,
  alt: card.alt,
  content: (
    <div className={styles.content}>
      {card.paragraphs.map((paragraph, index) => (
        // biome-ignore lint/suspicious/noArrayIndexKey: paragraphs are static
        <p key={index}>{paragraph}</p>
      ))}
    </div>
  ),
}));

export default function Page() {
  return (
    <div className={styles.wrapper}>
      <header className={styles.header}>
        <h2 className={styles.heading}>Our Three Core<br />Philosophies</h2>
        <p className={styles.description}>
          The principles that guide everything we create, from design decisions to the experiences we craft for our users.
        </p>
      </header>
      <Carousel.Bleed>
        <Carousel.Root align="center" gap={24} totalItems={items.length} variant="inset">
          <Carousel.Viewport>
            <Carousel.Content className={styles.carousel}>
              {items.map((item, index) => (
                <Carousel.Item index={index} key={item.id}>
                  <ExpandableCard item={item} />
                </Carousel.Item>
              ))}
            </Carousel.Content>
          </Carousel.Viewport>
        </Carousel.Root>
      </Carousel.Bleed>
    </div>
  );
}

.wrapper {
  width: 100%;
  max-width: 100%;
  padding: 24px 0;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 24px;
  margin-bottom: 32px;
  margin-left: auto;
  margin-right: auto;
  max-width: 1008px;
  width: 100%;
}

.heading {
  font-size: 3rem;
  font-weight: 500;
  color: var(--foreground);
  margin: 0;
  letter-spacing: -0.02em;
  line-height: 1.3;
}

.description {
  font-size: var(--font-size-sm);
  color: var(--muted-foreground);
  margin: 0;
  max-width: 360px;
  text-align: left;
}

.carousel {
  justify-content: center;
}

.content p {
  margin: 0 0 1rem 0;
}

.content p:last-child {
  margin-bottom: 0;
}

{
  "cards": [
    {
      "id": 1,
      "imageSrc": "/p-01.svg",
      "cardHeading": "Eudaimonia",
      "alt": "Figure contemplating under an archway at dusk",
      "paragraphs": [
        "Aristotle taught that eudaimonia, or human flourishing, is not found in fleeting pleasures or material wealth, but in living a life of virtue and purpose. It is the highest good, achieved through the cultivation of character and the exercise of reason.",
        "We build not for momentary delight, but for lasting excellence. Every interface, every interaction is an opportunity to help users flourish in their work and creative pursuits."
      ]
    },
    {
      "id": 2,
      "imageSrc": "/p-02.svg",
      "cardHeading": "Ataraxia",
      "alt": "Serene figure amidst classical columns at night",
      "paragraphs": [
        "The Stoics and Epicureans alike sought ataraxia: a state of serene calmness, undisturbed by the chaos of the world. It emerges not from avoidance, but from clarity of mind and freedom from unnecessary complexity.",
        "Our designs embrace this tranquility. We strip away the superfluous, leaving only what serves. In simplicity, we find peace; in clarity, we find power."
      ]
    },
    {
      "id": 3,
      "imageSrc": "/p-03.svg",
      "cardHeading": "Arete",
      "alt": "Wanderer on a path toward distant pillars",
      "paragraphs": [
        "Arete speaks to excellence: the fulfillment of purpose, the realization of potential. For the ancient Greeks, it meant living up to one's highest nature, whether as an athlete, a philosopher, or an artisan.",
        "We pursue arete in every pixel and every line of code. Not perfection for its own sake, but excellence in service of those who use what we create."
      ]
    }
  ]
}
