"use client";
import { Dialog } from "@base-ui/react/dialog";
import { ScrollArea } from "@base-ui/react/scroll-area";
import { Plus, X } from "lucide-react";
import { AnimatePresence, motion, useReducedMotion } from "motion/react";
import { useState } from "react";
import { cn } from "@/lib/utils";
import styles from "./expandable-card.module.css";

type ExpandableCardItem = {
  id: string | number;
  imageSrc: string;
  alt: string;
  cardHeading: string;
  content?: React.ReactNode;
};

type ExpandableCardProps = {
  item: ExpandableCardItem;
  className?: string;
};

function ExpandableCard({ item, className }: ExpandableCardProps) {
  const [isOpen, setIsOpen] = useState(false);
  const prefersReducedMotion = useReducedMotion();

  return (
    <div className={cn(styles.wrapper, className)}>
      <Dialog.Root onOpenChange={setIsOpen} open={isOpen}>
        <AnimatePresence>
          {isOpen ? (
            <Dialog.Backdrop
              hidden={undefined}
              key="overlay"
              render={
                <motion.div
                  animate={{
                    opacity: 1,
                  }}
                  className={styles.overlay}
                  exit={{ opacity: 0 }}
                  initial={{ opacity: 0 }}
                  transition={{
                    duration: prefersReducedMotion ? 0 : 0.25,
                    ease: [0.455, 0.03, 0.515, 0.955],
                  }}
                />
              }
            />
          ) : null}
        </AnimatePresence>
        <Dialog.Portal keepMounted>
          <AnimatePresence>
            {isOpen ? (
              <div className={styles.modalPositioner} key="positioner">
                <Dialog.Popup
                  hidden={undefined}
                  render={
                    <motion.div
                      className={styles.expandedCard}
                      layoutId={`card-${item.id}`}
                      style={{
                        borderRadius: "32px",
                        overflow: "hidden",
                      }}
                    />
                  }
                >
                  <ScrollArea.Root className={styles.scrollAreaRoot}>
                  <ScrollArea.Viewport className={styles.scrollAreaViewport}>
                    <ScrollArea.Content className={styles.scrollableContent}>
                      <div className={styles.closeButtonContainer}>
                        <Dialog.Close
                          aria-label="Close"
                          render={
                            <motion.button
                              animate={{ opacity: 1 }}
                              className={styles.closeButton}
                              exit={{ opacity: 0, display: "flex" }}
                              initial={{ opacity: prefersReducedMotion ? 1 : 0 }}
                              transition={{
                                type: "spring",
                                duration: prefersReducedMotion ? 0 : 0.3,
                                delay: prefersReducedMotion ? 0 : 0.1,
                              }}
                            />
                          }
                        >
                          <X height={21} strokeWidth={2} width={21} />
                        </Dialog.Close>
                      </div>

                      <motion.img
                        alt={item.alt}
                        className={styles.expandedImage}
                        height={600}
                        layoutId={`image-${item.id}`}
                        src={item.imageSrc}
                        style={{ borderRadius: "24px" }}
                        width={600}
                      />

                      <motion.div className={styles.contentExpanded}>
                        <motion.div layoutId={`heading-${item.id}`}>
                          <h3 className={styles.expandedHeading}>{item.cardHeading}</h3>
                        </motion.div>

                        <motion.div
                          animate={{ opacity: 1, y: 0, scale: 1 }}
                          className={styles.paragraphWrapper}
                          exit={{
                            opacity: 0,
                            display: "block",
                            y: prefersReducedMotion ? 0 : -40,
                            scale: prefersReducedMotion ? 1 : 0.92,
                          }}
                          initial={{
                            opacity: prefersReducedMotion ? 1 : 0,
                            y: prefersReducedMotion ? 0 : -40,
                            scale: prefersReducedMotion ? 1 : 0.92,
                          }}
                          transition={{
                            delay: prefersReducedMotion ? 0 : 0.1,
                            duration: prefersReducedMotion ? 0 : 0.3,
                            type: "spring",
                            bounce: 0,
                          }}
                        >
                          {item.content}
                        </motion.div>
                      </motion.div>
                    </ScrollArea.Content>
                  </ScrollArea.Viewport>
                  <ScrollArea.Scrollbar className={styles.scrollbar} orientation="vertical">
                    <ScrollArea.Thumb className={styles.scrollbarThumb} />
                  </ScrollArea.Scrollbar>
                </ScrollArea.Root>
                </Dialog.Popup>
              </div>
            ) : null}
          </AnimatePresence>
        </Dialog.Portal>
        <Dialog.Trigger
          render={
            <motion.button
              aria-label={`Expand ${item.cardHeading}`}
              className={styles.card}
              layoutId={`card-${item.id}`}
              style={{ borderRadius: "24px" }}
            />
          }
        >
          <motion.img
            alt={item.alt}
            className={styles.image}
            height={300}
            layoutId={`image-${item.id}`}
            src={item.imageSrc}
            style={{ borderRadius: "24px" }}
            width={300}
          />

          <div className={styles.contentContainer}>
            <motion.div layoutId={`heading-${item.id}`}>
              <h3 className={styles.heading}>{item.cardHeading}</h3>
            </motion.div>

            <motion.div className={styles.expandIcon}>
              <Plus height={21} strokeWidth={2} width={21} />
            </motion.div>
          </div>
        </Dialog.Trigger>
      </Dialog.Root>
    </div>
  );
}

export { ExpandableCard };
export type { ExpandableCardItem, ExpandableCardProps };

.wrapper {
  position: relative;
  width: fit-content;
  height: fit-content;
}

.overlay {
  position: fixed;
  min-height: 100dvh;
  z-index: 100;
  inset: 0;
  background-color: var(--background);
}

.card {
  all: unset;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 320px;
  box-shadow: inset 0 0 0 0.5px oklch(from var(--border) l c h / 0.7);
  overflow: hidden;
}

.card:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.image {
  width: 100%;
  height: 320px;
  object-fit: cover;
}

.heading {
  font-size: var(--font-size-2xl);
  font-weight: 500;
  color: var(--secondary-foreground);
  margin: 0;
  transition: color 150ms ease-out;
  line-height: 1.5;
  letter-spacing: -0.02em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.card:hover .heading {
  color: var(--foreground);
}

.modalPositioner {
  position: fixed;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  pointer-events: none;
  overflow-y: auto;
  max-height: 100%;
}

.expandedCard {
  position: fixed;
  top: 5vh;
  width: 100%;
  max-width: 960px;
  height: 100dvh;
  overflow: hidden;
  background-color: var(--mix-card-15-bg);
  box-shadow: inset 0 0 0 0.5px oklch(from var(--border) l c h / 0.6);
  padding: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
  pointer-events: auto;
  transition: none;
  animation: none;
  opacity: 1;
  transform: none;
  scrollbar-width: thin;
  scrollbar-color: var(--border) transparent;
}

.scrollAreaRoot {
  width: 100%;
  height: 100%;
  position: relative;
}

.scrollAreaViewport {
  width: 100%;
  height: 100%;
  overscroll-behavior: contain;
}

.scrollAreaViewport::before,
.scrollAreaViewport::after {
  content: "";
  display: block;
  left: 0;
  width: 100%;
  position: absolute;
  pointer-events: none;
  border-radius: 0.375rem;
  transition: height 100ms ease-out;
  z-index: 10;
}

.scrollAreaViewport::before {
  --scroll-area-overflow-y-start: inherit;
  top: 0;
  height: min(60px, var(--scroll-area-overflow-y-start));
  background: linear-gradient(to bottom, var(--background), transparent);
}

.scrollAreaViewport::after {
  --scroll-area-overflow-y-end: inherit;
  bottom: 0;
  height: min(10vh, var(--scroll-area-overflow-y-end, 10vh));
  background: linear-gradient(to top, var(--background) 0%, var(--background) 30%, transparent 100%);
}

.scrollableContent {
  width: 100%;
  padding: 24px;
  padding-top: max(0vh, env(safe-area-inset-top));
  padding-bottom: max(12vh, env(safe-area-inset-bottom));
  padding-left: max(24px, env(safe-area-inset-left));
  padding-right: max(24px, env(safe-area-inset-right));
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}

.scrollbar {
  display: flex;
  justify-content: center;
  width: 6px;
  border-radius: 3px;
  margin: 4px;
  margin-top: calc(32px + 0.5vh);
  margin-left: 0;
  touch-action: none;
  user-select: none;
  opacity: 0;
  transition: opacity 150ms ease-out 800ms;
  pointer-events: none;
}

.scrollbar[data-scrolling] {
  opacity: 1;
  transition: opacity 150ms ease-out 0ms;
  pointer-events: auto;
}

.scrollbarThumb {
  width: 100%;
  border-radius: inherit;
  background-color: oklch(from var(--border) l c h / 0.5);
  transition: background-color 150ms ease-out;
}

.scrollbarThumb:hover {
  background-color: oklch(from var(--border) l c h / 0.8);
}

.contentExpanded {
  max-width: 700px;
  line-height: 2;
  margin: 0 auto;
  padding-top: 28px;
  padding-left: 0;
  padding-right: 0;
  padding-bottom: 0;
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 36px;
  text-align: left;
}

.expandedImage {
  width: 100%;
  max-width: 700px;
  height: auto;
  object-fit: contain;
}

.expandedHeading {
  font-size: 48px;
  font-weight: 500;
  color: var(--foreground);
  margin: 0;
  align-self: flex-start;
  width: 100%;
  line-height: 1.5;
  letter-spacing: -0.02em;
}

.paragraphWrapper {
  color: oklch(from var(--secondary-foreground) l c h / 0.8);
}

.closeButtonContainer {
  position: sticky;
  align-self: flex-end;
  width: 44px;
  height: 44px;
  top: 32px;
  border-radius: 50%;
  z-index: 20;

  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
}

.closeButton {
  padding: 0.5rem;
  border-radius: 999px;
  box-shadow: inset 0 0 0 0.5px oklch(from var(--border) l c h / 0.7);
  z-index: 20;
  background: transparent;
  color: var(--muted-foreground);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition:
    background-color 150ms ease-out,
    color 150ms ease-out;
}

.closeButton:hover {
  color: var(--foreground);
  background-color: var(--muted);
}

.closeButton:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.contentContainer {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 16px;
}

.expandIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  width: 36px;
  height: 36px;
  min-width: 36px;
  min-height: 36px;
  border-radius: 50%;
  box-shadow: inset 0 0 0 0.5px oklch(from var(--border) l c h / 0.7);
  margin-left: auto;
  flex-shrink: 0;
  transition:
    background-color 150ms ease-out,
    color 150ms ease-out;
}
.expandIcon:hover,
.card:hover .expandIcon {
  background-color: var(--card);
  color: var(--foreground);
}

@media (max-width: 768px) {
  .card {
    max-width: 300px;
  }
  .expandedHeading {
    font-size: 32px;
  }
  .expandedContent {
    font-size: 14px;
  }

  .scrollableContent {
    padding: 0px 16px;
    padding-left: max(16px, env(safe-area-inset-left));
    padding-right: max(16px, env(safe-area-inset-right));
    padding-bottom: max(8vh, env(safe-area-inset-bottom));
    justify-content: space-between;
    gap: 0px;
  }

  .closeButton {
    background-color: var(--background);
  }
}

@media (prefers-reduced-motion: reduce) {
  .card,
  .closeButton,
  .expandIcon,
  .heading,
  .scrollbar,
  .scrollbarThumb {
    transition: none;
  }
}
