"use client";
import { Dialog } from "@base-ui-components/react/dialog";
import { Plus, X } from "lucide-react";
import { AnimatePresence, motion } from "motion/react";
import { useState } from "react";
import styles from "./expandable-card.module.css";

type ExpandableCardItem = {
  id: string | number;
  imageSrc: string;
  alt: string;
  cardHeading: string;
  content?: React.ReactNode;
};

type ExpandableCardProps = {
  item: ExpandableCardItem;
};

export default function ExpandableCard({ item }: ExpandableCardProps) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className={styles.wrapper}>
      <Dialog.Root onOpenChange={setIsOpen} open={isOpen}>
        {isOpen && (
          <Dialog.Backdrop
            className={styles.overlay}
            key="overlay"
            render={
              <motion.div
                animate={{
                  opacity: 1,
                }}
                className={styles.overlay}
                exit={{ opacity: 0, background: "red" }}
                initial={{ opacity: 0 }}
                transition={{
                  delay: 0.1,
                  duration: 0.17,
                  // biome-ignore lint/style/noMagicNumbers: cubic-bezier easing values
                  ease: [0.455, 0.03, 0.515, 0.955],
                }}
              />
            }
          />
        )}

        <Dialog.Portal keepMounted>
          <AnimatePresence mode="popLayout">
            {isOpen && (
              <div className={styles.modalPositioner} key="positioner">
                <Dialog.Popup
                  render={
                    <motion.div
                      className={styles.expandedCard}
                      layoutId={`card-${item.id}`}
                      style={{
                        borderRadius: "32px",
                        overflow: "hidden",
                      }}
                    />
                  }
                >
                  <div className={styles.scrollableContent}>
                    <div className={styles.closeButtonContainer}>
                      <Dialog.Close
                        aria-label="Close"
                        className={styles.closeButton}
                        render={
                          <motion.button
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            initial={{ opacity: 0 }}
                            transition={{
                              type: "spring",
                              duration: 0.2,
                              delay: 0.1,
                            }}
                          />
                        }
                      >
                        <X height={21} strokeWidth={2} width={21} />
                      </Dialog.Close>
                    </div>

                    <motion.img
                      alt={item.alt}
                      className={styles.expandedImage}
                      height={600}
                      layoutId={`image-${item.id}`}
                      src={item.imageSrc}
                      style={{ borderRadius: "16px" }}
                      width={600}
                    />

                    <motion.div className={styles.contentExpanded}>
                      <motion.div layoutId={`heading-${item.id}`}>
                        <motion.h3 className={styles.expandedHeading}>
                          {item.cardHeading}
                        </motion.h3>
                      </motion.div>

                      <motion.div
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        className={styles.expandedContent}
                        exit={{ opacity: 0, y: -20, scale: 0.95 }}
                        initial={{ opacity: 0, y: -20, scale: 0.95 }}
                      >
                        {item.content}
                      </motion.div>
                    </motion.div>
                  </div>
                </Dialog.Popup>
              </div>
            )}
          </AnimatePresence>
        </Dialog.Portal>

        <Dialog.Trigger
          render={
            <motion.button
              className={styles.card}
              layoutId={`card-${item.id}`}
              style={{ borderRadius: "24px" }}
            />
          }
        >
          <motion.img
            alt={item.alt}
            className={styles.image}
            height={300}
            layoutId={`image-${item.id}`}
            src={item.imageSrc}
            style={{ borderRadius: "12px" }}
            width={300}
          />

          <div className={styles.contentContainer}>
            <motion.div layoutId={`heading-${item.id}`}>
              <motion.h3 className={styles.heading}>
                {item.cardHeading}
              </motion.h3>
            </motion.div>

            <motion.div className={styles.expandIcon}>
              <Plus height={21} strokeWidth={2} width={21} />
            </motion.div>
          </div>
        </Dialog.Trigger>
      </Dialog.Root>
    </div>
  );
}

export type { ExpandableCardItem };

.wrapper {
  position: relative;
  width: fit-content;
  height: fit-content;
}

.overlay {
  position: fixed;
  inset: 0;
  background-color: var(--background);
  z-index: 9998;
  pointer-events: auto;
}

.card {
  all: unset;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 320px;
  border: 1px solid var(--border);
  overflow: hidden;
}

.card:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.image {
  width: 100%;
  height: 320px;
  object-fit: cover;
}

.heading {
  font-size: var(--font-size-2xl);
  font-weight: 600;
  color: var(--secondary-foreground);
  margin: 0;
  transition: 150ms ease-out;
}

.card:hover .heading {
  color: var(--foreground);
}

.modalPositioner {
  position: fixed;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  pointer-events: none;
  overflow-y: auto;
  max-height: 100%;
}

.expandedCard {
  position: relative;
  top: 5vh;
  width: 100%;
  max-width: 900px;
  max-height: 100dvh;
  overflow: hidden;
  background-color: var(--mix-card-15-bg);
  border: 1px solid var(--border-dark);
  padding: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
  pointer-events: auto;
  transition: none;
  animation: none;
  opacity: 1;
  transform: none;
  scrollbar-width: thin;
  scrollbar-color: var(--border) transparent;
}

.scrollableContent {
  width: 100%;
  height: 100%;
  position: relative;
  overflow-y: auto;
  padding: 24px;
  padding-top: 0vh;
  padding-bottom: 8vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
  scrollbar-width: thin;
  scrollbar-color: var(--border) transparent;
  mask-image: linear-gradient(to bottom, #000 0%, #000 91%, transparent 100%);
  -webkit-mask-image: linear-gradient(
    to bottom,
    #000 0%,
    #000 91%,
    transparent 100%
  );
}

.scrollableContent::-webkit-scrollbar {
  width: 8px;
}

.scrollableContent::-webkit-scrollbar-track {
  background: transparent;
}

.scrollableContent::-webkit-scrollbar-thumb {
  background-color: var(--border-dark);
  border-radius: 4px;
}

.scrollableContent::-webkit-scrollbar-thumb:hover {
  background-color: var(--muted-foreground);
}

.contentExpanded {
  max-width: 600px;
  margin: 0 auto;
  padding-top: 28px;
  padding-left: 0;
  padding-right: 0;
  padding-bottom: 0;
  width: 100%;
  height: auto;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 36px;
  text-align: left;
}

.expandedImage {
  width: 100%;
  max-width: 600px;
  height: auto;
  object-fit: contain;
}

.expandedHeading {
  font-size: 48px;
  font-weight: 600;
  color: var(--title);
  margin: 0;
  align-self: flex-start;
  width: 100%;
}

.expandedContent {
  color: var(--foreground);
}

.closeButtonContainer {
  position: sticky;
  align-self: flex-end;
  width: 44px;
  height: 44px;
  top: 32px;
  border-radius: 50%;
  z-index: 20;

  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
}

.closeButton {
  width: 32px;
  height: 32px;
  border-radius: 999px;
  border: 1px solid var(--border);
  z-index: 20;
  background: transparent;
  color: var(--muted-foreground);
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: 150ms ease-out;
}

.closeButton:hover {
  color: var(--foreground);
  background-color: var(--muted);
}

.contentContainer {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 16px;
}

.expandIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--muted-foreground);
  width: 36px;
  height: 36px;
  min-width: 36px;
  min-height: 36px;
  border-radius: 50%;
  border: 1px solid var(--border-dark);
  margin-left: auto;
  flex-shrink: 0;
  transition: 150ms ease-out;
}
.expandIcon:hover,
.card:hover .expandIcon {
  background-color: var(--card);
  color: var(--foreground);
}

@media (max-width: 768px) {
  .card {
    max-width: 300px;
  }
  .expandedHeading {
    font-size: 32px;
  }
  .expandedContent {
    font-size: 14px;
  }
}
