"use client";

import { Field } from "@base-ui-components/react/field";
import { cn } from "@/lib/utils";
import styles from "./field.module.css";

function FieldRoot({
  className,
  ...props
}: Field.Root.Props) {
  return (
    <Field.Root
      data-slot="field-root"
      className={cn(styles.root, className)}
      validationMode="onBlur"
      {...props}
    />
  );
}

function FieldControl({
  className,
  ...props
}: Field.Control.Props) {
  return <Field.Control className={cn(styles.control, className)} {...props} />;
}

function FieldLabel({
  className,
  ...props
}: Field.Label.Props) {
  return <Field.Label className={cn(styles.label, className)} {...props} />;
}

function FieldDescription({
  className,
  ...props
}: Field.Description.Props) {
  return (
    <Field.Description
      data-slot="field-description"
      className={cn(styles.description, className)}
      {...props}
    />
  );
}

function FieldError({
  className,
  ...props
}: Field.Error.Props) {
  return <Field.Error className={cn(styles.error, className)} {...props} />;
}

export {
  FieldRoot as Field,
  FieldControl,
  FieldDescription,
  FieldError,
  FieldLabel,
};

.root {
  display: flex;
  flex-direction: column;
  align-items: start;
  gap: 0.5rem;
  width: 100%;
}

.control {
  box-sizing: border-box;
  padding: 0 0.875rem;
  margin: 0;
  border: 1px solid var(--border);
  width: 100%;
  height: 2.5rem;
  border-radius: var(--radius);
  font-family: inherit;
  font-size: 0.875rem;
  background-color: var(--mix-card-50-bg);
  color: var(--foreground);

  &::placeholder {
    color: var(--muted-foreground);
    font-size: 0.875rem;
  }

  &:focus {
    outline: 2px solid var(--ring);
    outline-offset: -1px;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }

  &[data-invalid] {
    border-color: var(--destructive);

    &:focus {
      outline: 2px solid var(--destructive);
      outline-offset: -1px;
    }
  }
}

.label {
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-weight: 400;
  color: var(--foreground);

  &[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
  }
}

.description {
  margin: 0;
  font-size: 0.875rem;
  line-height: 1.25rem;
  color: var(--muted-foreground);
}

.error {
  font-size: 0.875rem;
  line-height: 1.25rem;
  color: var(--destructive);
}
