"use client";

import { Field } from "@base-ui-components/react/field";
import { cn } from "@/lib/utils";
import styles from "./field.module.css";

function FieldRoot({ className, ...props }: React.ComponentProps<typeof Field.Root>) {
  return <Field.Root className={cn(styles.root, className)} validationMode="onBlur" {...props} />;
}

function FieldControl({ className, ...props }: React.ComponentProps<typeof Field.Control>) {
  return <Field.Control className={cn(styles.control, className)} {...props} />;
}

function FieldLabel({ className, ...props }: React.ComponentProps<typeof Field.Label>) {
  return <Field.Label className={cn(styles.label, className)} {...props} />;
}

function FieldDescription({
  className,
  ...props
}: React.ComponentProps<typeof Field.Description>) {
  return <Field.Description className={cn(styles.description, className)} {...props} />;
}

function FieldError({ className, ...props }: React.ComponentProps<typeof Field.Error>) {
  return <Field.Error className={cn(styles.error, className)} {...props} />;
}

export { FieldRoot as Field, FieldControl, FieldLabel, FieldDescription, FieldError };

.root {
    display: flex;
    flex-direction: column;
    align-items: start;
    gap: 0.5rem;
    width: 100%;
}

.control {
    box-sizing: border-box;
    padding: 0 0.875rem;
    margin: 0;
    border: 1px solid var(--border);
    width: 100%;
    height: 2.5rem;
    border-radius: var(--radius);
    font-family: inherit;
    font-size: 0.875rem;
    background-color: color-mix(in oklab, var(--input) 70%, transparent);
    color: var(--foreground);
}

.control::placeholder {
    color: var(--muted-foreground);
    font-size: 0.875rem;
}

.control:focus {
    outline: 2px solid var(--ring);
    outline-offset: -1px;
}

.control[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
}

.control[data-invalid] {
    border-color: var(--destructive);
}

.control[data-invalid]:focus {
    outline: 2px solid var(--destructive);
    outline-offset: -1px;
}


.label {
    font-size: 0.875rem;
    line-height: 1.25rem;
    font-weight: 400;
    color: var(--foreground);
}

.label[data-disabled] {
    opacity: 0.5;
    cursor: not-allowed;
}

.description {
    margin: 0;
    font-size: 0.875rem;
    line-height: 1.25rem;
    color: var(--muted-foreground);
}

.error {
    font-size: 0.875rem;
    line-height: 1.25rem;
    color: var(--destructive);
}
