"use client";

import { Input } from "@base-ui/react/input";
import { cn } from "@/lib/utils";
import styles from "./input.module.css";

interface InputProps extends Input.Props {
  variant?: "default" | "error";
}

function InputRoot({ className, variant = "default", ...props }: InputProps) {
  return (
    <Input
      className={(state: Input.State) =>
        cn(styles.base, styles[variant], typeof className === "function" ? className(state) : className)
      }
      {...props}
    />
  );
}

export { InputRoot as Input };

.base {
  display: flex;
  height: 2.5rem;
  width: 100%;
  border-radius: var(--radius);
  border: none;
  background-color: var(--popover);
  padding: 0 0.75rem;
  font-size: 0.875rem;
  transition: all 150ms ease-out;
  color: var(--foreground);
  box-shadow: 0 0 0 1px oklch(from var(--border) l c h / 0.5);
}

.base::placeholder {
  color: var(--muted-foreground);
  font-size: 0.875rem;
}

.base:focus,
.base:focus-visible,
.base[data-focused] {
  outline: none;
  box-shadow:
    0 0 0 1px var(--ring),
    0 0 0 3px oklch(from var(--ring) l c h / 0.2);
}

.base[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.base[data-invalid] {
  box-shadow: 0 0 0 1px var(--destructive);
}

.base[data-invalid]:focus,
.base[data-invalid][data-focused] {
  box-shadow:
    0 0 0 1px var(--destructive),
    0 0 0 3px oklch(from var(--destructive) l c h / 0.2);
}

.base[data-valid] {
  box-shadow: 0 0 0 1px var(--success, var(--primary));
}

.error {
  box-shadow: 0 0 0 1px var(--destructive);
}
