"use client";

import { Input } from "@base-ui-components/react/input";
import { cn } from "@/lib/utils";
import styles from "./input.module.css";

interface InputProps extends React.ComponentProps<typeof Input> {
  variant?: "default" | "error";
}

const InputRoot = ({ className, variant = "default", ...props }: InputProps) => (
  <Input className={cn(styles.base, styles[variant], className)} {...props} />
);

export { InputRoot as Input };

.base {
    display: flex;
    height: 2.5rem;
    width: 100%;
    border-radius: var(--radius);
    border: 1px solid var(--border);
    background-color: var(--input);
    padding: 0 0.75rem;
    font-size: 0.875rem;
    transition: all 150ms ease-out;
    color: var(--foreground);
}

.base::placeholder {
    color: var(--muted-foreground);
    font-size: 0.875rem;
}

.base[data-focused] {
    outline: none;
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
}

.base[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
}

.base[data-invalid] {
    border-color: var(--destructive);
}

.base[data-invalid]:focus,
.base[data-invalid][data-focused] {
    border-color: var(--destructive);
    box-shadow: 0 0 0 2px var(--destructive) / 0.2;
}

.base[data-valid] {
    border-color: var(--success, var(--primary));
}

.error {
    border-color: var(--destructive);
}
