import { cva, type VariantProps } from "class-variance-authority";
import { Command } from "lucide-react";
import { cn } from "@/lib/utils";
import styles from "./kbd.module.css";

const kbdVariants = cva(styles.kbd, {
  variants: {
    size: {
      sm: styles.sm,
      md: styles.md,
      lg: styles.lg,
    },
  },
  defaultVariants: {
    size: "md",
  },
});

/**
 * Kbd component for displaying keyboard shortcuts
 *
 * @example
 * ```tsx
 * <Kbd>⌘</Kbd>
 * <Kbd>K</Kbd>
 * ```
 *
 * @example
 * ```tsx
 * <Kbd size="sm">Ctrl</Kbd>
 * <Kbd size="lg">Enter</Kbd>
 * ```
 */
function Kbd({ className, size, children, ...props }: React.ComponentProps<"kbd"> & VariantProps<typeof kbdVariants>) {
  const isCommandKey = typeof children === "string" && (children === "⌘" || children.toLowerCase() === "ctrl");

  const sizeMap = {
    sm: 11,
    md: 13,
    lg: 15,
  };

  const iconSize = sizeMap[size || "md"];

  return (
    <kbd className={cn(kbdVariants({ size }), isCommandKey && styles.commandKey, className)} data-slot="kbd" {...props}>
      {isCommandKey ? <Command size={iconSize} strokeWidth={2.5} /> : children}
    </kbd>
  );
}

export { Kbd };

.kbd {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-family:
    ui-monospace, SFMono-Regular, "SF Mono", Menlo, Monaco, Consolas,
    "Liberation Mono", "Courier New", monospace;
  font-weight: 550;
  line-height: 1;
  white-space: nowrap;
  border: 1px solid var(--mix-card-6-trans);
  border-bottom: 3px solid oklch(from var(--foreground) l c h / 0.06);
  background: var(--card);
  box-shadow:
    0 0.753698px 0.452219px -0.583333px rgba(0, 0, 0, 0.01),
    0 1.927px 1.1562px -1.16667px rgba(0, 0, 0, 0.025),
    0 3.86321px 2.31793px -1.75px rgba(0, 0, 0, 0.02);
  color: var(--foreground);
  transition: all 150ms ease;
}

.commandKey {
  font-size: 1.15em;
}

.kbd:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

/* Sizes */
.sm {
  min-width: 1.25rem;
  height: 1.25rem;
  padding: 0 0.25rem;
  font-size: 0.625rem;
  border-radius: calc(var(--radius) * 0.75);
}

.md {
  min-width: 1.5rem;
  height: 1.5rem;
  padding: 0 0.375rem;
  font-size: 0.75rem;
  border-radius: 2px;
}

.lg {
  min-width: 2rem;
  height: 2rem;
  padding: 0 0.5rem;
  font-size: 0.875rem;
  border-radius: var(--radius-lg);
}
