"use client";

interface PayloadEntry {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
}

export interface ChartTooltipProps {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
}

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!active || !payload || !payload.length) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && <div style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}>{formatLabel(label)}</div>}
      {payload.map((entry, index) => (
        <div
          key={index}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>{formatName(entry.name || entry.dataKey || "")}</span>
          </div>
          <span
            style={{
              fontFamily: "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import {
  LineChart as RechartsLineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Dot,
} from "recharts";
import ChartTooltip from "./chart-tooltip";
import styles from "./line-chart.module.css";

type NumberValue = number | { valueOf(): number };

export interface LineChartData {
  date: NumberValue;
  value: number;
  type: string;
}

export type CurveType = "linear" | "monotoneX" | "cardinal" | "basis" | "step";

export interface LineChartProps {
  data: LineChartData[];
  showXAxis?: boolean;
  showYAxis?: boolean;
  showXGrid?: boolean;
  showYGrid?: boolean;
  curve?: CurveType;
  showPoints?: boolean;
  pointSize?: number;
  xAxisFormatter?: (value: NumberValue | string) => string;
  animated?: boolean;
  ticks?: number[];
}

function LineChart({
  data,
  showXAxis = true,
  showYAxis = true,
  showXGrid = true,
  showYGrid = true,
  showPoints = false,
  pointSize = 4,
  xAxisFormatter,
  animated = false,
  ticks,
}: LineChartProps) {
  const uniqueTypes = Array.from(new Set(data.map((d) => d.type)));
  const colors = ["var(--chart1)", "var(--chart2)"];

  const formatDate = (value: NumberValue | string) => {
    if (xAxisFormatter) {
      return xAxisFormatter(value);
    }
    return Math.round(Number(value)).toString();
  };

  const tooltipLabelFormatter = (value: NumberValue | string) => formatDate(value);

  interface DotProps {
    cx?: number;
    cy?: number;
    payload?: LineChartData;
    [key: string]: unknown;
  }

  const CustomDot = (props: DotProps) => {
    const { cx, cy, payload } = props;
    if (showPoints && cx !== undefined && cy !== undefined && payload) {
      return (
        <Dot
          cx={cx}
          cy={cy}
          r={pointSize}
          fill={colors[uniqueTypes.indexOf(payload.type)]}
          stroke="var(--background)"
          strokeWidth={2}
        />
      );
    }
    return null;
  };

  interface GroupedDataItem {
    date: NumberValue;
    [key: string]: NumberValue | number;
  }

  const groupedData = data.reduce((acc: GroupedDataItem[], curr) => {
    const existingDate = acc.find((item) => item.date === curr.date);
    if (existingDate) {
      existingDate[curr.type] = curr.value;
    } else {
      acc.push({
        date: curr.date,
        [curr.type]: curr.value,
      });
    }
    return acc;
  }, []);

  const dates = data.map((d) => Number(d.date));
  const minDate = Math.min(...dates);
  const maxDate = Math.max(...dates);

  const padding = (maxDate - minDate) * 0.05;
  const domainMin = Math.max(0, minDate - padding);
  const domainMax = maxDate + padding;

  return (
    <div
      className={styles.lineChart}
      style={{
        position: "relative",
        width: "100%",
        height: "100%",
      }}
    >
      <ResponsiveContainer width="100%" height="100%">
        <RechartsLineChart data={groupedData} margin={{ top: 10, right: 12, left: 12, bottom: 20 }}>
          {showXGrid ||
            (showYGrid && <CartesianGrid strokeDasharray="3 3" stroke="var(--border)" strokeOpacity={0.2} />)}
          <XAxis
            dataKey="date"
            axisLine={false}
            tickLine={false}
            tick={showXAxis ? { fontSize: 11, fill: "var(--muted-foreground)" } : false}
            tickFormatter={formatDate}
            ticks={ticks}
            domain={[domainMin, domainMax]}
            hide={!showXAxis}
          />
          {showYAxis && (
            <YAxis
              axisLine={false}
              tickLine={false}
              tick={{ fontSize: 11, fill: "var(--muted-foreground)" }}
              width={30}
            />
          )}
          <Tooltip
            cursor={{ stroke: "var(--secondary)", strokeWidth: 1 }}
            content={<ChartTooltip labelFormatter={tooltipLabelFormatter} />}
          />
          {uniqueTypes.map((type, index) => (
            <Line
              key={type}
              type="monotone"
              dataKey={type}
              stroke={colors[index % colors.length]}
              strokeWidth={3}
              dot={showPoints ? <CustomDot /> : false}
              animationDuration={animated ? 800 : 0}
              animationBegin={animated ? index * 200 : undefined}
            />
          ))}
        </RechartsLineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default LineChart;

.lineChart {
    position: relative;
    width: 100%;
    height: 100%;
}

.lineChart *:focus,
.lineChart *:focus-visible {
    outline: none !important;
}
