"use client";

import { Menubar } from "@base-ui-components/react/menubar";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuArrow,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuSubmenuRoot,
  DropdownMenuSubmenuTrigger,
  DropdownMenuTrigger,
} from "../dropdown-menu/dropdown-menu";
import styles from "./menubar.module.css";

function MenubarRoot({
  className,
  ...props
}: React.ComponentProps<typeof Menubar>) {
  return <Menubar className={cn(styles.root, className)} {...props} />;
}

function MenubarMenuRoot({
  ...props
}: React.ComponentProps<typeof DropdownMenu>) {
  return <DropdownMenu {...props} />;
}

function MenubarMenuTrigger({
  ...props
}: React.ComponentProps<typeof DropdownMenuTrigger>) {
  return <DropdownMenuTrigger {...props} />;
}

const MenubarMenuPortal = DropdownMenuPortal;

function MenubarMenuPositioner({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuPositioner>) {
  return (
    <DropdownMenuPositioner
      align="start"
      className={cn(styles.menuPositioner, className)}
      side="bottom"
      {...props}
    />
  );
}

function MenubarMenuPopup({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuPopup>) {
  return (
    <DropdownMenuPopup className={cn(styles.menuPopup, className)} {...props} />
  );
}

interface MenubarMenuItemProps
  extends React.ComponentProps<typeof DropdownMenuItem> {
  icon?: ReactNode;
}

function MenubarMenuItem({
  className,
  icon,
  children,
  ...props
}: MenubarMenuItemProps) {
  return (
    <DropdownMenuItem
      className={cn(styles.menuItem, className)}
      icon={icon}
      {...props}
    >
      {children}
    </DropdownMenuItem>
  );
}

function MenubarMenuSeparator({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuSeparator>) {
  return (
    <DropdownMenuSeparator
      className={cn(styles.menuSeparator, className)}
      {...props}
    />
  );
}

function MenubarMenuArrow({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuArrow>) {
  return (
    <DropdownMenuArrow className={cn(styles.menuArrow, className)} {...props} />
  );
}

function MenubarMenuSubmenuRoot({
  ...props
}: React.ComponentProps<typeof DropdownMenuSubmenuRoot>) {
  return <DropdownMenuSubmenuRoot {...props} />;
}

function MenubarMenuSubmenuTrigger({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuSubmenuTrigger>) {
  return (
    <DropdownMenuSubmenuTrigger
      className={cn(styles.menuSubmenuTrigger, className)}
      {...props}
    />
  );
}

export {
  MenubarRoot as Menubar,
  MenubarMenuRoot as MenubarMenu,
  MenubarMenuArrow,
  MenubarMenuItem,
  MenubarMenuPopup,
  MenubarMenuPortal,
  MenubarMenuPositioner,
  MenubarMenuSeparator,
  MenubarMenuSubmenuRoot,
  MenubarMenuSubmenuTrigger,
  MenubarMenuTrigger,
};

.root {
  display: flex;
  align-items: center;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 0.25rem;
  gap: 0.25rem;
}

.menuRoot {
  display: inline-block;
}

.menuTrigger {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem 0.75rem;
  background: transparent;
  border: none;
  border-radius: calc(var(--radius) - 2px);
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
  cursor: pointer;
  transition: all 0.2s ease;
}

.menuTrigger:hover {
  background: var(--muted);
  color: var(--foreground);
}

.menuTrigger:focus {
  outline: none;
  background: var(--muted);
}

.menuTrigger:focus-visible {
  outline: 2px solid var(--ring);
  outline-offset: 2px;
}

.menuTrigger[data-state="open"] {
  background: var(--muted);
  color: var(--foreground);
}

.menuTrigger[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.menuPositioner {
  z-index: 150;
  position: absolute;
  left: 0;
}

.menuPopup {
  min-width: 200px;
  padding: 0.5rem;
  background: var(--mix-card-33-bg);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  box-shadow:
    0 20px 25px -5px rgba(0, 0, 0, 0.1),
    0 10px 10px -5px rgba(0, 0, 0, 0.04);
  animation: popup-in 0.15s ease-out;
  display: flex;
  flex-direction: column;
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

.menuPopup[data-starting-style] {
  opacity: 0;
  transform: scale(0.95) translateY(-0.25rem);
}

.menuPopup[data-ending-style] {
  opacity: 0;
  transform: scale(0.95) translateY(-0.25rem);
}

@keyframes popup-in {
  from {
    opacity: 0;
    transform: scale(0.95) translateY(-0.25rem);
  }
  to {
    opacity: 1;
    transform: scale(1) translateY(0);
  }
}

.menuItem {
  display: flex;
  align-items: center;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  line-height: 1.2;
  color: var(--foreground);
  cursor: pointer;
  border-radius: calc(var(--radius) - 2px);
  transition: all 0.2s cubic-bezier(0.25, 0.1, 0.25, 1);
  justify-content: flex-start;
  margin: 0;
}

.menuItemIcon {
  margin-right: 0.75rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  opacity: 0.8;
  transition: opacity 0.2s ease;
}

.menuItem:focus {
  outline: none;
}

.menuItem[data-disabled] {
  cursor: not-allowed;
  opacity: 0.5;
}

.menuItem[data-disabled]:hover {
  background: transparent;
  color: var(--foreground);
}

.menuSeparator {
  height: 1px;
  margin: 0.25rem 0.75rem;
  background: var(--border);
  opacity: 0.6;
}

.menuArrow {
  fill: var(--background);
  stroke: var(--border);
  stroke-width: 1px;
}

.menuSubmenuTrigger {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  color: var(--foreground);
  cursor: pointer;
  border-radius: calc(var(--radius) - 2px);
  transition: all 0.15s ease;
}

.menuSubmenuTrigger:hover {
  background: var(--muted);
  color: var(--foreground);
}

.menuSubmenuTrigger:focus {
  outline: none;
  background: var(--muted);
  color: var(--foreground);
}

.menuSubmenuTrigger[data-state="open"] {
  background: var(--muted);
  color: var(--foreground);
}

.menuSubmenuTrigger::after {
  content: "â€º";
  margin-left: auto;
  font-size: 1rem;
}
