"use client";

import { Menubar } from "@base-ui-components/react/menubar";
import type { ReactNode } from "react";
import { cn } from "@/lib/utils";
import {
  DropdownMenu,
  DropdownMenuArrow,
  DropdownMenuItem,
  DropdownMenuPopup,
  DropdownMenuPortal,
  DropdownMenuPositioner,
  DropdownMenuSeparator,
  DropdownMenuSubmenuRoot,
  DropdownMenuSubmenuTrigger,
  DropdownMenuTrigger,
} from "../dropdown-menu/dropdown-menu";
import styles from "./menubar.module.css";

function MenubarRoot({
  className,
  ...props
}: React.ComponentProps<typeof Menubar>) {
  return <Menubar className={cn(styles.root, className)} {...props} />;
}

function MenubarMenuRoot({
  ...props
}: React.ComponentProps<typeof DropdownMenu>) {
  return <DropdownMenu {...props} />;
}

function MenubarMenuTrigger({
  ...props
}: React.ComponentProps<typeof DropdownMenuTrigger>) {
  return <DropdownMenuTrigger {...props} />;
}

const MenubarMenuPortal = DropdownMenuPortal;

function MenubarMenuPositioner({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuPositioner>) {
  return (
    <DropdownMenuPositioner
      align="start"
      className={cn(styles.menuPositioner, className)}
      side="bottom"
      {...props}
    />
  );
}

function MenubarMenuPopup({
  className,
  ...props
}: React.ComponentProps<typeof DropdownMenuPopup>) {
  return (
    <DropdownMenuPopup className={cn(styles.menuPopup, className)} {...props} />
  );
}

interface MenubarMenuItemProps
  extends React.ComponentProps<typeof DropdownMenuItem> {
  icon?: ReactNode;
}

function MenubarMenuItem({
  className,
  icon,
  children,
  ...props
}: MenubarMenuItemProps) {
  return (
    <DropdownMenuItem
      className={className}
      icon={icon}
      style={icon ? undefined : { paddingLeft: "12px" }}
      {...props}
    >
      {children}
    </DropdownMenuItem>
  );
}

function MenubarMenuSeparator({
  ...props
}: React.ComponentProps<typeof DropdownMenuSeparator>) {
  return <DropdownMenuSeparator {...props} />;
}

function MenubarMenuArrow({
  ...props
}: React.ComponentProps<typeof DropdownMenuArrow>) {
  return <DropdownMenuArrow {...props} />;
}

function MenubarMenuSubmenuRoot({
  ...props
}: React.ComponentProps<typeof DropdownMenuSubmenuRoot>) {
  return <DropdownMenuSubmenuRoot {...props} />;
}

function MenubarMenuSubmenuTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof DropdownMenuSubmenuTrigger>) {
  // Check if children contains an icon element (checking for common icon props)
  const hasIcon =
    typeof children === "object" &&
    children !== null &&
    "type" in children &&
    typeof children.type === "function";

  return (
    <DropdownMenuSubmenuTrigger
      className={className}
      style={hasIcon ? undefined : { paddingLeft: "12px" }}
      {...props}
    >
      {children}
    </DropdownMenuSubmenuTrigger>
  );
}

function MenubarMenuShortcut({
  className,
  ...props
}: React.HTMLAttributes<HTMLSpanElement>) {
  return (
    <span
      className={cn(styles.shortcut, className)}
      {...props}
    />
  );
}

function MenubarMenuSpacer() {
  return <div style={{ height: "4px", width: "100%" }} />;
}

export {
  MenubarRoot as Menubar,
  MenubarMenuRoot as MenubarMenu,
  MenubarMenuArrow,
  MenubarMenuItem,
  MenubarMenuPopup,
  MenubarMenuPortal,
  MenubarMenuPositioner,
  MenubarMenuSeparator,
  MenubarMenuShortcut,
  MenubarMenuSpacer,
  MenubarMenuSubmenuRoot,
  MenubarMenuSubmenuTrigger,
  MenubarMenuTrigger,
};

.root {
  display: flex;
  align-items: center;
  background: var(--mix-card-50-bg);
  border: 0.5px solid oklch(from var(--border) l c h / 0.6);
  border-radius: var(--radius);
  box-shadow:
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px;
  padding: 0.125rem;
  gap: 0.125rem;

  @media (max-width: 640px) {
    padding: 0.375rem;
    gap: 0.375rem;
  }
}

.menuRoot {
  display: inline-block;
}

.menuTrigger {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem 0.75rem;
  background: transparent;
  border: none;
  border-radius: 0.125rem;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
  cursor: pointer;
  position: relative;
  isolation: isolate;

  &::before {
    content: "";
    position: absolute;
    inset: 0;
    border-radius: 0.3rem;
    background-color: transparent;
    z-index: -1;
    transition: background-color 250ms var(--ease-out-expo);
  }

  &:focus::before,
  &[data-popup-open]::before {
    background-color: var(--accent);
  }

  @media (hover: hover) {
    &:hover::before {
      background-color: var(--accent);
    }

    &[data-popup-open]:hover::before {
      background-color: var(--accent);
    }
  }

  &:focus {
    outline: none;
  }

  &:focus-visible {
    outline: 2px solid var(--ring);
    outline-offset: 2px;
  }

  &[data-disabled] {
    cursor: not-allowed;
    opacity: 0.5;
  }

  @media (max-width: 640px) {
    padding: 0.5rem 1rem;
    font-size: 0.9375rem;
    min-height: 2.5rem;
  }
}

.menuPositioner {
  z-index: 150;
  position: absolute;
  left: 0;
}

.menuPopup {
  min-width: 170px;
  background-color: var(--popover);
  border: 0.5px solid oklch(from var(--border) l c h / 0.8);
  border-radius: var(--radius);
  display: flex;
  flex-direction: column;
  box-shadow:
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px,
    oklch(from var(--border) l c h / 0.2) 0px 0.5px 0.5px;
  transform-origin: "top center";
  transition:
    transform 250ms var(--ease-out-expo),
    opacity 250ms var(--ease-out-expo);

  &[data-starting-style],
  &[data-ending-style] {
    opacity: 0;
    transform: scale(0.95);
  }

  @media (max-width: 640px) {
    padding: 0.375rem;
    max-width: calc(100vw - 2rem);
  }
}

.shortcut {
  margin-left: auto;
  margin-right: 4px;
  font-size: 0.75rem;
  color: var(--muted-foreground);
  letter-spacing: 0.025em;
  opacity: 0.6;

  @media (max-width: 640px) {
    display: none;
  }
}
