"use client";

import { Meter } from "@base-ui-components/react/meter";
import { cn } from "@/lib/utils";
import styles from "./meter.module.css";

const MeterRoot = ({
  className,
  ...props
}: React.ComponentProps<typeof Meter.Root>) => (
  <Meter.Root className={cn(styles.root, className)} {...props} />
);

const MeterLabel = ({
  className,
  ...props
}: React.ComponentProps<typeof Meter.Label>) => (
  <Meter.Label className={cn(styles.label, className)} {...props} />
);

const MeterTrack = ({
  className,
  ...props
}: React.ComponentProps<typeof Meter.Track>) => (
  <Meter.Track className={cn(styles.track, className)} {...props} />
);

const MeterIndicator = ({
  className,
  ...props
}: React.ComponentProps<typeof Meter.Indicator>) => (
  <Meter.Indicator className={cn(styles.indicator, className)} {...props} />
);

const MeterValue = ({
  className,
  ...props
}: React.ComponentProps<typeof Meter.Value>) => (
  <Meter.Value className={cn(styles.value, className)} {...props} />
);

export {
  MeterRoot as Meter,
  MeterLabel,
  MeterTrack,
  MeterIndicator,
  MeterValue,
};

.root {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;
}

.label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--foreground);
}

.value {
  font-size: 0.75rem;
  color: var(--muted-foreground);
  font-weight: 500;
}

.track {
  position: relative;
  height: 0.375rem;
  width: 100%;
  background-color: color-mix(in oklch, var(--card) 66%, var(--background));
  border-radius: 9999px;
  overflow: hidden;
}

.indicator {
  height: 100%;
  background-color: var(--primary);
  border-radius: 9999px;
  transition: all 150ms ease-out;
  width: var(--meter-indicator-width);
}

/* Status color variations */
.root[data-value="high"] .indicator {
  background-color: var(--destructive);
}

.root[data-value="medium"] .indicator {
  background-color: hsl(45 93% 47%);
}

.root[data-value="low"] .indicator {
  background-color: var(--primary);
}
