"use client";

import { Meter } from "@base-ui/react/meter";
import { cn } from "@/lib/utils";
import styles from "./meter.module.css";

function MeterRoot({ className, ...props }: Meter.Root.Props) {
  return <Meter.Root className={cn(styles.root, className)} {...props} />;
}

function MeterLabel({ className, ...props }: Meter.Label.Props) {
  return <Meter.Label className={cn(styles.label, className)} {...props} />;
}

function MeterTrack({ className, ...props }: Meter.Track.Props) {
  return <Meter.Track className={cn(styles.track, className)} {...props} />;
}

function MeterIndicator({ className, ...props }: Meter.Indicator.Props) {
  return <Meter.Indicator className={cn(styles.indicator, className)} data-slot="meter-indicator" {...props} />;
}

function MeterValue({ className, ...props }: Meter.Value.Props) {
  return <Meter.Value className={cn(styles.value, className)} {...props} />;
}

export { MeterRoot as Meter, MeterIndicator, MeterLabel, MeterTrack, MeterValue };

.root {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  width: 100%;

  &[data-value="high"] .indicator {
    background-color: var(--destructive);
  }

  &[data-value="medium"] .indicator {
    background-color: hsl(45 93% 47%);
  }

  &[data-value="low"] .indicator {
    background-color: var(--primary);
  }
}

.label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--secondary-foreground);
}

.value {
  font-size: 0.75rem;
  color: var(--muted-foreground);
  font-weight: 500;
}

.track {
  position: relative;
  height: 0.5rem;
  width: 100%;
  background-color: var(--mix-card-75-bg);
  border-radius: 0;
  overflow: hidden;
}

.indicator {
  height: 100%;
  background-color: oklch(from var(--foreground) l c h / 0.5);
  border-radius: 0;
  transition: all 150ms ease-out;
  width: var(--meter-indicator-width);
}
