"use client";

import { NumberField } from "@base-ui-components/react/number-field";
import { cn } from "@/lib/utils";
import styles from "./number-field.module.css";

function NumberFieldRoot({
  className,
  ...props
}: NumberField.Root.Props) {
  return <NumberField.Root className={cn(styles.root, className)} {...props} />;
}

function NumberFieldGroup({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) {
  return <div className={cn(styles.group, className)} {...props} />;
}

function NumberFieldInput({
  className,
  ...props
}: NumberField.Input.Props) {
  return (
    <NumberField.Input data-slot="numberfield-input" className={cn(styles.input, className)} {...props} />
  );
}

function NumberFieldIncrement({
  className,
  children,
  ...props
}: NumberField.Increment.Props) {
  return (
    <NumberField.Increment
      data-slot="numberfield-increment"
      className={cn(styles.increment, className)}
      {...props}
    >
      {children || (
        <svg
          aria-label="Increment"
          className={styles.icon}
          fill="none"
          height="16"
          role="img"
          viewBox="0 0 24 24"
          width="16"
        >
          <path
            d="M12 5v14m-7-7h14"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
          />
        </svg>
      )}
    </NumberField.Increment>
  );
}

function NumberFieldDecrement({
  className,
  children,
  ...props
}: NumberField.Decrement.Props) {
  return (
    <NumberField.Decrement
      data-slot="numberfield-decrement"
      className={cn(styles.decrement, className)}
      {...props}
    >
      {children || (
        <svg
          aria-label="Decrement"
          className={styles.icon}
          fill="none"
          height="16"
          role="img"
          viewBox="0 0 24 24"
          width="16"
        >
          <path
            d="M5 12h14"
            stroke="currentColor"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
          />
        </svg>
      )}
    </NumberField.Decrement>
  );
}

function NumberFieldScrubArea({
  className,
  ...props
}: NumberField.ScrubArea.Props) {
  return (
    <NumberField.ScrubArea
      data-slot="numberfield-scrubarea"
      className={cn(styles.scrubArea, className)}
      {...props}
    />
  );
}

function NumberFieldScrubAreaCursor({
  className,
  ...props
}: NumberField.ScrubAreaCursor.Props) {
  return (
    <NumberField.ScrubAreaCursor
      data-slot="numberfield-scrubareacursor"
      className={cn(styles.scrubAreaCursor, className)}
      {...props}
    />
  );
}

export {
  NumberFieldRoot as NumberField,
  NumberFieldDecrement,
  NumberFieldGroup,
  NumberFieldIncrement,
  NumberFieldInput,
  NumberFieldScrubArea,
  NumberFieldScrubAreaCursor,
};

.root {
  position: relative;
  display: inline-flex;
  flex-direction: column;
  width: 100%;
  max-width: 12rem;
}

.scrubArea {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1;
  cursor: col-resize;
  user-select: none;
  -webkit-user-select: none;

  &:active .scrubAreaCursor {
    opacity: 1;
  }
}

.scrubAreaCursor {
  position: absolute;
  pointer-events: none;
  background: rgba(59, 130, 246, 0.8);
  width: 2px;
  height: 100%;
  border-radius: 1px;
  opacity: 0;
  transition: opacity 0.15s ease;
}

.group {
  position: relative;
  display: flex;
  align-items: center;
  gap: 0;
  z-index: 2;
}

.input {
  flex: 0 1 6rem;
  min-width: 6rem;
  height: 2.5rem;
  border: 1px solid var(--border);
  padding: 0 0.5rem;
  outline: none;
  font-size: 0.875rem;
  color: var(--foreground);
  text-align: center;
  transition: all 150ms ease-out;
  background-color: var(--mix-card-50-bg);

  &::placeholder {
    color: var(--muted-foreground);
  }

  &:focus {
    border-color: var(--ring);
    box-shadow: 0 0 0 2px var(--ring) / 0.2;
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;
  }
}

.increment,
.decrement {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 1rem;
  height: 2.5rem;
  border: 1px solid var(--border);
  background-color: var(--mix-card-50-bg);
  color: var(--muted-foreground);
  cursor: pointer;
  transition: all 150ms ease-out;

  &:hover {
    color: var(--foreground);
    background: var(--muted);
  }

  &:active {
    background: var(--muted);
  }

  &:disabled {
    cursor: not-allowed;
    opacity: 0.5;

    &:hover {
      color: var(--muted-foreground);
      background: transparent;
    }
  }
}

.decrement {
  border-radius: var(--radius) 0 0 var(--radius);
  border-right: none;
}

.increment {
  border-radius: 0 var(--radius) var(--radius) 0;
  border-left: none;
}

.icon {
  flex-shrink: 0;
}
