"use client";

interface PayloadEntry {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
}

export interface ChartTooltipProps {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
}

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!active || !payload || !payload.length) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && <div style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}>{formatLabel(label)}</div>}
      {payload.map((entry, index) => (
        <div
          key={index}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>{formatName(entry.name || entry.dataKey || "")}</span>
          </div>
          <span
            style={{
              fontFamily: "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import { useMemo } from "react";
import { PieChart as RechartsePieChart, Pie, Cell, ResponsiveContainer, Tooltip } from "recharts";
import ChartTooltip from "./chart-tooltip";
import styles from "./pie-chart.module.css";

export interface PieChartData {
  category: string;
  value: number;
  type: string;
}

export interface PieChartProps {
  data: PieChartData[];
  innerRadius?: number;
  outerRadius?: number;
  animate?: boolean;
  interactive?: boolean;
  colors?: string[];
}

function PieChart({
  data,
  innerRadius = 0,
  outerRadius,
  animate = true,

  colors,
}: PieChartProps) {
  const defaultColors = ["var(--chart1)", "var(--chart2)", "var(--accent)", "var(--warning)", "var(--destructive)"];
  const pieColors = colors || defaultColors;

  const total = useMemo(() => data.reduce((sum, d) => sum + d.value, 0), [data]);

  const dataWithColors = useMemo(
    () =>
      data.map((item, index) => ({
        ...item,
        fill: pieColors[index % pieColors.length],
      })),
    [data, pieColors],
  );

  const tooltipValueFormatter = (value: number | string, name?: string) => {
    const numValue = typeof value === 'number' ? value : parseFloat(String(value));
    if (isNaN(numValue)) return String(value);
    const percentage = ((numValue / total) * 100).toFixed(1);
    return `${percentage}%`;
  };

  interface PieTooltipPayload {
    value: number;
    payload: PieChartData & { fill: string };
  }

  function CustomTooltip({ active, payload }: { active?: boolean; payload?: PieTooltipPayload[] }) {
    if (!active || !payload || !payload.length) return null;

    const data = payload[0];
    return (
      <ChartTooltip
        active={active}
        payload={[
          {
            ...data,
            color: data.payload.fill,
            name: data.payload.category,
          },
        ]}
        valueFormatter={tooltipValueFormatter}
      />
    );
  }

  return (
    <div className={styles.pieChart}>
      <ResponsiveContainer width="100%" height="100%">
        <RechartsePieChart>
          <Pie
            data={dataWithColors}
            cx="50%"
            cy="50%"
            labelLine={false}
            label={false}
            outerRadius={outerRadius || 120}
            innerRadius={innerRadius}
            fill="#8884d8"
            dataKey="value"
            animationBegin={animate ? 0 : undefined}
            animationDuration={animate ? 800 : 0}
            stroke="none"
          >
            {dataWithColors.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.fill} />
            ))}
          </Pie>
          <Tooltip content={<CustomTooltip />} />
        </RechartsePieChart>
      </ResponsiveContainer>
    </div>
  );
}

export default PieChart;

.pieChart {
    position: relative;
    width: 100%;
    height: 100%;
}

.pieChart *:focus,
.pieChart *:focus-visible {
    outline: none !important;
}
