"use client";

type PayloadEntry = {
  name?: string;
  dataKey?: string;
  value: number | string;
  color?: string;
};

export type ChartTooltipProps = {
  active?: boolean;
  payload?: PayloadEntry[];
  label?: string | number;
  labelFormatter?: (value: string | number) => string;
  valueFormatter?: (value: number | string, name?: string) => string;
  nameFormatter?: (name: string) => string;
};

export function ChartTooltip({
  active,
  payload,
  label,
  labelFormatter,
  valueFormatter,
  nameFormatter,
}: ChartTooltipProps) {
  if (!(active && payload && payload.length)) {
    return null;
  }

  const formatLabel = (value: string | number) => {
    if (labelFormatter) {
      return labelFormatter(value);
    }
    return String(value);
  };

  const formatValue = (value: number | string, name?: string) => {
    if (valueFormatter) {
      return valueFormatter(value, name);
    }
    if (typeof value === "number") {
      return value.toLocaleString();
    }
    return String(value);
  };

  const formatName = (name: string) => {
    if (nameFormatter) {
      return nameFormatter(name);
    }

    return name.toLowerCase().replace(/\b\w/g, (l) => l.toUpperCase());
  };

  return (
    <div
      style={{
        backgroundColor: "var(--muted)",
        color: "var(--foreground)",
        borderRadius: "var(--radius)",
        padding: "8px",
        fontSize: "12px",
        border: "1px solid var(--border)",
        boxShadow: "0 4px 6px -1px rgb(0 0 0 / 0.1)",
      }}
    >
      {label && (
        <div
          style={{ fontSize: "11px", fontWeight: "500", marginBottom: "4px" }}
        >
          {formatLabel(label)}
        </div>
      )}
      {payload.map((entry, index) => (
        <div
          key={`${entry.dataKey || entry.name || "entry"}-${index}`}
          style={{
            fontSize: "11px",
            marginBottom: "2px",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            gap: "12px",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <span style={{ color: entry.color, marginRight: "4px" }}>‚óè</span>
            <span style={{ color: "var(--muted-foreground)" }}>
              {formatName(entry.name || entry.dataKey || "")}
            </span>
          </div>
          <span
            style={{
              fontFamily:
                "'SF Mono', Monaco, 'Cascadia Code', 'Roboto Mono', Consolas, 'Courier New', monospace",
              fontWeight: "500",
            }}
          >
            {formatValue(entry.value, entry.name || entry.dataKey)}
          </span>
        </div>
      ))}
    </div>
  );
}

export default ChartTooltip;

"use client";

import { useMemo } from "react";
import {
  Cell,
  Pie,
  PieChart as RechartsePieChart,
  ResponsiveContainer,
  Tooltip,
} from "recharts";
import ChartTooltip from "./chart-tooltip";
import styles from "./pie-chart.module.css";

export type PieChartData = {
  category: string;
  value: number;
  type: string;
};

export type PieChartProps = {
  data: PieChartData[];
  innerRadius?: number;
  outerRadius?: number;
  animate?: boolean;
  interactive?: boolean;
  colors?: string[];
};

const ANIMATION_DURATION_MS = 800;
const DEFAULT_OUTER_RADIUS = 120;
const PERCENTAGE_MULTIPLIER = 100;

type PieTooltipPayload = {
  value: number;
  payload: PieChartData & { fill: string };
};

type CustomTooltipProps = {
  active?: boolean;
  payload?: PieTooltipPayload[];
  tooltipValueFormatter: (value: number | string, name?: string) => string;
};

function CustomTooltip({
  active,
  payload,
  tooltipValueFormatter,
}: CustomTooltipProps) {
  if (!(active && payload && payload.length)) {
    return null;
  }

  const tooltipData = payload[0];
  return (
    <ChartTooltip
      active={active}
      label={tooltipData.payload.category}
      payload={[
        {
          ...tooltipData,
          name: tooltipData.payload.category,
          color: tooltipData.payload.fill,
        },
      ]}
      valueFormatter={tooltipValueFormatter}
    />
  );
}

function PieChart({
  data,
  innerRadius = 0,
  outerRadius,
  animate = true,

  colors,
}: PieChartProps) {
  const defaultColors = [
    "var(--chart1)",
    "var(--chart2)",
    "var(--accent)",
    "var(--warning)",
    "var(--destructive)",
  ];
  const pieColors = colors || defaultColors;

  const total = useMemo(
    () => data.reduce((sum, d) => sum + d.value, 0),
    [data]
  );

  const dataWithColors = useMemo(
    () =>
      data.map((item, index) => ({
        ...item,
        fill: pieColors[index % pieColors.length],
      })),
    [data, pieColors]
  );

  const tooltipValueFormatter = (value: number | string, _name?: string) => {
    const numValue =
      typeof value === "number" ? value : Number.parseFloat(String(value));
    if (Number.isNaN(numValue)) {
      return String(value);
    }
    const percentage = ((numValue / total) * PERCENTAGE_MULTIPLIER).toFixed(1);
    return `${percentage}%`;
  };

  return (
    <div className={styles.pieChart}>
      <ResponsiveContainer height="100%" width="100%">
        <RechartsePieChart>
          <Pie
            animationBegin={animate ? 0 : undefined}
            animationDuration={animate ? ANIMATION_DURATION_MS : 0}
            cx="50%"
            cy="50%"
            data={dataWithColors}
            dataKey="value"
            fill="#8884d8"
            innerRadius={innerRadius}
            label={false}
            labelLine={false}
            outerRadius={outerRadius || DEFAULT_OUTER_RADIUS}
            stroke="none"
          >
            {dataWithColors.map((entry) => (
              <Cell fill={entry.fill} key={`cell-${entry.category}`} />
            ))}
          </Pie>
          <Tooltip
            content={
              <CustomTooltip tooltipValueFormatter={tooltipValueFormatter} />
            }
          />
        </RechartsePieChart>
      </ResponsiveContainer>
    </div>
  );
}

export default PieChart;

.pieChart {
  position: relative;
  width: 100%;
  height: 100%;
}

.pieChart *:focus,
.pieChart *:focus-visible {
  outline: none !important;
}
